define('o9.DemandPlanning.DisagExceptions',['o9/data/query', 'o9/data/cellset','o9.web.api'],function(){

    var ExtractMeasure=function(Query,Measure='RecordCount'){
        var cellsetModule = require('o9/data/cellset');
        var queryModule=require('o9/data/query')
        var QueryCellset=cellsetModule.createCellSet(queryModule.select(Query))
        if (QueryCellset.rowCount==0)
            return 0    
        return QueryCellset.row(0).cell(QueryCellset.measureColumnIndex(Measure))
        
    }
    var PopulateDisagExceptions=function (o9Params)
    {
        
        var cellsetModule = require('o9/data/cellset');
        var queryModule=require('o9/data/query')
        var parsedParams = JSON.parse(o9Params);
        var ForecastIteration=parsedParams.Iteration
        
        var Version=parsedParams.VersionName
        var PopulateDisag=parsedParams.PopulateDisag
        if(!PopulateDisag)
            return 
        
        
        ForecastIteration.forEach(Iteration =>{

            queryModule.select('delete data for model [401 Stat Process Exception] where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};')
            var Dims=['Item','Account','Channel','Region','Location','PnL','Demand Domain']
            var Levels={}
            Dims.forEach(element=>{
                var query='Select([Forecast Iteration].[Forecast Iteration].['+Iteration+'] * [Version].[Version Name].['+Version+'] * ['+element+'].[Planning '+element+']) on row,({Measure.['+element+' Level]}) on column;'
                Levels[element]=ExtractMeasure(query,element+' Level')
            })
            

            var  ExpectQuery='Select ([Version].[Version Name].['+Version+'] * [Region].[Stat Region] * [Item].[Stat Item] * [PnL].[Stat PnL] *[Forecast Iteration].[Forecast Iteration].['+Iteration+']  * [Location].[Stat Location] * [Channel].[Stat Channel] * [Demand Domain].[Stat Demand Domain] * [Account].[Stat Account] ) on row, ({Measure.[System Stat Fcst L1]}) on column ;'
            var  ExpectCellset=cellsetModule.createCellSet(queryModule.select(ExpectQuery))
            
            
            if(ExpectCellset.rowCount<1){
                Log.Info('Actual Rows: '+ExpectCellset.rowCount)
                return 
            } 

            
            for(var i=0;i<ExpectCellset.rowCount;i++)
                {
                    
                    var intersection={}
                    Dims.forEach(element=>{intersection[element]=ExpectCellset.row(i).cell(ExpectCellset.memberColumnIndex(element,'Stat '+element)).Name})
                    var ExpectValue=ExpectCellset.row(i).cell(ExpectCellset.measureColumnIndex('System Stat Fcst L1'))
                    var actualQuery='Select ([Version].[Version Name].['+Version+'] * [Region].[Planning Region].relatedmembers(['+Levels['Region']+']).['+intersection['Region']+'] * [Item].[Planning Item].relatedmembers(['+Levels['Item']+']).['+intersection['Item']+'] * [PnL].[Planning PnL].relatedmembers(['+Levels['PnL']+']).['+intersection['PnL']+'] *[Forecast Iteration].[Forecast Iteration].['+Iteration+']  * [Location].[Planning Location].relatedmembers(['+Levels['Location']+']).['+intersection['Location']+']  * [Channel].[Planning Channel].relatedmembers(['+Levels['Channel']+']).['+intersection['Channel']+'] * [Demand Domain].[Planning Demand Domain].relatedmembers(['+Levels['Demand Domain']+']).['+intersection['Demand Domain']+'] * [Account].[Planning Account].relatedmembers(['+Levels['Account']+']).['+intersection['Account']+'] ) on row, ({Measure.[Stat Fcst PL]}) on column;'
                    
                    var actualCellset=cellsetModule.createCellSet(queryModule.select(actualQuery))
                    var ActualValue=0
                    if(actualCellset.rowCount>0)
                        ActualValue=actualCellset.row(0).cell(actualCellset.measureColumnIndex('Stat Fcst PL'))
                    ActualValue=!ActualValue?0:ActualValue
                    ExpectValue=!ExpectValue?0:ExpectValue
                    Log.Info("Current Intersection :"+intersection['Item']+", "+intersection['Account']+", "+intersection['Channel']+", "+intersection['Region']+", "+intersection['Location']+", "+intersection['PnL']+", "+intersection['Demand Domain']+" and Expect: "+ExpectValue+" Actual: "+ActualValue)                   
                    var loss=Math.round(ExpectValue-ActualValue)
                    
                    var populateQuery='Cartesian Scope: ([Version].[Version Name].['+Version+'] * [Region].[Stat Region].['+intersection['Region']+'] * [Item].[Stat Item].['+intersection['Item']+'] * [PnL].[Stat PnL].['+intersection['PnL']+'] * [Forecast Iteration].[Forecast Iteration].['+Iteration+'] * [Location].[Stat Location].['+intersection['Location']+'] * [Channel].[Stat Channel].['+intersection['Channel']+'] * [Demand Domain].[Stat Demand Domain].['+intersection['Demand Domain']+'] * [Account].[Stat Account].['+intersection['Account']+'] );Measure.[Volume Loss PL]='+loss+';end scope;'
                    
                    if(loss>0)
                    queryModule.select(populateQuery)
                }
        })         
    }

 return{
    PopulateDisagExceptions:PopulateDisagExceptions
 }

    
});

