define('o9.DemandPlanning.BackTestML', ['o9/data/query', 'o9/data/cellset'],  function() {
		 var queryModule= require('o9/data/query');
		 var BackTestML = function (o9Params)
		 {
		    Log.Info("**********************Start**********************" );

			// passing params from AB to JS variables
			var parsedParams = JSON.parse(o9Params);
			var Version = parsedParams.VersionName;
			var CustomerGroup = parsedParams.SalesDomain;
			var Location = parsedParams.Location;
			var Item = parsedParams.Item;
			var RID = parsedParams.RID;
			var TID = parsedParams.TID;
			var cycles = parsedParams.PredictNoOfCycles;
			var Lags = parsedParams.NoOfLags;
			var ForecastGenerationTimeLevel = parsedParams.ForecastGenerationTimeLevel;
			var queryModule= require('o9/data/query');
			var cellsetModule = require('o9/data/cellset');
			
			// converting passed params to required form
			Log.Info(" O9 PARAMS ---" + JSON.stringify(o9Params));
			var VersionInput = "\"" + Version.join("\",\"") + "\"";
			var CustomerGroupInput = "\"" + CustomerGroup.join("\",\"") + "\"";
			var ItemInput = "\"" + Item.join("\",\"") + "\"";
			var LocationInput = "\"" + Location.join("\",\"") + "\"";
			
			if (ForecastGenerationTimeLevel == 'Planning Month'){
				var DimTimeTableQueryRaw = "select &AllPlanningMonths.filter(#.Key >= &CurrentPlanningMonth.element(0).LeadOffset(" + (parseInt(-1*cycles)) + ").Key && #.Key <= &CurrentPlanningMonth.element(0).LeadOffset(-1).Key)" ;
				
				// Nulling Out Existing Lag Outputs
				var pluginquerynull = 'scope:([Lag].[Lag] * [Version].[Version Name].filter(#.Name in{'+VersionInput+'}) * [Item].[Stat Item].filter(#.Name in{'+ItemInput+'})* [Sales Domain].[Stat Customer Group].filter(#.Name in{'+CustomerGroupInput+'}) * [Location].[Stat Location].filter(#.Name in{'+LocationInput+'}) * &AllPlanningMonths); Measure.[ML PM Lag]=Null; end scope;';
				
				var QueryColumn = "Planning Month" ;
				var LagColumn = "ML PM Lag" ;
				
			}
			else if (ForecastGenerationTimeLevel == 'Month') {
				var DimTimeTableQueryRaw = "select &AllMonths.filter(#.Key >= &CurrentMonth.element(0).LeadOffset(" + (parseInt(-1*cycles)) + ").Key && #.Key <= &CurrentMonth.element(0).LeadOffset(-1).Key)" ;
				
				// Nulling Out Existing Lag Outputs
				var pluginquerynull = 'scope:([Lag].[Lag] * [Version].[Version Name].filter(#.Name in{'+VersionInput+'}) * [Item].[Stat Item].filter(#.Name in{'+ItemInput+'})* [Sales Domain].[Stat Customer Group].filter(#.Name in{'+CustomerGroupInput+'}) * [Location].[Stat Location].filter(#.Name in{'+LocationInput+'}) * &AllMonths); Measure.[ML M Lag]=Null; end scope;';
				
				var QueryColumn = "Month" ;
				var LagColumn = "ML M Lag" ;
			}
			else if (ForecastGenerationTimeLevel == 'Week') {
				var DimTimeTableQueryRaw = "select &AllWeeks.filter(#.Key >= &CurrentWeek.element(0).LeadOffset(" + (parseInt(-1*cycles)) + ").Key && #.Key <= &CurrentWeek.element(0).LeadOffset(-1).Key)" ;
				
				// Nulling Out Existing Lag Outputs
				var pluginquerynull = 'scope:([Lag].[Lag] * [Version].[Version Name].filter(#.Name in{'+VersionInput+'}) * [Item].[Stat Item].filter(#.Name in{'+ItemInput+'})* [Sales Domain].[Stat Customer Group].filter(#.Name in{'+CustomerGroupInput+'}) * [Location].[Stat Location].filter(#.Name in{'+LocationInput+'}) * &AllWeeks); Measure.[ML W Lag]=Null; end scope;';
				
				var QueryColumn = "Week" ;
				var LagColumn = "ML W Lag" ;
			}
			else {
				Log.Info("Unknown Forecast Generation Time Bucket : " + ForecastGenerationTimeLevel);
			}
			
			
			for(var i=0; i<cycles;i++)
			{
				Log.Info("Started Backtest Cycle : " + i);
				DimTimeTableQuery = DimTimeTableQueryRaw + '.element(' + (parseInt(i)) + ').Name;'
				Log.Info("DimTimeTableQuery ..." + DimTimeTableQuery);
				var lsResponse = queryModule.select(DimTimeTableQuery);
				var CurrentPeriod = lsResponse.Result;
				Log.Info("CurrentPeriod ..." + CurrentPeriod);
				Log.Info("LagColumn ..." + LagColumn);
				Log.Info("QueryColumn ..." + QueryColumn);
				
				// AI predict Plugin invocation
				var pluginStartTime = Date.now();
				var pluginquery = 'exec plugin instance [DP032AIPredictMLAtL1Level] for measures {Measure.[System ML Fcst L1]} using scope ([Location].[Stat Location].filter(#.Name in{'+LocationInput+'})*[Item].[Stat Item].filter(#.Name in{'+ItemInput+'}) * [Sales Domain].[Stat Customer Group].filter(#.Name in{'+CustomerGroupInput+'}) * &AllStatL1Buckets * [Version].[Version Name].filter(#.Name in{'+VersionInput+'}) ) using arguments {("ForecastMeasureName", "System ML Fcst L1"), ("IgnoreMissingOutputs", true) , ("ExecutorMemory", "5g") , (DriverMemory, "5G") , (NumExecutors, 2) , (DriverCores, 4), (ExecutorCores, 4), ("ForecastTournamentId", "'+TID+'"),("ForecastRecipeId", "'+RID+'"),("ForecastCurrentPeriod", "'+CurrentPeriod+'"),("SkipRecipeValidation", true), ("ForecastPeriods", '+(parseInt(Lags)+1)+'), ([AiPredictMode], "Training")};';
				
				Log.Info("**********************ExecQuery**********************" );

				Log.Info("Query to be executed ... " + pluginquery);
				queryModule.update(pluginquery);
				var pluginEndTime = Date.now();
				Log.Info('Time Taken for AI Predict plugin execution ' + (pluginEndTime - pluginStartTime) + 'ms');
				
				
				// Populating ML Fcst measure via evaluate member
				var populateStartTime = Date.now();
				for(let j=0; j<=Lags;j++){
					Log.Info("Lag " + j);
					var evaluateStartTime = Date.now();
					var evaluatememberquery = 'evaluatemember scope: ([Location].[Stat Location].filter(#.Name in{'+LocationInput+'}) * [Version].[Version Name].filter(#.Name in{'+VersionInput+'}) * [Sales Domain].[Stat Customer Group].filter(#.Name in{'+CustomerGroupInput+'}) * [Item].[Stat Item].filter(#.Name in{'+ItemInput+'}) * [Time].[' + QueryColumn + '].['+CurrentPeriod+ '].leadoffset('+ j +') * [Lag].[Lag].[' + j +'] )' + ';' + 'Measure.[' + LagColumn + '] = if (Lag.#.Name == "'+ j +'" && Time.#.Key == [Time].[' + QueryColumn + '].['+CurrentPeriod+'].leadoffset('+ j +').Key) then Measure.[System ML Fcst L1]@(Time.#.LeadOffset('+ -1* j +')) else Measure.[' + LagColumn + ']' + ';' + 'end scope;';
					Log.Info("**********************EVALQuery**********************" );

					Log.Info("Query to Populate Lags : " + evaluatememberquery);
					queryModule.update(evaluatememberquery);
					var evaluateEndTime = Date.now();
					Log.Info('Time Taken for evalaute query ' + (evaluateEndTime - evaluateStartTime) + 'ms');
				}
			
				
			}
			
			Log.Info("**********************End**********************");
			
	};			
    return {
        BackTestML : BackTestML
    };
});

