define('o9.DemandPlanning.StatProcesses',['o9/data/query', 'o9/data/cellset','o9.web.api'], function(){
        
    const cellsetModule = require('o9/data/cellset');
    const queryModule=require('o9/data/query')

    var itemize=function(item){
        var itemdata=''+item+''
        itemdata=itemdata.split(",").map(i=>"["+i+"]")
        return itemdata
    }

    var Processes=[
        'Generate Stat Actuals',
        'Generate Segmentation',
        'Generate Outlier Cleansing',
        'Generate Assigned Rules',
        'Generate System Stat',
        'Populate Analysis Cockpit',
        'Identify Bestfit Algorithm',
        'Populate Stat Fcst',
        'Disaggregate Stat',
        'Forecast Reconciliation',
        'Stat Accuracy Calculation',
        'Planning Accuracy Calculation',
    ]
    
    var Cycles=function(o9Params){
        var parsedParams = JSON.parse(o9Params);
        var IterationTypes=parsedParams.ForecastIterationType;
        var ExecutionMode=parsedParams.ExecutionMode;

        var PlanningCycles=[]
        IterationTypes.forEach(IterationType => {
            var Query ='Select ( &CWV * [Forecast Iteration].[Forecast Iteration Type].['+IterationType+'] ) on row, ({Measure.[Forecast Generation Time Bucket]}) on column;'
            var QueryCellset=cellsetModule.createCellSet(queryModule.select(Query))
            
            if (QueryCellset.rowCount==0)
                return 0 
    
            var GenBucket = QueryCellset.row(0).cell(QueryCellset.measureColumnIndex('Forecast Generation Time Bucket'))   
            if(GenBucket=='Week'){
                TimeQuery='Select( [Time].[Week].filter(#.Key>&CurrentWeek.element(0).leadoffset(-6).Key && #.Key<=&CurrentWeek.element(0).leadoffset(6).Key));'
                CurrentPeriodQuery='Select(&CurrentWeek);'
                
            }
            else if(GenBucket=='Month'){
                TimeQuery='Select( [Time].[Month].filter(#.Key>&CurrentMonth.element(0).leadoffset(-6).Key && #.Key<=&CurrentMonth.element(0).leadoffset(6).Key));'
                CurrentPeriodQuery='Select(&CurrentMonth);'
            }
            else if(GenBucket=='Planning Month'){
                TimeQuery='Select( [Time].[Planning Month].filter(#.Key>&CurrentPlanningMonth.element(0).leadoffset(-6).Key && #.Key<=&CurrentPlanningMonth.element(0).leadoffset(6).Key));'
                CurrentPeriodQuery='Select(&CurrentPlanningMonth);'
            }
            else if(GenBucket=='Quarter'){
                TimeQuery='Select( [Time].[Quarter].filter(#.Key>&CurrentQuarter.element(0).leadoffset(-6).Key && #.Key<=&CurrentQuarter.element(0).leadoffset(6).Key));'
                CurrentPeriodQuery='Select(&CurrentQuarter);'
            }
            else if(GenBucket=='Planning Quarter'){
                TimeQuery='Select( [Time].[Planning Quarter].filter(#.Key>&CurrentPlanningQuarter.element(0).leadoffset(-6).Key && #.Key<=&CurrentPlanningQuarter.element(0).leadoffset(6).Key));'
                CurrentPeriodQuery='Select(&CurrentPlanningQuarter);'
            }



            Log.Info('Execution Mode : '+ExecutionMode)
            if(ExecutionMode=='Prod'){
                
                Log.Info( ' Query Here : '+CurrentPeriodQuery)
                TimeQueryCellset=cellsetModule.createCellSet(queryModule.select(CurrentPeriodQuery))
    
                Log.Info('TimeQueryCellset Here : '+TimeQueryCellset)
                var FutureCycles=TimeQueryCellset.attributeMembers("Time",GenBucket).map(function (item) {
                return item['Key'];
                })
    
                Log.Info('FutureCycles Here : '+FutureCycles)
                FutureCycles=FutureCycles.map(i=>"todatetime(\""+i+"\")")
    
                Log.Info('Here :'+FutureCycles)
                PlanningCycles= [...PlanningCycles, ...FutureCycles];
                return;
            }
            

            var TimeQueryCellset=cellsetModule.createCellSet(queryModule.select(TimeQuery))

        
            var FutureCycles=TimeQueryCellset.attributeMembers("Time",GenBucket).map(function (item) {
                return item['Key'];
            })
            FutureCycles=FutureCycles.map(i=>"todatetime(\""+i+"\")")
            PlanningCycles= [...PlanningCycles, ...FutureCycles];



        });

        parsedParams['Cycles']=PlanningCycles.toString();

        
        return parsedParams;

    }


    var Process=function(o9Params){

        parsedParams = JSON.parse(o9Params);
        var processesList=parsedParams.Processes;
        var ExecutionMode=parsedParams.ExecutionMode;
        var VersionName=parsedParams.VersionName;
        var IterationType=parsedParams.ForecastIterationType;
        var FinalProcesses=[]


        
        if(ExecutionMode=='Dev')
        {
            Log.Info("In Dev");
            FinalProcesses=[...FinalProcesses,...Processes]
            
            var InputStreams=[]
       
            IterationType.forEach(Type=>{
                
                InputStreams=[...InputStreams,Type.split('_')[1]]
                
            })
           
            InputStreams=[...new Set(InputStreams)]
            var Recon=false;
            VersionName.forEach(Version=>{
                InputStreams.forEach(InputStream=>{
                    var Iterations=[]
                    IterationType.forEach(Type=>{
                        if(Type.includes(InputStream))
                            Iterations=[...Iterations,Type]
                    })
                    var ReconListQuery="Select([Version].[Version Name].["+Version+"]*[Data Stream].[Data Stream Type].["+InputStream+"] ) on row,({Measure.[Forecast Reconciliation]}) on column;"        
                    var ReconListCellSet=cellsetModule.createCellSet(queryModule.select(ReconListQuery))
                    var ReconList=ReconListCellSet.row(0).cell(ReconListCellSet.measureColumnIndex('Forecast Reconciliation'))
                    ReconList=ReconList.split(',')

                    if(ReconList.every(item => Iterations.includes(item)))
                        Recon=true;

                })

            })
            if(Recon)
            FinalProcesses=[...FinalProcesses,'Reconciliation']
        }
        else
        {
            Log.Info("In Prod");
            processesList.forEach(Process=>{
                
                Process=Process.replace(/[\[\]]/g, '').trim();
                Log.Info('Process is :'+Process)
               if(Process=="Generate Slice Association"||Process=="Generate Stat Actuals")
                FinalProcesses=[...FinalProcesses,"Generate Stat Actuals"]
               if(Process=="Generate Segmentation"||Process== "Seasonality Detection")
                FinalProcesses=[...FinalProcesses,"Generate Segmentation"]
               if(Process=="Generate System Stat"||Process=="Generate Outlier Cleansing"||Process=="Generate Assigned Rules"||Process=="Populate Analysis Cockpit"||Process=="Identify Bestfit Algorithm"||Process=="Populate Stat Fcst"||Process=="Forecast Reconciliation"||Process=="Stat Accuracy Calculation"||Process=="Planning Accuracy Calculation")
                FinalProcesses=[...FinalProcesses,Process]
               if(Process=="PL Count Validation"||Process=="PL Volume Loss Validation"||Process=="TL Count Validation"||Process=="TL Volume Loss Validation"||Process=="Disaggregate Stat")
                FinalProcesses=[...FinalProcesses,'Disaggregate Stat']
               if(Process=="Generate Ensemble Fcst"||Process=="Generate CML Fcst")
                FinalProcesses=[...FinalProcesses,'Generate System Stat']
               
            })
        }

        var StatProcesses= [...new Set(FinalProcesses)];

        StatProcesses=StatProcesses.map(i=>"["+i+"]")
        parsedParams['StatProcesses']=StatProcesses.toString();
        return parsedParams;





    
    }



    return {
        Cycles:Cycles,
        Process:Process


    }
})

