define('o9.DemandPlanning.RunStatBatch',['o9/data/query', 'o9/data/cellset','o9.web.api'], function(){

    const cellsetModule = require('o9/data/cellset');
    const queryModule=require('o9/data/query')

    // List of overarching processes that are being executed
    const StatProcesses= [
       
        // 'Generate Stat Actuals',
        'Accuracy Calculation',
        'Generate Segmentation',
        'Generate Outlier Cleansing',
        'Generate Assigned Rules',
        'Generate System Stat',
        'Populate Analysis Cockpit',
        'Identify Bestfit Algorithm',
        'Populate Stat Fcst',
        'Disaggregate Stat',
        'Forecast Reconciliation',
        'Populate Lag Model'
    ]
 
    //List of procedures being called 
    const BatchProcs={
        'DailyRefresh': 'o9DP010DailyRefresh',
        'Segmentation' : 'o9DP0900GenerateSegmentation',
        'OutlierCorrection' : 'o9DP0901OutlierCorrection',
        'BestfitFlag': 'o9DP0906UpdateBestfitFlagAttribute',
        'AssignRuleAndAlgo':'o9DP0152AssignRuleAndAlgoGlobal',
        'SeasonalIndices':'o9DP0154GenerateSeasonalIndices',
        'PreprocessSystemStat':'o9DP0903PreprocessSystemStat',
        'GenerateSystemStat' :'o9DP0904GenerateSystemStat',
        'GenerateSystemML':'o9DP0904GenerateSystemML',
        'GenerateFastStat':'o9DP0904GenerateFastStat',
        'GenerateSystemStatUsingBestfitLC':'o9DP0905GenerateSystemStatUsingBestfitLC',
        'GenerateFastStatUsingBestfitLC':'o9DP0905GenerateFastStatUsingBestfitLC ',
        'OutputChecks':'o9DP0909OutputChecks',
        'IdentifyBestfitModel':'o9DP0907IdentifyBestFitModel',
        'EnsembleFit':'o9DP0907EnsembleFit',
        'PopulateBestFitForecast':'o9DP0908PopulateBestFitForecast',
        'DisaggregateStat':'o9DP0910DisaggregateStat',
        'PopulateLagModel':'o9DP005PopulateLags',
        'Reconciliation':'o9DP0916GenerateReconciledForecast',
        'PopulateHistory':'o9DP002PopulateHistoryFullScope',
        'CalculateAccuracyStat':'o9DP005PopulateAccuracyStat'
    }

    //Assortment Measure per Input Stream
    const AssortmentMeasure = {
        "Actual": "Assortment Stat",
        "Sell Out Actual": "Sell Out Actual"
    };


    //Function that handles all conditions being checked in the code
    var CheckCondition= function(Version,Itype,Check)
    {
        var ForecastEngineQuery='Select ([Forecast Iteration].[Forecast Iteration Type].['+Itype+'].relatedmembers([Forecast Iteration]) * [Version].[Version Name].['+Version+'] ) on row, ({Measure.[Forecast Engine],Measure.[Forecast Strategy],Measure.[Assigned Algorithm List]}) on column;'

        var Iteration=Itype.split('_');
        Type=Iteration[0]
        BestfitItemsQuery=' select([Item].[Segmentation LOB].filter(#.[Bestfit Flag '+Type+'] == 1).relatedmembers([Stat Item]));'
     
        var BestfitCellset=cellsetModule.createCellSet(queryModule.select(BestfitItemsQuery))



        var ForecastEngineCellset=cellsetModule.createCellSet(queryModule.select(ForecastEngineQuery))
        var ForecastEngine = ForecastEngineCellset.row(0).cell(ForecastEngineCellset.measureColumnIndex('Forecast Engine'))
        var ForecastStrategy = ForecastEngineCellset.row(0).cell(ForecastEngineCellset.measureColumnIndex('Forecast Strategy'))
        var AssignedAlgorithmList = ForecastEngineCellset.row(0).cell(ForecastEngineCellset.measureColumnIndex('Assigned Algorithm List'))

        var FastStatQuery='Select ([Version].[Version Name].['+Version+'] ) on row,({Measure.[Stat Archetype]}) on column;'
        var FastStatCellset=cellsetModule.createCellSet(queryModule.select(FastStatQuery))
        var Container=false
        if(FastStatCellset.rowCount>0)
            {
                var ContainerVal=FastStatCellset.row(0).cell(FastStatCellset.measureColumnIndex('Stat Archetype'))
                if(ContainerVal=='Container')
                    Container=true
            }


        //Checks if Forecast Engine is only ML
        if(Check=='ML' && ForecastEngine.toString()=='ML')
                return true
        
        //Checks if Forecast Engine contains ML
        if(Check=='GenML' && ForecastEngine.toString().includes('ML'))
                return true
        //Checks if Forecast Engine contains Stat    
        if(Check=='GenStat' && ForecastEngine.toString().includes('Stat'))
                return true

        //Checks if Assigned Algorithm list contains SCHM
        if(Check=='SCHM' && AssignedAlgorithmList.toString().includes('SCHM'))
            return true


        //Checks if Forecast Strategy is Ensemble
        if(Check=='Ensemble' && ForecastStrategy.toString().includes('Ensemble'))
                return true

        //Checks if BEstfit Items Exists
        if(Check=='BestfitItems' && BestfitCellset.rowCount>0)
            return true

        //Checks if Stat Archetype is Container
        if(Check=='FastStat' && Container)
            return true


    }

    //Function that executes all processes
    var ProcessProcesses=function(Process,Version,IType,GenBucket,save=false){
      

        IterationType=IType.split('_');
        InputStream = IterationType[1];
        let Type=IterationType[0];
        Log.Info('Input Stream :' +InputStream)

        var BatchParams1='{"VersionName" : "'+Version+'", "ForecastIterationType":"'+Type+'","InputStream":["'+InputStream+'"]};'

        var BatchParams2=' {"VersionName" : "'+Version+'", "ForecastIterationType" : "'+Type+'", "Week": true,"Month":false,"PlanningMonth":false, "Quarter":false,"PlanningQuarter":false,"InputStream":["'+InputStream+'"]};' 
        if(GenBucket=='Month')
            BatchParams2=' {"VersionName" : "'+Version+'", "ForecastIterationType" : "'+Type+'", "Week": false,"Month":true,"PlanningMonth":false, "Quarter":false,"PlanningQuarter":false,"InputStream":["'+InputStream+'"]};'
        if(GenBucket=='Planning Month')
            BatchParams2=' {"VersionName" : "'+Version+'", "ForecastIterationType" : "'+Type+'", "Week": false,"Month":false,"PlanningMonth":true, "Quarter":false,"PlanningQuarter":false,"InputStream":["'+InputStream+'"]};'
        if(GenBucket=='Quarter')
            BatchParams2=' {"VersionName" : "'+Version+'", "ForecastIterationType" : "'+Type+'", "Week": false,"Month":false,"PlanningMonth":false, "Quarter":true,"PlanningQuarter":false,"InputStream":["'+InputStream+'"]};'
        if(GenBucket=='Planning Quarter')
            BatchParams2=' {"VersionName" : "'+Version+'", "ForecastIterationType" : "'+Type+'", "Week": false,"Month":false,"PlanningMonth":false, "Quarter":false,"PlanningQuarter":true,"InputStream":["'+InputStream+'"]};'


        var BatchParams3='{"VersionName" : "'+Version+'", "ForecastIterationType" : "'+Type+'","ShortTerm":true , "MidTerm":false, "LongTerm":false, "VeryShortTerm":false ,"VeryLongTerm":false ,"InputStream":["'+InputStream+'"]};'
        if(Type=='Mid Term')
            BatchParams3='{"VersionName" : "'+Version+'", "ForecastIterationType" : "'+Type+'","ShortTerm":false , "MidTerm":true, "LongTerm":false, "VeryShortTerm":false ,"VeryLongTerm":false ,"InputStream":["'+InputStream+'"]};'
        if(Type=='Long Term')
            BatchParams3='{"VersionName" : "'+Version+'", "ForecastIterationType" : "'+Type+'","ShortTerm":false , "MidTerm":false, "LongTerm":true, "VeryShortTerm":false ,"VeryLongTerm":false ,"InputStream":["'+InputStream+'"]};'
        if(Type=='Very Short Term')
            BatchParams3='{"VersionName" : "'+Version+'", "ForecastIterationType" : "'+Type+'","ShortTerm":false , "MidTerm":false, "LongTerm":false, "VeryShortTerm":true ,"VeryLongTerm":false ,"InputStream":["'+InputStream+'"]};'
        if(Type=='Very Long Term')
            BatchParams3='{"VersionName" : "'+Version+'", "ForecastIterationType" : "'+Type+'","ShortTerm":false , "MidTerm":false, "LongTerm":false, "VeryShortTerm":false ,"VeryLongTerm":true ,"InputStream":["'+InputStream+'"]};'
        
        if(Process=='Generate Stat Actuals'){
            
            GenerateStatActuals(Version,IType)
        }
        if(Process=='Accuracy Calculation')	{
            var AccuracyQuery= 'exec procedure '+BatchProcs['CalculateAccuracyStat'] + ' {"VersionName" : ["'+Version+'"], "ForecastIterationType" : "'+Type+'", "Week": true,"Month":false,"PlanningMonth":false, "Quarter":false,"PlanningQuarter":false,"InputStream":["'+InputStream+'"]};'
            Log.Info('Running Accuracy Calculation for Version :'+Version)
            Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
            queryModule.select(AccuracyQuery)
        }
        if(Process=='Generate Segmentation')
        {

            condition=CheckCondition(Version,IType,'ML')
            if(condition)
                return;
            
            var SegementationQuery='exec procedure '+BatchProcs['Segmentation']+BatchParams1 
            if(CheckCondition(Version,IType,'FastStat'))
                SegmentationQuery='exec procedure o9DP0900GenerateSegmentationFastStat'+BatchParams1

            Log.Info('Running Segmentation for Version :'+Version)
            
            Log.Info('Current Version :'+Version  + ', Current Process :'+Process)

          

            queryModule.select(SegementationQuery)

        }

        if(Process=='Generate Outlier Cleansing')
        {
            condition=CheckCondition(Version,IType,'ML')
            if(condition)
                return;

            var OutlierQuery='exec procedure '+BatchProcs['OutlierCorrection']+BatchParams2 
            Log.Info('Running Outlier Correction for Version :'+Version)
            Log.Info('Current Version :'+Version  + ', Current Process :'+Process)    
            queryModule.select(OutlierQuery)

            if(save)
                queryModule.select('save();');
        }
        if(Process=='Generate Assigned Rules')
        {
            var UpdateBestfitQuery='exec procedure '+BatchProcs['BestfitFlag']+BatchParams2 
            var AssignRuleQuery='exec procedure '+BatchProcs['AssignRuleAndAlgo']+BatchParams1 

            Log.Info('Running Assign Rule and Algo for Version :'+Version)
            Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
            queryModule.select(AssignRuleQuery)
            condition=CheckCondition(Version,IType,'ML')
            if(condition)
                return;
            condition=CheckCondition(Version,IType,'SCHM')
            if(condition)
            {
                Log.Info('Running Seasonal Indices for Version :'+Version)
                var SeasonalQuery='exec procedure '+BatchProcs['SeasonalIndices']+BatchParams1 
                Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
                queryModule.select(SeasonalQuery)
            }

            Log.Info('Running Update Bestfit Flag for Version :'+Version)
            Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
            queryModule.select(UpdateBestfitQuery)


            var PreProcessQuery='exec procedure '+BatchProcs['PreprocessSystemStat']+BatchParams3 
                Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
                queryModule.select(PreProcessQuery);
        }

        if(Process=='Generate System Stat')
        {
            var condition=CheckCondition(Version,IType,'GenML')
            if(condition){
                var SystemStatQuery='exec procedure '+BatchProcs['GenerateSystemML']+BatchParams1 
                Log.Info('Running Generate System ML for Version :'+Version)
                    Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
                    PopulateStartTime(Version,IType,'Generate System Stat')
                    queryModule.select(SystemStatQuery)
                    PopulateEndTime(Version,IType,'Generate System Stat')
            }

            condition=CheckCondition(Version,IType,'GenStat')
            if(condition){
                var BestfitCondition = CheckCondition(Version,IType,'BestfitItems')
                var FastStatCondition = CheckCondition(Version,IType,'FastStat')

                var NoLCBestfitItems='Select([Item].[Segmentation LOB].filter(isNull(#.[Bestfit Flag '+Type+'])));'
                var NoLCBestfitCellset=cellsetModule.createCellSet(queryModule.select(NoLCBestfitItems))
              
            
                PopulateStartTime(Version,IType,'Generate System Stat')
                if(BestfitCondition||NoLCBestfitCellset.rowCount>0){
                    
                    if(FastStatCondition){
                        var SystemStatQuery='exec procedure '+BatchProcs['GenerateFastStat']+BatchParams3 
                        Log.Info('Running Generate System Stat for Version :'+Version)
                                    Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
                        queryModule.select(SystemStatQuery)
                    }
                    else{
                        var SystemStatQuery='exec procedure '+BatchProcs['GenerateSystemStat']+BatchParams3 
                        Log.Info('Running Generate Fast Stat for Version :'+Version)
                                    Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
                        queryModule.select(SystemStatQuery)
                    }
                   
                }
                else{
                    if(FastStatCondition){
                        var SystemStatQuery='exec procedure '+BatchProcs['GenerateFastStatUsingBestfitLC']+BatchParams3 
                        Log.Info('Running Generate System Stat for Version :'+Version)
                                    Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
                        queryModule.select(SystemStatQuery)
                    }
                    else{
                        var SystemStatQuery='exec procedure '+BatchProcs['GenerateSystemStatUsingBestfitLC']+BatchParams3 
                        Log.Info('Running Generate Fast Stat for Version :'+Version)
                                    Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
                        queryModule.select(SystemStatQuery)
                    }
                    
                }
                PopulateEndTime(Version,IType,'Generate System Stat')
            }
            if(save)
                queryModule.select('save();');


        }

        if(Process=='Output Checks')
        {
            var OutputChecksQuery='exec procedure '+BatchProcs['OutputChecks']+BatchParams2 
            Log.Info('Running Output Checks for Version :'+Version)
            Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
            queryModule.select(OutputChecksQuery)
        }

        if(Process=='Identify Bestfit Algorithm')
        {
            condition=CheckCondition(Version,IType,'Ensemble')
            if(condition){
                var OutputChecksQuery='exec procedure '+BatchProcs['OutputChecks']+BatchParams2 
                Log.Info('Running Output Checks for Version :'+Version)
                Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
                queryModule.select(OutputChecksQuery)
            }
            var BestfitQuery='exec procedure '+BatchProcs['IdentifyBestfitModel']+BatchParams1 
            Log.Info('Running Identify Bestfit Model for Version :'+Version)
            Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
            queryModule.select(BestfitQuery)
            
            if(condition){
                var EnsembleQuery='exec procedure '+BatchProcs['EnsembleFit']+BatchParams1 
                Log.Info('Running Ensemble Fit for Version :'+Version)
                    Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
                queryModule.select(EnsembleQuery)
            }

            if(save)
                queryModule.select('save();');

        }
        if(Process=='Populate Stat Fcst')
        {
            var PopulateStatQuery='exec procedure '+BatchProcs['PopulateBestFitForecast']+BatchParams3 
            Log.Info('Running Populate Bestfit Forecast for Version :'+Version)
            Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
            queryModule.select(PopulateStatQuery)
            // var L1LagModel='EXEC plugin instance [DP220CalculateLagModelL1] for measures {[Actual Cleansed]} using scope (&CWVAndScenarios.['+Version+']) using arguments {([IgnoreMissingOutputs], true), ([MaxSliceTableCells],2000000000),("IncludeNullRows", "False"), (DriverMemory, "5G"),([Param.o9_sys_log_level], "INFO")};'
            // queryModule.select(L1LagModel)
        }
        if(Process=='Disaggregate Stat')
        {
            var DisaggregateQuery='exec procedure '+BatchProcs['DisaggregateStat']+BatchParams1 
            if(CheckCondition(Version,IType,'FastStat'))
                DisaggregateQuery='exec procedure o9DP0910DisaggregateFastStat'+BatchParams1
            Log.Info('Running Disaggregate Stat for Version :'+Version)
            Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
            queryModule.select(DisaggregateQuery)

            if(save)
                queryModule.select('save();');

