                var SliceAssocNull='delete data for model [860 Slice Association Stat] where {&CWVAndScenarios.filter(#.Name in{"'+Version+'"}), &AllForecastIterations.filter(#.Name in{"'+ForecastIteration+'"})};delete data for model [870 Slice Association TL] where {&CWVAndScenarios.filter(#.Name in{"'+Version+'"}), &AllForecastIterations.filter(#.Name in{"'+ForecastIteration+'"})};delete data for model [880 Slice Association PL] where {&CWVAndScenarios.filter(#.Name in{"'+Version+'"}),&AllForecastIterations.filter(#.Name in{"'+ForecastIteration+'"})};'

                var SliceAssoc='EXEC plugin instance [DP041PopulateSliceAssociation] for measures {[Trend Strength L1]} using scope (&CWVAndScenarios.filter(#.Name in{"'+Version+'"}) *&AllForecastIterations.filter(#.Name in{"'+ForecastIteration+'"})) using arguments {([IgnoreMissingOutputs], true) ,([MaxSliceTableCells], 900000000),([MaxMessageLength],99999990),("IncludeNullRows", "False"),("SparkProfileConfig", "unsliced_low")};'

