                        actual:'Select ({DistinctCount([Region].[Planning Region] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Location].[Planning Location] * [Demand Domain].[Planning Demand Domain] * [Account].[Planning Account] * [Channel].[Planning Channel] ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst PL Lag Abs Error])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
                    },
