                        actual:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst Ensemble])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
                    }
                
                    }


                if(Process=='Generate Stat Actuals')
                {
                    PopulateMeasures('actual',Process,Iteration,ExtractMeasure(SummaryQueries[Process].query,'Stat Actual'),Version)
                    let ExpectQuery='Select ([Version].[Version Name].['+Version+']  ) on row,({Measure.[Stat Actual L0],Measure.[Forecast Iteration Selection]}) on column where {Measure.[Forecast Iteration Selection]=="'+Iteration+'"};'
                    PopulateMeasures('expect',Process,Iteration,ExtractMeasure(ExpectQuery,'Stat Actual L0'),Version)
                     
                    ExpectQuery='Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Actual])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
