                    PopulateMeasures('expect','Seasonality Detection',Iteration,ExtractMeasure(SummaryQueries['Seasonality Detection'].query),Version)
                    PopulateMeasures('actual','Seasonality Detection',Iteration,ExtractMeasure(SummaryQueries['Seasonality Detection'].query),Version)

