                    let ExpectValue=ExtractMeasure(SummaryQueries[Process].expect)-noStatIntersections
                    let ActualValue=ExtractMeasure(SummaryQueries[Process].query)+CMLCount
                    if(ActualValue!=ExpectValue)
                        if(ActualValue+DiscoValue==ExpectValue)
                            ExpectValue=ActualValue   
                    
                    PopulateMeasures('actual',Process,Iteration,ActualValue,Version)
                    PopulateMeasures('expect',Process,Iteration,ExpectValue,Version)
                    PopulateMeasures('expect','Generate CML Fcst',Iteration,CMLExpect-noStatIntersections,Version)
                    PopulateMeasures('actual','Generate CML Fcst',Iteration,CMLActual-noStatIntersections,Version)
                   

                }
                if(Process=='Identify Bestfit Algorithm'){
                    let CMLIntersections='Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[System Bestfit Algorithm]=="CML"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'

                    let CMLCount=ExtractMeasure(CMLIntersections)
                    let DiscoValue=ExtractMeasure(SummaryQueries['Generate System Stat'].disco)
                    let noStatIntersections=ExtractMeasure(SummaryQueries['Generate System Stat'].nostat)
                    let ExpectValue=ExtractMeasure(SummaryQueries['Generate System Stat'].expect)-noStatIntersections
                    let ActualValue=ExtractMeasure(SummaryQueries['Generate System Stat'].query)+CMLCount
                    if(ActualValue!=ExpectValue)
                        if(ActualValue+DiscoValue==ExpectValue)
                            ExpectValue=ActualValue   

                    PopulateMeasures('actual',Process,Iteration,ExtractMeasure(SummaryQueries[Process].query),Version)
                    PopulateMeasures('expect',Process,Iteration,ExpectValue,Version)
                    PopulateMeasures('actual','Populate Analysis Cockpit',Iteration,ExtractMeasure(SummaryQueries[Process].query),Version)
                    PopulateMeasures('expect','Populate Analysis Cockpit',Iteration,ExpectValue,Version)

                }
                if(Process=='Populate Stat Fcst'){
                    let CMLIntersections='Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[System Bestfit Algorithm]=="CML"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'

                    let CMLCount=ExtractMeasure(CMLIntersections)
                    let DiscoValue=ExtractMeasure(SummaryQueries['Generate System Stat'].disco)
