				    queryModule.select(PostProcessPL)
				}



                var BucketWeigtNull='scope:(&AllStatBuckets * &CWVAndScenarios.filter(#.Name in{"'+Version+'"}) *&AllForecastIterations.filter(#.Name in{"'+ForecastIteration+'"}) );Measure.[Stat Bucket Weight]= NULL;end scope;'
                

                var DimCountNull='scope : (&CWVAndScenarios.filter(#.Name in{"'+Version+'"}) *&AllForecastIterations.filter(#.Name in{"'+ForecastIteration+'"}));Measure.[Account L1 Count] = NULL ;Measure.[Account L2 Count] = NULL ;Measure.[Account L3 Count] = NULL ;Measure.[Account L4 Count] = NULL ;Measure.[Actual At Version] = NULL ;Measure.[Channel L1 Count] = NULL ;Measure.[Channel L2 Count] = NULL ;Measure.[Demand Domain L1 Count] = NULL ;Measure.[Demand Domain L2 Count] = NULL ;Measure.[Demand Domain L3 Count] = NULL ;Measure.[Demand Domain L4 Count] = NULL ;Measure.[L1 Count] = NULL ;Measure.[L2 Count] = NULL ;Measure.[L3 Count] = NULL ;Measure.[L4 Count] = NULL ;Measure.[L5 Count] = NULL ;Measure.[L6 Count] = NULL ;Measure.[Location Country Count] = NULL ;Measure.[Location Region Count] = NULL;Measure.[Location Type Count] = NULL ;Measure.[Planning Account Count] = NULL ;Measure.[Planning Channel Count] = NULL ;Measure.[Planning Demand Domain Count] = NULL ;Measure.[Planning Item Count] = NULL ;Measure.[Planning Location Count] = NULL ;Measure.[Planning PnL Count] = NULL ;Measure.[Planning Region Count] = NULL ;Measure.[PnL L1 Count] = NULL ;Measure.[PnL L2 Count] = NULL ;Measure.[PnL L3 Count] = NULL ;Measure.[PnL L4 Count] = NULL ;Measure.[Region L1 Count] = NULL ;Measure.[Region L2 Count] = NULL ;Measure.[Region L3 Count] = NULL ;Measure.[Region L4 Count] = NULL ;Measure.[Reporting Location Count] = NULL ;Measure.[Stat Account Count] = NULL ;Measure.[Stat Channel Count] = NULL ;Measure.[Stat Demand Domain Count] = NULL ;Measure.[Stat Item Count] = NULL ;Measure.[Stat Location Count] = NULL ;Measure.[Stat PnL Count] = NULL ;Measure.[Stat Region Count] = NULL ;end scope ;'

                var BucketWeight='Exec plugin instance [DP046PopulatePWDisaggProfile] for measures {[Actual]} using scope (&CWVAndScenarios.filter(#.Name in{"'+Version+'"}) *&AllForecastIterations.filter(#.Name in{"'+ForecastIteration+'"})) using arguments {([IgnoreMissingOutputs], true), ([MaxSliceTableCells],2000000000), ("IncludeNullRows", False),("SparkProfileConfig", "unsliced_low")};'

                var DimCount ='Exec plugin instance [DP051PopulateDimAttributeCount] for measures {[Actual]} using scope (&CWVAndScenarios.filter(#.Name in{"'+Version+'"}) *&AllForecastIterations.filter(#.Name in{"'+ForecastIteration+'"})) using arguments {([IgnoreMissingOutputs], true), ([MaxSliceTableCells],2000000000), ("IncludeNullRows", False),("SparkProfileConfig", "unsliced_low")};'

                var SliceAssocNull='delete data for model [860 Slice Association Stat] where {&CWVAndScenarios.filter(#.Name in{"'+Version+'"}), &AllForecastIterations.filter(#.Name in{"'+ForecastIteration+'"})};delete data for model [870 Slice Association TL] where {&CWVAndScenarios.filter(#.Name in{"'+Version+'"}), &AllForecastIterations.filter(#.Name in{"'+ForecastIteration+'"})};delete data for model [880 Slice Association PL] where {&CWVAndScenarios.filter(#.Name in{"'+Version+'"}),&AllForecastIterations.filter(#.Name in{"'+ForecastIteration+'"})};'

                var SliceAssoc='EXEC plugin instance [DP041PopulateSliceAssociation] for measures {[Trend Strength L1]} using scope (&CWVAndScenarios.filter(#.Name in{"'+Version+'"}) *&AllForecastIterations.filter(#.Name in{"'+ForecastIteration+'"})) using arguments {([IgnoreMissingOutputs], true) ,([MaxSliceTableCells], 900000000),([MaxMessageLength],99999990),("IncludeNullRows", "False"),("SparkProfileConfig", "unsliced_low")};'

                queryModule.select(BucketWeigtNull)
