                PopulateStartTime(Version,IType,'Generate Slice Association')
                queryModule.select(SliceAssoc)
                PopulateEndTime(Version,IType,'Generate Slice Association')


                // var AccuracyQuery='EXEC plugin instance [DP212CalculateRollOverAccuracyIteration] for measures {[Actual Cleansed]} using scope (&CWVAndScenarios.['+Version+']*[Forecast Iteration].[Forecast Iteration Type].['+IType+'].relatedmembers([Forecast Iteration])) using arguments {([IgnoreMissingOutputs], true), ([MaxSliceTableCells],2000000000),("IncludeNullRows", "False"),(DriverMemory, "10G"), (DriverCores, 2), (NumExecutors, 3), (ExecutorMemory, "3G"), (ExecutorCores, 4),("SparkProfileConfig", "sliced_low"),(DataTransferMode,"csv"),([Param.o9_sys_log_level], "INFO")};'
                
