    var PopulateMeasures=function(type,process,Iteration,value,Version)
    {
            
        var queryModule=require('o9/data/query')
        Log.Info(process+" "+type+" Count: "+value)
        if(type=="expect")
            {
                var scopeQuery='cartesian scope: ([Version].[Version Name].['+Version+'] * [Process].[Process].['+process+'] * [Forecast Iteration].[Forecast Iteration].['+Iteration+']); Measure.[Stat Process Expected Output]='+value+'; end scope;'
                queryModule.select(scopeQuery)
            }
        else if(type=="actual")
            {
                var scopeQuery='cartesian scope: ([Version].[Version Name].['+Version+'] * [Process].[Process].['+process+'] * [Forecast Iteration].[Forecast Iteration].['+Iteration+']); Measure.[Stat Process Actual Output]='+value+'; end scope;'
                queryModule.select(scopeQuery)
            }

    }




    
    var ExtractMeasure=function(Query,Measure='RecordCount'){
  
        var QueryCellset=cellsetModule.createCellSet(queryModule.select(Query))
        
        if (QueryCellset.rowCount==0)
            return 0 
        var value =QueryCellset.row(0).cell(QueryCellset.measureColumnIndex(Measure))   
        Log.Info("Value be : "+value)
        return value
        
    }
    var itemize=function(item){
        var itemdata=''+item+''
        itemdata=itemdata.split(",").map(i=>"\""+i+"\"")
        return itemdata
    }


    const Grains ='[Region].[Stat Region] * [Item].[Stat Item] * [PnL].[Stat PnL] * [Location].[Stat Location] * [Demand Domain].[Stat Demand Domain] * [Account].[Stat Account] * [Channel].[Stat Channel]'
    



    
    var ExecuteProcesses = function(Version,IType,GenBucket,FullScope,Processes=StatProcesses,save=false){

        var CompletedVersions=[]
      
        
    
       Version.forEach(Version => {
        
       
            IterationType=IType.split('_');
            InputStream = IterationType[1];
            let Type=IterationType[0];
		    Log.Info('Input Stream :' +InputStream)

            

            
            var CompletedProcesses=[]

            if(FullScope){

                
                let deleteDashboard='delete data for model [400 Stat Process] where {&CWVAndScenarios.filter(#.Name in {"'+Version+'"}),[Forecast Iteration].[Forecast Iteration Type].filter(#.Name in {"'+IType+'"}).relatedmembers([Forecast Iteration])};'
                queryModule.select(deleteDashboard)
                var BatchStartQuery='Scope: ([Version].[Version Name].['+Version+'] * [Forecast Iteration].[Forecast Iteration Type].['+IType+'].relatedmembers([Forecast Iteration]) );Measure.[Stat Batch Start Time]=now();end scope;'

                Log.Info('Populating Batch Start Time for Version :'+Version)
                queryModule.select(BatchStartQuery)

                let LastCycleQuery='Scope: (&CWVAndScenarios.filter(#.Name in {"'+Version+'"}) * [Forecast Iteration].[Forecast Iteration Type].filter(#.Name in {"'+IType+'"}).relatedmembers([Forecast Iteration]) * [Region].[Stat Region] * [Location].[Stat Location] * [Channel].[Stat Channel] * [PnL].[Stat PnL] * [Item].[Stat Item] * [Demand Domain].[Stat Demand Domain] * [Time].[Partial Week] * [Account].[Stat Account] );Measure.[Stat Fcst L1 LC]=Measure.[Stat Fcst L1];end scope;'
                queryModule.select(LastCycleQuery)
             
            }

            
            Processes.forEach(Process => {
                Log.Info('Completed Versions :'+CompletedVersions.toString()  + ', Completed Processes :'+CompletedProcesses.toString())
                Log.Info('Current Version :'+Version  + ', Current Process :'+Process)
               

                ProcessProcesses(Process,Version,IType,GenBucket,save)
                
                FlagQ='Scope: (&CWVAndScenarios.['+Version+'] * [Process].[Process].['+Process+'] * [Forecast Iteration].[Forecast Iteration Type].['+IType+'].relatedmembers([Forecast Iteration]) ) ; Measure.[Stat Process Flag]=if(~isnull(Measure.[Stat Process Expected Output])) then 1;end scope;'
                queryModule.select(FlagQ)
    
                //Populate Process Status
                ProcessQ='Scope: (&CWVAndScenarios.['+Version+'] * [Process].[Process].['+Process+'] *  [Forecast Iteration].[Forecast Iteration Type].['+IType+'].relatedmembers([Forecast Iteration])  ) ; Measure.[Stat Process Status]=if(Measure.[Actual vs Expected Stat Process Output Gap %]<0.05 && Measure.[Actual vs Expected Stat Process Output Gap %]>=0) then 1; end scope;'
                queryModule.select(ProcessQ)


                var ForecastIteration = 'Select ([Forecast Iteration].[Forecast Iteration] * [Forecast Iteration].[Forecast Iteration Type].filter(#.Name startswith(['+IType+'])));';
                var ForecastIterationq = cellsetModule.createCellSet(queryModule.select(ForecastIteration));
                var ForecastIterationData = ForecastIterationq.attributeMembers('Forecast Iteration', 'Forecast Iteration');
                var ForecastIterationDatalist = ForecastIterationData.map(function (item) {
                    return item['Name'];
                 });
                ForecastIterationDatalist.forEach(Iteration=>{
                                  
                    var HistoryQuery='Select([Version].[Version Name].['+Version+']*[Forecast Iteration].[Forecast Iteration].['+Iteration+']) on row, ({Measure.[History Period]}) on column;'
                    var HistroyPeriod=ExtractMeasure(HistoryQuery,'History Period')
                    var HistoryNamedset='[Time].[Week].filter(#.Key<&CurrentWeek.element(0).Key && #.Key>=&CurrentWeek.element(0).leadoffset(-'+HistroyPeriod+').Key)'
                    if(GenBucket=="Week")
                        {
                            HistoryNamedset='[Time].[Week].filter(#.Key<&CurrentWeek.element(0).Key && #.Key>=&CurrentWeek.element(0).leadoffset(-'+HistroyPeriod+').Key)'
                        }
                    if(GenBucket=="Month")
                        {
                            HistoryNamedset='[Time].[Month].filter(#.Key<&CurrentMonth.element(0).Key && #.Key>=&CurrentMonth.element(0).leadoffset(-'+HistroyPeriod+').Key)'
                        }
                    if(GenBucket=="Planning Month")
                        {
                            HistoryNamedset='[Time].[Planning Month].filter(#.Key<&CurrentPlanningMonth.element(0).Key && #.Key>=&CurrentPlanningMonth.element(0).leadoffset(-'+HistroyPeriod+').Key)'
                        }
                    if(GenBucket=="Quarter")
                        {
                            HistoryNamedset='[Time].[Quarter].filter(#.Key<&CurrentQuarter.element(0).Key && #.Key>=&CurrentQuarter.element(0).leadoffset(-'+HistroyPeriod+').Key)'
                        }
                    if(GenBucket=="Planning Quarter")
                        {
                            HistoryNamedset='[Time].[Planning Quarter].filter(#.Key<&CurrentPlanningQuarter.element(0).Key && #.Key>=&CurrentPlanningQuarter.element(0).leadoffset(-'+HistroyPeriod+').Key)'
                        }
                    let SummaryQueries= {
                    "Generate Stat Actuals":{query:'Select ([Version].[Version Name].['+Version+'] * [Forecast Iteration].[Forecast Iteration].['+Iteration+'] ) on row, ({Measure.[Stat Actual]}) on column;'},
                    "Stat Actuals":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[Stat Actual]>0}) on row, () on column where {[Version].[Version Name].['+Version+'], '+HistoryNamedset+',[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'},
                    "Generate Slice Association":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Slice Association Stat])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'},
                    "Generate Segmentation":{query: 'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[PLC Status L1])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'},
                    "Seasonality Detection":{query: 'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Seasonality L1])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'},
                    "Generate Outlier Cleansing":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Actual Cleansed])}) on row, () on column where {[Version].[Version Name].['+Version+'], '+HistoryNamedset+',[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'},
                    "Generate Assigned Rules":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Assigned Algorithm List])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'},
                    "Generate System Stat":{
                            expect:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Actual])}) on row, () on column where {[Version].[Version Name].['+Version+'], '+HistoryNamedset+',[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                            
                            disco:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[PLC Status L1]==\"DISC\"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                            nostat:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[Assigned Algorithm List] contains \"No Forecast\"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                            query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Run Time])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',

                        
                        },
                    "Identify Bestfit Algorithm":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[System Bestfit Algorithm])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'},
                    "Populate Stat Fcst":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[System Stat Fcst L1])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'},
                    "Populate Analysis Cockpit":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Run Count])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'},
                    "PL Count Validation":{
                        query:'Select ( {DistinctCount([Region].[Planning Region] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Location].[Planning Location] * [Channel].[Planning Channel] * [Demand Domain].[Planning Demand Domain] * [Account].[Planning Account]) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst PL])} ) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']}; ',
                        
                        expect:'Select ( {DistinctCount([Region].[Planning Region] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Location].[Planning Location] * [Channel].[Planning Channel] * [Demand Domain].[Planning Demand Domain] * [Account].[Planning Account]) as Transient.RecordCount using ~isnull(Measure.[Stat Actual L0])&& Measure.[Forecast Iteration Selection]=="'+Iteration+'"} ) on row, () on column where {&CWV,[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
                    },
            
                    "TL Count Validation":{
                
                        query:'Select({DistinctCount( [Region].[Planning Region] * [Item].[Transition Item] * [PnL].[Planning PnL] *[Location].[Planning Location] * [Channel].[Planning Channel] * [Demand Domain].[Transition Demand Domain] * [Account].[Planning Account]) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst TL])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                        expect:'Select({DistinctCount( [Region].[Planning Region] * [Item].[Transition Item] * [PnL].[Planning PnL] *[Location].[Planning Location] * [Channel].[Planning Channel] * [Demand Domain].[Transition Demand Domain] * [Account].[Planning Account]) as Transient.RecordCount using ~isnull(Measure.[Stat Actual L0])&& Measure.[Forecast Iteration Selection]=="'+Iteration+'"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
                    },
                    "Stat Accuracy Calculation":{
                        expect:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst L1 Lag])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                        actual:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst L1 Lag Abs Error])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
                    },
                    "Planning Accuracy Calculation":{
                        expect:'Select ({DistinctCount([Region].[Planning Region] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Location].[Planning Location] * [Demand Domain].[Planning Demand Domain] * [Account].[Planning Account] * [Channel].[Planning Channel] ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst PL Lag])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                        actual:'Select ({DistinctCount([Region].[Planning Region] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Location].[Planning Location] * [Demand Domain].[Planning Demand Domain] * [Account].[Planning Account] * [Channel].[Planning Channel] ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst PL Lag Abs Error])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
                    },
                    "CML Count Validation":{
                        expect:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Actual])}) on row, () on column where {[Version].[Version Name].['+Version+'], '+HistoryNamedset+',[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                        actual:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst CML])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
                    },
                    "Ensemble Count Validation":{
                        expect:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Actual])}) on row, () on column where {[Version].[Version Name].['+Version+'], '+HistoryNamedset+',[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                        actual:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst Ensemble])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
                    }
                
                    }


                if(Process=='Generate Stat Actuals')
                {
                    PopulateMeasures('actual',Process,Iteration,ExtractMeasure(SummaryQueries[Process].query,'Stat Actual'),Version)
                    let ExpectQuery='Select ([Version].[Version Name].['+Version+']  ) on row,({Measure.[Stat Actual L0],Measure.[Forecast Iteration Selection]}) on column where {Measure.[Forecast Iteration Selection]=="'+Iteration+'"};'
                    PopulateMeasures('expect',Process,Iteration,ExtractMeasure(ExpectQuery,'Stat Actual L0'),Version)
                     
                    ExpectQuery='Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Actual])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
                    PopulateMeasures('expect','Generate Slice Association',Iteration,ExtractMeasure(ExpectQuery),Version)
                    PopulateMeasures('actual','Generate Slice Association',Iteration,ExtractMeasure(SummaryQueries['Generate Slice Association'].query),Version)
                }
                if(Process=='Generate Segmentation'){
                    condition=CheckCondition(Version,IType,'ML')
                    if(condition){
                        PopulateMeasures('expect',Process,Iteration,'0',Version)
                        PopulateMeasures('actual',Process,Iteration,'0',Version)
                        PopulateMeasures('expect','Seasonality Detection',Iteration,'0',Version)
                        PopulateMeasures('actual','Seasonality Detection',Iteration,'0',Version)    
                    return;
                    }
                    
                    let ExpectQuery=SummaryQueries['Stat Actuals'].query
                    PopulateMeasures('expect',Process,Iteration,ExtractMeasure(ExpectQuery),Version)
                    PopulateMeasures('actual',Process,Iteration,ExtractMeasure(SummaryQueries[Process].query),Version)
                    PopulateMeasures('expect','Seasonality Detection',Iteration,ExtractMeasure(SummaryQueries['Seasonality Detection'].query),Version)
                    PopulateMeasures('actual','Seasonality Detection',Iteration,ExtractMeasure(SummaryQueries['Seasonality Detection'].query),Version)

                }
        
               
                if(Process=='Generate Assigned Rules'||Process=='Generate Outlier Cleansing'){
                    condition=CheckCondition(Version,IType,'ML')
                    if(condition){
                        PopulateMeasures('expect',Process,Iteration,'0',Version)
                        PopulateMeasures('actual',Process,Iteration,'0',Version)  
                    return;
                    }
                    PopulateMeasures('actual',Process,Iteration,ExtractMeasure(SummaryQueries[Process].query),Version)
                    PopulateMeasures('expect',Process,Iteration,ExtractMeasure(SummaryQueries['Stat Actuals'].query),Version)

                }
                if(Process=='Generate System Stat'){

                    var CMLExpect=0;
                    var CMLActual=0;
                    
                    if(CheckCondition(Version,IType,'GenML')){
                        CMLExpect=ExtractMeasure(SummaryQueries['CML Count Validation'].expect)
                        CMLActual=ExtractMeasure(SummaryQueries['CML Count Validation'].actual)

                    }
                    


                    let CMLIntersections='Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[System Bestfit Algorithm]=="CML"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'

                    let CMLCount=ExtractMeasure(CMLIntersections)
                    let DiscoValue=ExtractMeasure(SummaryQueries[Process].disco)
                    let noStatIntersections=ExtractMeasure(SummaryQueries[Process].nostat)
                    let ExpectValue=ExtractMeasure(SummaryQueries[Process].expect)-noStatIntersections
                    let ActualValue=ExtractMeasure(SummaryQueries[Process].query)+CMLCount
                    if(ActualValue!=ExpectValue)
                        if(ActualValue+DiscoValue==ExpectValue)
                            ExpectValue=ActualValue   
                    
                    PopulateMeasures('actual',Process,Iteration,ActualValue,Version)
                    PopulateMeasures('expect',Process,Iteration,ExpectValue,Version)
                    PopulateMeasures('expect','Generate CML Fcst',Iteration,CMLExpect-noStatIntersections,Version)
                    PopulateMeasures('actual','Generate CML Fcst',Iteration,CMLActual-noStatIntersections,Version)
                   

                }
                if(Process=='Identify Bestfit Algorithm'){
                    let CMLIntersections='Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[System Bestfit Algorithm]=="CML"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'

                    let CMLCount=ExtractMeasure(CMLIntersections)
                    let DiscoValue=ExtractMeasure(SummaryQueries['Generate System Stat'].disco)
                    let noStatIntersections=ExtractMeasure(SummaryQueries['Generate System Stat'].nostat)
