                    PopulateMeasures('expect','Stat Accuracy Calculation',Iteration,ExtractMeasure(SummaryQueries['Stat Accuracy Calculation'].expect),Version)
                    PopulateMeasures('actual','Planning Accuracy Calculation',Iteration,ExtractMeasure(SummaryQueries['Planning Accuracy Calculation'].actual),Version)
                    PopulateMeasures('expect','Planning Accuracy Calculation',Iteration,ExtractMeasure(SummaryQueries['Planning Accuracy Calculation'].expect),Version)
               
                }
                if(Process=='Disaggregate Stat'){

                    var FutureNamedSet='&CurrentandFuturePartialWeeks'
                    if(GenBucket=="Week")
                        {
                            FutureNamedSet='[Time].[Week].filter(#.Key>=&CurrentWeek.element(0).Key )'
                        }
                    if(GenBucket=="Month")
                        {
                            FutureNamedSet='[Time].[Month].filter(#.Key>=&CurrentMonth.element(0).Key)'
                        }
                    if(GenBucket=="Planning Month")
                        {
                            FutureNamedSet='[Time].[Planning Month].filter(#.Key>=&CurrentPlanningMonth.element(0).Key)'
                        }
                    if(GenBucket=="Quarter")
                        {
                            FutureNamedSet='[Time].[Quarter].filter(#.Key>=&CurrentQuarter.element(0).Key)'
                        }
                    if(GenBucket=="Planning Quarter")
                        {
                            FutureNamedSet='[Time].[Planning Quarter].filter(#.Key>=&CurrentPlanningQuarter.element(0).Key)'
                        }

                    var DisagIntersections=0
                    var VolumeActual=0
                    var VolumeExpect=0
                    var TLVolumeActual=0
                    var DisagType=ExtractMeasure('Select([Version].[Version Name].['+Version+']*[Forecast Iteration].[Forecast Iteration].['+Iteration+']) on row, ({Measure.[Disaggregation Type]}) on column;','Disaggregation Type')
        
        
                    if(DisagType=="No Forecast")
                        {
                        DisagIntersections=DisagIntersections+ExtractMeasure('Select ( {DistinctCount([Region].[Planning Region] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Location].[Planning Location] * [Channel].[Planning Channel] * [Demand Domain].[Planning Demand Domain] * [Account].[Planning Account]) as Transient.RecordCount using ~isnull(Measure.[Stat Actual L0])&&Measure.[Forecast Iteration Selection]=="'+Iteration+'"} ) on row, () on column where {&CWV,[Forecast Iteration].[Forecast Iteration Type].['+IterationType+']};')
                        
                        }
                        else
                        {
                            VolumeActual=ExtractMeasure('Select([Version].[Version Name].['+Version+']*[Forecast Iteration].[Forecast Iteration].['+Iteration+']) on row, ({Measure.[Stat Fcst PL]}) on column;','Stat Fcst PL')
                            VolumeExpect=ExtractMeasure('Select([Version].[Version Name].['+Version+']*[Forecast Iteration].[Forecast Iteration].['+Iteration+']) on row, ({Measure.[Stat Fcst L1]}) on column where {'+FutureNamedSet+'};','Stat Fcst L1')
                            TLVolumeActual=ExtractMeasure('Select([Version].[Version Name].['+Version+']*[Forecast Iteration].[Forecast Iteration].['+Iteration+']) on row, ({Measure.[Stat Fcst TL]}) on column where {'+FutureNamedSet+'};','Stat Fcst TL')
                        }  

                        var Dims=['Item','Account','Channel','Region','Location','PnL','Demand Domain']
                        var Levels={}
                        Dims.forEach(element=>{
                        var query='Select([Forecast Iteration].[Forecast Iteration].['+Iteration+'] * [Version].[Version Name].['+Version+'] * ['+element+'].[Planning '+element+']) on row,({Measure.['+element+' Level]}) on column;'
                        Levels[element]=ExtractMeasure(query,element+' Level')
                        })  
            
                        var noStatQuery='Select ([Version].[Version Name].['+Version+'] * [Region].[Stat Region] * [Item].[Stat Item] * [PnL].[Stat PnL] * [Forecast Iteration].[Forecast Iteration].['+Iteration+'] * [Location].[Stat Location] * [Channel].[Stat Channel] * [Demand Domain].[Stat Demand Domain] * [Account].[Stat Account] ) on row, ({Measure.[Assigned Algorithm List]}) on column where {Measure.[Assigned Algorithm List] contains "No Forecast"};'
                        var items={}
                        var nostatCellset=cellsetModule.createCellSet(queryModule.select(noStatQuery))
                        Dims.forEach(element=>{
                            items[element]=[]   
                            for(var j=0;j<nostatCellset.rowCount;j++)
                                {
                                    items[element].push(nostatCellset.row(j).cell(nostatCellset.memberColumnIndex(element,'Stat '+element)).Name)
                                }
                            items[element]=itemize(items[element])
                        })
            
                        var nostatPLQuery='(Select([Version].[Version Name].['+Version+']* [Forecast Iteration].[Forecast Iteration].['+Iteration+']* [Region].['+Levels['Region']+'].filter(#.Name in {'+items['Region']+'}).relatedmembers([Planning Region])*[Item].['+Levels['Item']+'].filter(#.Name in {'+items['Item']+'}).relatedmembers([Planning Item])*[Account].['+Levels['Account']+'].filter(#.Name in {'+items['Account']+'}).relatedmembers([Planning Account])*[Channel].['+Levels['Channel']+'].filter(#.Name in {'+items['Channel']+'}).relatedmembers([Planning Channel])*[Location].['+Levels['Location']+'].filter(#.Name in {'+items['Location']+'}).relatedmembers([Planning Location])*[PnL].['+Levels['PnL']+'].filter(#.Name in {'+items['PnL']+'}).relatedmembers([Planning PnL])*[Demand Domain].['+Levels['Demand Domain']+'].filter(#.Name in {'+items['Demand Domain']+'}).relatedmembers([Planning Demand Domain])) on row, ({Measure.[Stat Actual L0],Measure.[Stat Fcst PL]}) on column).filter(isnull(Measure.[Stat Fcst PL]));'
                        var nostatPLCellSet=cellsetModule.createCellSet(queryModule.select(nostatPLQuery))
                        var nostatPLcols=nostatPLCellSet.rowCount
                        var nostatTLQuery='(Select([Version].[Version Name].['+Version+']* [Forecast Iteration].[Forecast Iteration].['+Iteration+']* [Region].['+Levels['Region']+'].filter(#.Name in {'+items['Region']+'}).relatedmembers([Planning Region])*[Item].['+Levels['Item']+'].filter(#.Name in {'+items['Item']+'}).relatedmembers([Transition Item])*[Account].['+Levels['Account']+'].filter(#.Name in {'+items['Account']+'}).relatedmembers([Planning Account])*[Channel].['+Levels['Channel']+'].filter(#.Name in {'+items['Channel']+'}).relatedmembers([Planning Channel])*[Location].['+Levels['Location']+'].filter(#.Name in {'+items['Location']+'}).relatedmembers([Planning Location])*[PnL].['+Levels['PnL']+'].filter(#.Name in {'+items['PnL']+'}).relatedmembers([Planning PnL])*[Demand Domain].['+Levels['Demand Domain']+'].filter(#.Name in {'+items['Demand Domain']+'}).relatedmembers([Transition Demand Domain])) on row, ({Measure.[Stat Actual L0],Measure.[Stat Fcst TL]}) on column).filter(isnull(Measure.[Stat Fcst TL]));'
                        var nostatTLCellSet=cellsetModule.createCellSet(queryModule.select(nostatTLQuery))
                        var nostatTLcols=nostatTLCellSet.rowCount
                        
                        Log.Info('NoStat PL: '+nostatPLcols+' NoStat TL: '+nostatTLcols)

                        PopulateMeasures('actual','PL Volume Loss Validation',Iteration,VolumeActual,Version)
                        PopulateMeasures('expect','PL Volume Loss Validation',Iteration,VolumeExpect,Version)
                        PopulateMeasures('actual','TL Volume Loss Validation',Iteration,TLVolumeActual,Version)
                        PopulateMeasures('expect','TL Volume Loss Validation',Iteration,VolumeExpect,Version)


                        ActualValue=ExtractMeasure(SummaryQueries['PL Count Validation'].query)
                        ExpectValue=ExtractMeasure(SummaryQueries['PL Count Validation'].expect)
                        if(ActualValue+DisagIntersections+nostatPLcols==ExpectValue)
                        ExpectValue=ActualValue
                        else
                        ExpectValue=ExpectValue-nostatPLcols
                        PopulateMeasures('actual','PL Count Validation',Iteration,ActualValue,Version)
                        let finalCount=ActualValue
                        PopulateMeasures('expect','PL Count Validation',Iteration,ExpectValue,Version)

                        ActualValue=ExtractMeasure(SummaryQueries['TL Count Validation'].query)
                        ExpectValue=ExtractMeasure(SummaryQueries['TL Count Validation'].expect)
                        if(ActualValue+DisagIntersections+nostatTLcols==ExpectValue)
                            ExpectValue=ActualValue
                        else
                        ExpectValue=ExpectValue-nostatTLcols
                        PopulateMeasures('actual','TL Count Validation',Iteration,ActualValue,Version)
                        PopulateMeasures('expect','TL Count Validation',Iteration,ExpectValue,Version)

                        PopulateMeasures('expect',Process,Iteration,ExpectValue,Version)
                        PopulateMeasures('actual',Process,Iteration,finalCount,Version)

  
                    
                }
                

                FlagQ='Scope: (&CWVAndScenarios.['+Version+'] * [Process].[Process].['+Process+'] * [Forecast Iteration].[Forecast Iteration].['+Iteration+'] ) ; Measure.[Stat Process Flag]=if(~isnull(Measure.[Stat Process Expected Output])) then 1;end scope;'
                queryModule.select(FlagQ)
    
                //Populate Process Status
                ProcessQ='Scope: (&CWVAndScenarios.['+Version+'] * [Process].[Process].['+Process+'] * [Forecast Iteration].[Forecast Iteration].['+Iteration+'] ) ; Measure.[Stat Process Status]=if(Measure.[Actual vs Expected Stat Process Output Gap %]<0.05 && Measure.[Actual vs Expected Stat Process Output Gap %]>=0) then 1; end scope;'
                queryModule.select(ProcessQ)


                })
                
                
            
                CompletedProcesses.push(Process)

            })
            

            if(FullScope){
                
                
                
                var BatchEndQuery='Scope: ([Version].[Version Name].['+Version+'] * [Forecast Iteration].[Forecast Iteration Type].['+IType+'].relatedmembers([Forecast Iteration]) );Measure.[Stat Batch End Time]=now();end scope;'

                Log.Info('Populating Batch Start Time for Version :'+Version)
                queryModule.select(BatchEndQuery)
            
            
            
            }
            

            CompletedVersions.push(Version)
       })
       
       

    }



    var RunBatch = function(o9Params){

        parsedParams = JSON.parse(o9Params)
        var VersionName=parsedParams.VersionName
        var Types=parsedParams.ForecastIterationType
        var Cycles=parsedParams.PlanningCycles;
        var ExecutionMode=parsedParams.ExecutionMode
        var save=parsedParams.Save


        CompletedCycles=[]

        if(ExecutionMode=='Prod')
        {
            {
                var UserProcesses=parsedParams.StatProcesses
                if (UserProcesses.includes('Forecast Reconciliation')) {
                    UserProcesses = UserProcesses.filter(process => process !== 'Forecast Reconciliation');
                    UserProcesses.push('Reconcile')
                }

                if (UserProcesses.includes('Stat Accuracy Calculation') || UserProcesses.includes('Planning Accuracy Calculation')) {
                    UserProcesses = UserProcesses.filter(process => process !== 'Stat Accuracy Calculation' && process !== 'Planning Accuracy Calculation');
                    UserProcesses.unshift('Accuracy Calculation');
                }
                Types.forEach(Type=>{
                    var Query='Select (&CWV * [Forecast Iteration].[Forecast Iteration Type].['+Type+']) on row,({Measure.[Forecast Generation Time Bucket]}) on column;'
                    var Cellset=cellsetModule.createCellSet(queryModule.select(Query))
                    var GenBucket=Cellset.row(0).cell(Cellset.measureColumnIndex('Forecast Generation Time Bucket'))
                    Log.Info("TEST : EXECUTING SPECIFIC PROCESSES")
                    
                ExecuteProcesses(VersionName,Type,GenBucket,false,UserProcesses)
                })
            }
        }
        else{

                var ShortCycles={}
                var MidCycles={}
                var LongCycles={}
                var VeryShortCycles={}
                var VeryLongCycles={}

                var InputStreams=[]
                Types.forEach(IterationType=>{
                    let InputStream=IterationType.split('_')[1];
                    if (!InputStreams.includes(InputStream)) {
                        InputStreams.push(InputStream);
                    }
                    ShortCycles[InputStream] = [];
                    MidCycles[InputStream] = [];
                    LongCycles[InputStream] = [];
                    VeryShortCycles[InputStream] = [];
                    VeryLongCycles[InputStream] = [];

                })

            
                var WTimeQuery='Select( [Time].[Week].filter(#.Key>&CurrentWeek.element(0).leadoffset(-6).Key && #.Key<=&CurrentWeek.element(0).leadoffset(6).Key));'
                var MTimeQuery='Select( [Time].[Month].filter(#.Key>&CurrentMonth.element(0).leadoffset(-6).Key && #.Key<=&CurrentMonth.element(0).leadoffset(6).Key));'
                var PMTimeQuery='Select( [Time].[Planning Month].filter(#.Key>&CurrentPlanningMonth.element(0).leadoffset(-6).Key && #.Key<=&CurrentPlanningMonth.element(0).leadoffset(6).Key));'
                var QTimeQuery='Select( [Time].[Quarter].filter(#.Key>&CurrentQuarter.element(0).leadoffset(-6).Key && #.Key<=&CurrentQuarter.element(0).leadoffset(6).Key));'
                var PQTimeQuery='Select( [Time].[Planning Quarter].filter(#.Key>&CurrentPlanningQuarter.element(0).leadoffset(-6).Key && #.Key<=&CurrentPlanningQuarter.element(0).leadoffset(6).Key));'
                
               
                Types.forEach(IterationType=>{
                    let InputStream=IterationType.split('_')[1];
               

                    var Query='Select (&CWV * [Forecast Iteration].[Forecast Iteration Type].['+IterationType+']) on row,({Measure.[Forecast Generation Time Bucket]}) on column;'
                    var Cellset=cellsetModule.createCellSet(queryModule.select(Query))
                    var GenBucket=Cellset.row(0).cell(Cellset.measureColumnIndex('Forecast Generation Time Bucket'))

                    var TimeCellset=[]
                    if(GenBucket=='Week')
                        TimeCellset=cellsetModule.createCellSet(queryModule.select(WTimeQuery))
                    if(GenBucket=='Month')
                        TimeCellset=cellsetModule.createCellSet(queryModule.select(MTimeQuery))
                    if(GenBucket=='Planning Month')
                        TimeCellset=cellsetModule.createCellSet(queryModule.select(PMTimeQuery))
                    if(GenBucket=='Quarter')
                        TimeCellset=cellsetModule.createCellSet(queryModule.select(QTimeQuery))
                    if(GenBucket=='Planning Quarter')
                        TimeCellset=cellsetModule.createCellSet(queryModule.select(PQTimeQuery))

                    var TypeCycles=TimeCellset.attributeMembers("Time",GenBucket).map(function (item) {
                        return 'todatetime("'+item['Key']+'")';
                        })
                    
                    var CyclesQuery='Select([Time].[Day].filter(#.Key in {'+TypeCycles+'}));'
                    var CyclesCellset=cellsetModule.createCellSet(queryModule.select(CyclesQuery))
                    TypeCycles=CyclesCellset.attributeMembers("Time","Day").map(function (item) {
                        return item['Name'];
                        })

                    
                    Log.Info('Cycles for '+IterationType+' :'+TypeCycles)   
                    if(IterationType.includes('Very Short'))
                        VeryShortCycles[InputStream]=[...VeryShortCycles[InputStream],...TypeCycles]
                    else if(IterationType.includes('Short'))
                        ShortCycles[InputStream]=[...ShortCycles[InputStream],...TypeCycles]
                    else if(IterationType.includes('Mid'))
                        MidCycles[InputStream]=[...MidCycles[InputStream],...TypeCycles]
                    else if(IterationType.includes('Very Long'))
                        VeryLongCycles[InputStream]=[...VeryLongCycles[InputStream],...TypeCycles]
                    else if(IterationType.includes('Long'))
                        LongCycles[InputStream]=[...LongCycles[InputStream],...TypeCycles]   
             
                    
                })

            var STCycles={}    
            var MTCycles ={}
            var LTCycles ={}
            var VSTCycles ={}
            var VLTCycles ={}


            

            InputStreams.forEach(InputStream=>{
              
    
            
            
            STCycles[InputStream] =[...new Set(Cycles.filter(item => ShortCycles[InputStream].includes(item)))];
        
            MTCycles[InputStream]= [...new Set(Cycles.filter(item => MidCycles[InputStream].includes(item)))];
            
            LTCycles[InputStream] = [...new Set(Cycles.filter(item => LongCycles[InputStream].includes(item)))];

            VSTCycles[InputStream] = [...new Set(Cycles.filter(item => VeryShortCycles[InputStream].includes(item)))];
            VLTCycles[InputStream] = [...new Set(Cycles.filter(item => VeryLongCycles[InputStream].includes(item)))];
            })

            Log.Info('Selected Cycles :'+JSON.stringify(Cycles))
            Log.Info('Short Term Cycles :'+JSON.stringify(STCycles))	
            Log.Info('Mid Term Cycles :'+JSON.stringify(MTCycles))
            Log.Info('Long Term Cycles :'+JSON.stringify(LTCycles))
            Log.Info('Very Short Term Cycles :'+JSON.stringify(VSTCycles))
            Log.Info('Very Long Term Cycles :'+JSON.stringify(VLTCycles))


            Cycles.sort((a, b) => new Date(a) - new Date(b));
            var Recon=false;
            VersionName.forEach(Version=>{
                InputStreams.forEach(InputStream=>{
                    var Iterations=[]
                    Types.forEach(Type=>{
                        if(Type.includes(InputStream))
                            Iterations=[...Iterations,Type]
                    })
                    var ReconListQuery="Select([Version].[Version Name].["+Version+"]*[Data Stream].[Data Stream Type].["+InputStream+"] ) on row,({Measure.[Forecast Reconciliation]}) on column;"        
                    var ReconListCellSet=cellsetModule.createCellSet(queryModule.select(ReconListQuery))
                    var ReconList=ReconListCellSet.row(0).cell(ReconListCellSet.measureColumnIndex('Forecast Reconciliation'))
                    ReconList=ReconList.split(',')

                    if(ReconList.every(item => Iterations.includes(item)))
                        Recon=true;

                })

            })

            
            
            while(Cycles.length>0)
            {


                Log.Info("Completed Cycles: "+ CompletedCycles.toString())
                Log.Info("Remaining Cycles: "+ Cycles.toString())
                
                let CurrentCycle=Cycles.shift()
                Log.Info('Current Cycle :'+CurrentCycle)
                
                var UpdateTimeQuery='updatemember(Time.[Day]= {,"'+CurrentCycle+'" }, Time.[Day$IsCurrent]= {"true",});'
                    

                if(save){
                    queryModule.select('save();')
                    queryModule.select('disable plan;')
                    queryModule.update(UpdateTimeQuery)
                    queryModule.select('enable plan;')
                }
                else
                queryModule.update(UpdateTimeQuery)
               

                
                // var HistoryQuery='exec procedure '+BatchProcs['PopulateHistory']+';'
                // queryModule.select(HistoryQuery)

                // var DailyRefreshQuery='exec procedure '+BatchProcs['DailyRefresh']+';' 
                // queryModule.select(DailyRefreshQuery)

               
                
                Types.forEach(IterationType=>{

                            var Query='Select (&CWV * [Forecast Iteration].[Forecast Iteration Type].['+IterationType+']) on row,({Measure.[Forecast Generation Time Bucket]}) on column;'
                            var Cellset=cellsetModule.createCellSet(queryModule.select(Query))
                            var GenBucket=Cellset.row(0).cell(Cellset.measureColumnIndex('Forecast Generation Time Bucket'))


                            Stream=IterationType.split('_')[1]
                            Type=IterationType.split('_')[0]

                            if(Type.includes('Very Short'))
                            {
                                if(VSTCycles[Stream].includes(CurrentCycle))
                                {
                                    ExecuteProcesses(VersionName,IterationType,GenBucket,true)
                                }
                            }
                            else if(Type.includes('Very Long'))
                            {
                                if(VLTCycles[Stream].includes(CurrentCycle))
                                {
                                    ExecuteProcesses(VersionName,IterationType,GenBucket,true)
                                }
                            }
                            else if(Type.includes('Short'))
                            {
                                if(STCycles[Stream].includes(CurrentCycle))
                                {
                                    ExecuteProcesses(VersionName,IterationType,GenBucket,true)
                                }
                            }
                            else if(Type.includes('Mid'))
                            {
                                if(MTCycles[Stream].includes(CurrentCycle))
                                {
                                    ExecuteProcesses(VersionName,IterationType,GenBucket,true)
                                }
                                }
                            else if(Type.includes('Long'))
                                {
                                    if(LTCycles[Stream].includes(CurrentCycle))
                                    {
                                    ExecuteProcesses(VersionName,IterationType,GenBucket,true)
                                    }
                                }

                        })


                
                VersionName.forEach(Version=>{



                    


                    if(Recon)
                        {
                            
                    
                                var PlanningLevelAccQuery='EXEC plugin instance [DP213CalculateRollOverAccuracy] for measures {[Actual Cleansed]} using scope (&CWVAndScenarios.['+Version+']) using arguments {([IgnoreMissingOutputs], true), ([MaxSliceTableCells],2000000000),("IncludeNullRows", "False"), (DriverMemory, "5G"),("Lags","0,1"),(DataTransferMode,"csv"),([Param.o9_sys_log_level], "INFO"),(TimeLevel, "Week")};';
                                Types.forEach(Type=>{
                                PopulateStartTime(Version,Type,'Planning Accuracy Calculation')
                                })
                                queryModule.select(PlanningLevelAccQuery)

                   
                            Types.forEach(Type=>{
                                PopulateStartTime(Version,Type,'Forecast Reconciliation')
                            })
                           
                            reconQuery='exec procedure '+BatchProcs['Reconciliation'] +'{"VersionName" : "'+Version+'"};'
                            queryModule.select(reconQuery)
                            Types.forEach(Type=>{

                                let ExpectCount=cellsetModule.createCellSet(queryModule.select('Select (&CWVAndScenarios.['+Version+'] * [Account].[Planning Account] * [Channel].[Planning Channel] * [Demand Domain].[Planning Demand Domain] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Region].[Planning Region] * [Location].[Planning Location] ) on row,({Measure.[Actual]}) on column;'))
                                var scopeQuery='cartesian scope: (&CWVAndScenarios.['+Version+']* [Process].[Process].[Forecast Reconciliation] * [Forecast Iteration].[Forecast Iteration Type].['+Type+'].relatedmembers([Forecast Iteration])); Measure.[Stat Process Expected Output]='+ExpectCount.rowCount+'; end scope;'
                                queryModule.select(scopeQuery)
                                let ActualCount=cellsetModule.createCellSet(queryModule.select('Select (&CWVAndScenarios.['+Version+'] * [Account].[Planning Account] * [Channel].[Planning Channel] * [Demand Domain].[Planning Demand Domain] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Region].[Planning Region] * [Location].[Planning Location] ) on row,({Measure.[Stat Fcst]}) on column;'))
                                var scopeQuery='cartesian scope: (&CWVAndScenarios.['+Version+'] * [Process].[Process].[Forecast Reconciliation] * [Forecast Iteration].[Forecast Iteration Type].['+Type+'].relatedmembers([Forecast Iteration])); Measure.[Stat Process Actual Output]='+ActualCount.rowCount+'; end scope;'
                                queryModule.select(scopeQuery)
          
                                PopulateEndTime(Version,Type,'Forecast Reconciliation')
                           
                  
                           
                            })


                            var CalculatePLLagModel='EXEC plugin instance [DP221CalculateLagModel] for measures {[Actual Cleansed]} using scope (&CWVAndScenarios.['+Version+']) using arguments {([IgnoreMissingOutputs], true), ([MaxSliceTableCells],2000000000),("IncludeNullRows", "False"), (DriverMemory, "5G"),([Param.o9_sys_log_level], "INFO")};'
                            queryModule.select(CalculatePLLagModel)

                           
                        }
                    
        
                
                })
                

                CompletedCycles.push(CurrentCycle)
            }


        }

    }

return{
        RunBatch:RunBatch
    }
})

