    var DisaggregateStat  = function(o9Params){
    var parsedParams = JSON.parse(o9Params);
    
    var VersionName = parsedParams.VersionName
    var ForecastIteration=parsedParams.ForecastIteration
    //Log method logs to the webapi log

    var Item = itemize(parsedParams.Item)
    var Account = itemize(parsedParams.Account)
    var Channel = itemize(parsedParams.Channel)
    var Region = itemize(parsedParams.Region)
    var Location = itemize(parsedParams.Location)
