    var DemandDomain = itemize(parsedParams.DemandDomain)

    var isBottomUp=parsedParams.IsBottomUp
    var Type=parsedParams.Type
    var MW = parsedParams.MediumWeight
    
    var LevelQuery='Select ([Version].[Version Name].['+VersionName+'] * [Forecast Iteration].[Forecast Iteration].['+ForecastIteration+']) on row, ({Measure.[Item Level], Measure.[Account Level], Measure.[Channel Level], Measure.[PnL Level], Measure.[Region Level], Measure.[Demand Domain Level], Measure.[Location Level]}) on column;'

    var ItemLevel=ExtractMeasure(LevelQuery,'Item Level');
