    var RegionLevel=ExtractMeasure(LevelQuery,'Region Level');
    var LocationLevel=ExtractMeasure(LevelQuery,'Location Level');
    var PnLLevel=ExtractMeasure(LevelQuery,'PnL Level');
    var DemandDomainLevel=ExtractMeasure(LevelQuery,'Demand Domain Level');
   
   
   isBottomUp = (isBottomUp === 'true');
  
    if(isBottomUp)
    {
        Log.Info('ISBOTOMUP:'+isBottomUp)
   
        Query='Select ([Version].[Version Name].['+VersionName+'] * [Forecast Iteration].[Forecast Iteration].[FI-PL] * [Region].['+RegionLevel+'].filter(#.Name in {'+Region.toString()+'}).relatedmembers([Planning Region]) * [Location].['+LocationLevel+'].filter(#.Name in {'+Location.toString()+'}).relatedmembers([Planning Location]) * [Channel].['+ChannelLevel+'].filter(#.Name in {'+Channel.toString()+'}).relatedmembers([Planning Channel]) * [PnL].['+PnLLevel+'].filter(#.Name in {'+PnL.toString()+'}).relatedmembers([Planning PnL]) * [Item].['+ItemLevel+'].filter(#.Name in {'+Item.toString()+'}).relatedmembers([Planning Item])* [Demand Domain].['+DemandDomainLevel+'].filter(#.Name in {'+DemandDomain.toString()+'}).relatedmembers([Planning Demand Domain]) * [Account].['+AccountLevel+'].filter(#.Name in {'+Account.toString()+'}).relatedmembers([Planning Account]) ) on row, ({Measure.[Stat Actual L0]}) on column;'
        QueryCellset=cellsetModule.createCellSet(queryModule.select(Query))
        if(QueryCellset.rowCount==0)
        {
            RuleOutputToUI = {Status: 'Error',Message:`Stat Fcst L1 for PL does not exist`};
            return
        }
        
        var PlanningItemList=GetMembers(Query,'Item')
        
        var PlanningAccountList=GetMembers(Query,'Account')
        
        var PlanningChannelList=GetMembers(Query,'Channel')
        
        var PlanningRegionList=GetMembers(Query,'Region')
        
        var PlanningLocationList=GetMembers(Query,'Location')
        
        var PlanningPnLList=GetMembers(Query,'PnL')
        
        var PlanningDemandDomainList=GetMembers(Query,'Demand Domain')

        Item.push(...PlanningItemList);
        Account.push(...PlanningAccountList);
        Channel.push(...PlanningChannelList);
        Region.push(...PlanningRegionList);
