    } 

    DisagQuery='exec procedure o9DP0158DisaggregateStat {"VersionName" : ["'+VersionName+'"], "Type" : "'+Type+'","ForecastIteration": "'+ForecastIteration+'","MediumWeight" : '+MW+',"BottomUp":'+(isBottomUp ? "true" : "false") +',"Item":['+Item.toString()+'],"ItemLevel":"'+ItemLevel+'","Account":['+Account.toString()+'],"AccountLevel":"'+AccountLevel+'","Channel":['+Channel.toString()+'],"ChannelLevel":"'+ChannelLevel+'","Region":['+Region.toString()+'],"RegionLevel":"'+RegionLevel+'","Location":['+Location.toString()+'],"LocationLevel":"'+LocationLevel+'","PnL":['+PnL.toString()+'],"PnLLevel":"'+PnLLevel+'","DemandDomain":['+DemandDomain.toString()+'],"DemandDomainLevel":"'+DemandDomainLevel+'"};'

    queryModule.select(DisagQuery)




};
    return {
        DisaggregateStat : DisaggregateStat
    };
});

