define('o9.DemandPlanning.StatProcessSummary',['o9/data/query', 'o9/data/cellset','o9.web.api'], function(){


    var itemize=function(item){
        var itemdata=''+item+''
        itemdata=itemdata.split(",").map(i=>"\""+i+"\"")
        return itemdata
    }
    var itemsplit=function(item){
        var itemdata=''+item+''
        itemdata=itemdata.split(",")
        return itemdata
    }
    var ExtractMeasure=function(Query,Measure='RecordCount'){
        var cellsetModule = require('o9/data/cellset');
        var queryModule=require('o9/data/query')
        var QueryCellset=cellsetModule.createCellSet(queryModule.select(Query))
        
        if (QueryCellset.rowCount==0)
            return 0 
        var value =QueryCellset.row(0).cell(QueryCellset.measureColumnIndex(Measure))   
        Log.Info("Value be : "+value)
        return value
        
    }


    var PopulateMeasures=function(type,process,Iteration,value,Version)
        {
                
            var queryModule=require('o9/data/query')
            Log.Info(process+" "+type+" Count: "+value)
            if(type=="expect")
                {
                    var scopeQuery='cartesian scope: ([Version].[Version Name].['+Version+'] * [Process].[Process].['+process+'] * [Forecast Iteration].[Forecast Iteration].['+Iteration+']); Measure.[Stat Process Expected Output]='+value+'; end scope;'
                    queryModule.select(scopeQuery)
                }
            else if(type=="actual")
                {
                    var scopeQuery='cartesian scope: ([Version].[Version Name].['+Version+'] * [Process].[Process].['+process+'] * [Forecast Iteration].[Forecast Iteration].['+Iteration+']); Measure.[Stat Process Actual Output]='+value+'; end scope;'
                    queryModule.select(scopeQuery)
                }

        }

    


        
    
    var Summary=function(o9Params){
        var cellsetModule = require('o9/data/cellset');
        var queryModule=require('o9/data/query')
        var parsedParams = JSON.parse(o9Params);
        var FcstIteration=parsedParams.Iteration
        var processesList=itemsplit(parsedParams.Processes)
        var Version=parsedParams.VersionName

        FcstIteration.forEach(Iteration => {
                var Dims=['Item','Account','Channel','Region','Location','PnL','Demand Domain']
            var Levels={}
            Dims.forEach(element=>{
            var query='Select([Forecast Iteration].[Forecast Iteration].['+Iteration+'] * [Version].[Version Name].['+Version+'] * ['+element+'].[Planning '+element+']) on row,({Measure.['+element+' Level]}) on column;'
            Levels[element]=ExtractMeasure(query,element+' Level')
            })  

            var noStatQuery='Select ([Version].[Version Name].['+Version+'] * [Region].[Stat Region] * [Item].[Stat Item] * [PnL].[Stat PnL] * [Forecast Iteration].[Forecast Iteration].['+Iteration+'] * [Location].[Stat Location] * [Channel].[Stat Channel] * [Demand Domain].[Stat Demand Domain] * [Account].[Stat Account] ) on row, ({Measure.[Assigned Algorithm List]}) on column where {Measure.[Assigned Algorithm List] contains "No Forecast"};'
            var items={}
            var nostatCellset=cellsetModule.createCellSet(queryModule.select(noStatQuery))
            Dims.forEach(element=>{
                items[element]=[]   
                for(var j=0;j<nostatCellset.rowCount;j++)
                    {
                        items[element].push(nostatCellset.row(j).cell(nostatCellset.memberColumnIndex(element,'Stat '+element)).Name)
                    }
                items[element]=itemize(items[element])
            })

            var nostatPLQuery='(Select([Version].[Version Name].['+Version+']* [Forecast Iteration].[Forecast Iteration].['+Iteration+']* [Region].['+Levels['Region']+'].filter(#.Name in {'+items['Region']+'}).relatedmembers([Planning Region])*[Item].['+Levels['Item']+'].filter(#.Name in {'+items['Item']+'}).relatedmembers([Planning Item])*[Account].['+Levels['Account']+'].filter(#.Name in {'+items['Account']+'}).relatedmembers([Planning Account])*[Channel].['+Levels['Channel']+'].filter(#.Name in {'+items['Channel']+'}).relatedmembers([Planning Channel])*[Location].['+Levels['Location']+'].filter(#.Name in {'+items['Location']+'}).relatedmembers([Planning Location])*[PnL].['+Levels['PnL']+'].filter(#.Name in {'+items['PnL']+'}).relatedmembers([Planning PnL])*[Demand Domain].['+Levels['Demand Domain']+'].filter(#.Name in {'+items['Demand Domain']+'}).relatedmembers([Planning Demand Domain])) on row, ({Measure.[Stat Actual L0],Measure.[Stat Fcst PL]}) on column).filter(isnull(Measure.[Stat Fcst PL]));'
            var nostatPLCellSet=cellsetModule.createCellSet(queryModule.select(nostatPLQuery))
            var nostatPLcols=nostatPLCellSet.rowCount
            var nostatTLQuery='(Select([Version].[Version Name].['+Version+']* [Forecast Iteration].[Forecast Iteration].['+Iteration+']* [Region].['+Levels['Region']+'].filter(#.Name in {'+items['Region']+'}).relatedmembers([Planning Region])*[Item].['+Levels['Item']+'].filter(#.Name in {'+items['Item']+'}).relatedmembers([Transition Item])*[Account].['+Levels['Account']+'].filter(#.Name in {'+items['Account']+'}).relatedmembers([Planning Account])*[Channel].['+Levels['Channel']+'].filter(#.Name in {'+items['Channel']+'}).relatedmembers([Planning Channel])*[Location].['+Levels['Location']+'].filter(#.Name in {'+items['Location']+'}).relatedmembers([Planning Location])*[PnL].['+Levels['PnL']+'].filter(#.Name in {'+items['PnL']+'}).relatedmembers([Planning PnL])*[Demand Domain].['+Levels['Demand Domain']+'].filter(#.Name in {'+items['Demand Domain']+'}).relatedmembers([Transition Demand Domain])) on row, ({Measure.[Stat Actual L0],Measure.[Stat Fcst TL]}) on column).filter(isnull(Measure.[Stat Fcst TL]));'
            var nostatTLCellSet=cellsetModule.createCellSet(queryModule.select(nostatTLQuery))
            var nostatTLcols=nostatTLCellSet.rowCount
            
            Log.Info('NoStat PL: '+nostatPLcols+' NoStat TL: '+nostatTLcols)





            var IterationsQuery='Select([Forecast Iteration].[Forecast Iteration].['+Iteration+'].relatedmembers([Forecast Iteration Type]));'
            var IterationCellset=cellsetModule.createCellSet(queryModule.select(IterationsQuery))
            var IterationType=IterationCellset.row(0).cell(IterationCellset.memberColumnIndex('Forecast Iteration','Forecast Iteration Type')).Name;
            var DisagType=ExtractMeasure('Select([Version].[Version Name].['+Version+']*[Forecast Iteration].[Forecast Iteration].['+Iteration+']) on row, ({Measure.[Disaggregation Type]}) on column;','Disaggregation Type')
        
            var HistoryQuery='Select([Version].[Version Name].['+Version+']*[Forecast Iteration].[Forecast Iteration].['+Iteration+']) on row, ({Measure.[History Period], Measure.[Forecast Generation Time Bucket],Measure.[Forecast Period]}) on column;'
            var HistroyPeriod=ExtractMeasure(HistoryQuery,'History Period')
            var HistoryBucket=ExtractMeasure(HistoryQuery,'Forecast Generation Time Bucket')
            var ForecastPeriod=ExtractMeasure(HistoryQuery,'Forecast Period')
            var ForecastHorizon='[Time].[Week].filter(#.Key>=&CurrentWeek.element(0).Key && #.Key<&CurrentWeek.element(0).leadoffset(-'+ForecastPeriod+').Key)'
            var HistoryNamedset='&Last36PlanningMonths'
            var FutureNamedSet='&CurrentandFuturePartialWeeks'
            if(HistoryBucket=="Week")
                {
                    HistoryNamedset='[Time].[Week].filter(#.Key<&CurrentWeek.element(0).Key && #.Key>=&CurrentWeek.element(0).leadoffset(-'+HistroyPeriod+').Key)'
                }
            if(HistoryBucket=="Month")
                {
                    HistoryNamedset='[Time].[Month].filter(#.Key<&CurrentMonth.element(0).Key && #.Key>=&CurrentMonth.element(0).leadoffset(-'+HistroyPeriod+').Key)'
                }
            if(HistoryBucket=="Planning Month")
                {
                    HistoryNamedset='[Time].[Planning Month].filter(#.Key<&CurrentPlanningMonth.element(0).Key && #.Key>=&CurrentPlanningMonth.element(0).leadoffset(-'+HistroyPeriod+').Key)'
                }
            if(HistoryBucket=="Quarter")
                {
                    HistoryNamedset='[Time].[Quarter].filter(#.Key<&CurrentQuarter.element(0).Key && #.Key>=&CurrentQuarter.element(0).leadoffset(-'+HistroyPeriod+').Key)'
                }
            if(HistoryBucket=="Planning Quarter")
                {
                    HistoryNamedset='[Time].[Planning Quarter].filter(#.Key<&CurrentPlanningQuarter.element(0).Key && #.Key>=&CurrentPlanningQuarter.element(0).leadoffset(-'+HistroyPeriod+').Key)'
                }
                
            if(HistoryBucket=="Week")
                {
                    FutureNamedSet='[Time].[Week].filter(#.Key>=&CurrentWeek.element(0).Key )'
                }
            if(HistoryBucket=="Month")
                {
                    FutureNamedSet='[Time].[Month].filter(#.Key>=&CurrentMonth.element(0).Key)'
                }
            if(HistoryBucket=="Planning Month")
                {
                    FutureNamedSet='[Time].[Planning Month].filter(#.Key>=&CurrentPlanningMonth.element(0).Key)'
                }
            if(HistoryBucket=="Quarter")
                {
                    FutureNamedSet='[Time].[Quarter].filter(#.Key>=&CurrentQuarter.element(0).Key)'
                }
            if(HistoryBucket=="Planning Quarter")
                {
                    FutureNamedSet='[Time].[Planning Quarter].filter(#.Key>=&CurrentPlanningQuarter.element(0).Key)'
                }
            
            
            var Grains ='[Region].[Stat Region] * [Item].[Stat Item] * [PnL].[Stat PnL] * [Location].[Stat Location] * [Demand Domain].[Stat Demand Domain] * [Account].[Stat Account] * [Channel].[Stat Channel]'
            var IType='Very Short Term'
            if (IterationType.includes("Very Short Term")) {
                IType = "Very Short Term";
            } else if( IterationType.includes("Short Term")) {
                IType = "Short Term";
            }else if(IterationType.includes("Very Long Term")) {
                IType= "Very Long Term";
            } else if (IterationType.includes("Mid Term")) {
                IType = "Mid Term";
            } else if (IterationType.includes("Long Term")) {
                IType = "Long Term";
            }




            var BestfitNamedSet='[Item].[Segmentation LOB].filter(#.[Bestfit Flag '+IType+'] == 1)'
            var BestfitLCNamedSet='[Item].[Segmentation LOB].filter(#.[Bestfit Flag '+IType+'] == 0)'
            var NoLCBestfitNamedset='[Item].[Segmentation LOB].filter(isNull(#.[Bestfit Flag '+IType+']))'
            var NoLCBestfitNamedsetquery='Select('+NoLCBestfitNamedset+');'
            var NoLCBestfitCellset=cellsetModule.createCellSet(queryModule.select(NoLCBestfitNamedsetquery))
            var CMLIntersections='Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[System Bestfit Algorithm]=="CML"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
            
            const Process= {
                "Generate Stat Actuals":{query:'Select ([Version].[Version Name].['+Version+'] * [Forecast Iteration].[Forecast Iteration].['+Iteration+'] ) on row, ({Measure.[Stat Actual]}) on column;',key:1},
                "Stat Actuals":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[Stat Actual]>0}) on row, () on column where {[Version].[Version Name].['+Version+'], '+HistoryNamedset+',[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',key:0},
                "Generate Slice Association":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Slice Association Stat])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',key:2},
                "Generate Segmentation":{query: 'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[PLC Status L1])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',key:3},
                "Seasonality Detection":{query: 'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Seasonality L1])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',key:4},
                "Generate Outlier Cleansing":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Actual Cleansed])}) on row, () on column where {[Version].[Version Name].['+Version+'], '+HistoryNamedset+',[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',key:5},
                "Generate Assigned Rules":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Assigned Algorithm List])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',key:6},
                "Generate System Stat":{
                        expect:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Actual])}) on row, () on column where {[Version].[Version Name].['+Version+'], '+HistoryNamedset+',[Forecast Iteration].[Forecast Iteration].['+Iteration+'],'+BestfitNamedSet+'};',
                        key:7,
                        disco:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[PLC Status L1]==\"DISC\"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+'],'+BestfitNamedSet+'};',
                        nostat:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[Assigned Algorithm List] contains \"No Forecast\"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+'],'+BestfitNamedSet+'};',
                        query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Run Time])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+'],'+BestfitNamedSet+'};',

                    
                    },
                "Generate System Stat using Bestfit LC":{
                        expect:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Actual])}) on row, () on column where {[Version].[Version Name].['+Version+'], '+HistoryNamedset+',[Forecast Iteration].[Forecast Iteration].['+Iteration+'],'+BestfitLCNamedSet+'};',
                        key:8,
                        disco:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[PLC Status L1]==\"DISC\"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+'],'+BestfitLCNamedSet+'};',
                        nostat:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[Assigned Algorithm List] contains \"No Forecast\"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+'],'+BestfitLCNamedSet+'};',
                        query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Run Time])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+'],'+BestfitLCNamedSet+'};'   
                        },
                "Identify Bestfit Algorithm":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[System Bestfit Algorithm])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',key:9},
                "Populate Stat Fcst":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[System Stat Fcst L1])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',key:10},
                "Populate Analysis Cockpit":{query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Run Count])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',key:11},
                "PL Count Validation":{
                    query:'Select ( {DistinctCount([Region].[Planning Region] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Location].[Planning Location] * [Channel].[Planning Channel] * [Demand Domain].[Planning Demand Domain] * [Account].[Planning Account]) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst PL])} ) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']}; ',
                    key:12,
                    expect:'Select ( {DistinctCount([Region].[Planning Region] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Location].[Planning Location] * [Channel].[Planning Channel] * [Demand Domain].[Planning Demand Domain] * [Account].[Planning Account]) as Transient.RecordCount using ~isnull(Measure.[Stat Actual L0])&& Measure.[Forecast Iteration Selection]=="'+Iteration+'"} ) on row, () on column where {&CWV,[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
                },
                "PL Volume Loss Validation":{
                    key:14,
                },
                "TL Count Validation":{
                    key:13,
                    query:'Select({DistinctCount( [Region].[Planning Region] * [Item].[Transition Item] * [PnL].[Planning PnL] *[Location].[Planning Location] * [Channel].[Planning Channel] * [Demand Domain].[Transition Demand Domain] * [Account].[Planning Account]) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst TL])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                    expect:'Select({DistinctCount( [Region].[Planning Region] * [Item].[Transition Item] * [PnL].[Planning PnL] *[Location].[Planning Location] * [Channel].[Planning Channel] * [Demand Domain].[Transition Demand Domain] * [Account].[Planning Account]) as Transient.RecordCount using ~isnull(Measure.[Stat Actual L0])&& Measure.[Forecast Iteration Selection]=="'+Iteration+'"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
                },
                "TL Volume Loss Validation":{
                    key:15
                },
                "Generate CML Fcst":{

                    key:16,
                    query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst CML])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',	
                    expect:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Actual])}) on row, () on column where {[Version].[Version Name].['+Version+'],'+HistoryNamedset+',[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',	
                    disco:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[PLC Status L1]==\"DISC\"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                    nostat:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[Assigned Algorithm List] contains \"No Forecast\"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                   
                },
                "Generate Ensemble Fcst":{
                    key:17,
                    query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst Ensemble])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',	
                    expect:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Actual])}) on row, () on column where {[Version].[Version Name].['+Version+'],'+HistoryNamedset+',[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',	
                	
                    disco:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[PLC Status L1]==\"DISC\"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                    nostat:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using Measure.[Assigned Algorithm List] contains \"No Forecast\"}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                   
                },
                "Disaggregate Stat":{
                    key:18

                },
                "Forecast Reconciliation":{
                    expect:'Select ({DistinctCount([Region].[Planning Region] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Location].[Planning Location] * [Demand Domain].[Planning Demand Domain] * [Account].[Planning Account] * [Channel].[Planning Channel] ) as Transient.RecordCount using ~isnull(Measure.[Actual])}) on row, () on column where {[Version].[Version Name].['+Version+'],'+HistoryNamedset+'};',
                    query:'Select ({DistinctCount([Region].[Planning Region] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Location].[Planning Location] * [Demand Domain].[Planning Demand Domain] * [Account].[Planning Account] * [Channel].[Planning Channel] ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst])}) on row, () on column where {[Version].[Version Name].['+Version+'],'+HistoryNamedset+'};',
                    key:19
                },
                "Stat Accuracy Calculation":{
                    expect:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst L1 Lag])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                    query:'Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst L1 Lag Abs Error])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                    key:20
                },
                "Planning Accuracy Calculation":{
                    expect:'Select ({DistinctCount([Region].[Planning Region] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Location].[Planning Location] * [Demand Domain].[Planning Demand Domain] * [Account].[Planning Account] * [Channel].[Planning Channel] ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst PL Lag])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                    query:'Select ({DistinctCount([Region].[Planning Region] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Location].[Planning Location] * [Demand Domain].[Planning Demand Domain] * [Account].[Planning Account] * [Channel].[Planning Channel] ) as Transient.RecordCount using ~isnull(Measure.[Stat Fcst PL Lag Abs Error])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};',
                    key:21
                }



            
            }
        





            
        
            
            var DisagIntersections=0
            var VolumeActual=0
            var VolumeExpect=0
            var TLVolumeActual=0

            if(DisagType=="No Forecast")
                {
                DisagIntersections=DisagIntersections+ExtractMeasure('Select ( {DistinctCount([Region].[Planning Region] * [Item].[Planning Item] * [PnL].[Planning PnL] * [Location].[Planning Location] * [Channel].[Planning Channel] * [Demand Domain].[Planning Demand Domain] * [Account].[Planning Account]) as Transient.RecordCount using ~isnull(Measure.[Stat Actual L0])&&Measure.[Forecast Iteration Selection]=="'+Iteration+'"} ) on row, () on column where {&CWV,[Forecast Iteration].[Forecast Iteration Type].['+IterationType+']};')
                
                }
                else
                {
                    VolumeActual=ExtractMeasure('Select([Version].[Version Name].['+Version+']*[Forecast Iteration].[Forecast Iteration].['+Iteration+']) on row, ({Measure.[Stat Fcst PL]}) on column;','Stat Fcst PL')
                    VolumeExpect=ExtractMeasure('Select([Version].[Version Name].['+Version+']*[Forecast Iteration].[Forecast Iteration].['+Iteration+']) on row, ({Measure.[Stat Fcst L1]}) on column where {'+FutureNamedSet+'};','Stat Fcst L1')
                    TLVolumeActual=ExtractMeasure('Select([Version].[Version Name].['+Version+']*[Forecast Iteration].[Forecast Iteration].['+Iteration+']) on row, ({Measure.[Stat Fcst TL]}) on column where {'+FutureNamedSet+'};','Stat Fcst TL')
                }  
        

                

            var statIntersectionCount=0
            
            processesList.forEach(element => {
               
                if(Process[element].key==1)
                {
                    PopulateMeasures('actual',element,Iteration,ExtractMeasure(Process[element].query,'Stat Actual'),Version)
                    var ExpectQuery='Select ([Version].[Version Name].['+Version+']  ) on row,({Measure.[Stat Actual L0],Measure.[Forecast Iteration Selection]}) on column where {Measure.[Forecast Iteration Selection]=="'+Iteration+'"};'
                    PopulateMeasures('expect',element,Iteration,ExtractMeasure(ExpectQuery,'Stat Actual L0'),Version)
                    return 
                    }
                if(Process[element].key==14)
                    {
                        PopulateMeasures('actual',element,Iteration,VolumeActual,Version)
                        PopulateMeasures('expect',element,Iteration,VolumeExpect,Version)
                        return 
                    }
                if(Process[element].key==15)
                    {
                        PopulateMeasures('actual',element,Iteration,TLVolumeActual,Version)
                        PopulateMeasures('expect',element,Iteration,VolumeExpect,Version)
                        return 
                    }
                var actualQuery = Process[element].query
                var ExpectQuery=Process['Stat Actuals'].query
                if(Process[element].key==2)
                    ExpectQuery='Select ({DistinctCount('+Grains+' ) as Transient.RecordCount using ~isnull(Measure.[Stat Actual])}) on row, () on column where {[Version].[Version Name].['+Version+'],[Forecast Iteration].[Forecast Iteration].['+Iteration+']};'
                if(Process[element].key<18){
                var ActualValue=ExtractMeasure(actualQuery,'RecordCount')
                var ExpectValue=ExtractMeasure(ExpectQuery,'RecordCount')
            }
                ActualValue=(!ActualValue)?0:ActualValue
                ExpectValue=(!ExpectValue)?-1:ExpectValue 
                    
                
                if(Process[element].key>6)
                    {   
                        var CMLCount=ExtractMeasure(CMLIntersections)

                        if(Process[element].key==7||Process[element].key==8||Process[element].key==16||Process[element].key==17)
                            {
                           
                            ExpectQuery=Process[element].expect   
                            var CMLCount=ExtractMeasure(CMLIntersections)
                            ExpectValue=ExtractMeasure(ExpectQuery,'RecordCount')
                            var discoQuery= Process[element].disco
                            var DiscoValue=ExtractMeasure(discoQuery,'RecordCount')
                            var BestfitQuery= (Process[element].key==7)?'Select('+BestfitNamedSet+');':'Select('+BestfitLCNamedSet+');';
                            var noStatIntersections=ExtractMeasure(Process[element].nostat)
                            var BestfitCellset=cellsetModule.createCellSet(queryModule.select(BestfitQuery))


                            if(Process[element].key==16||Process[element].key==17)
                                
                            {
                                var ForecastEngineQuery='Select ([Forecast Iteration].[Forecast Iteration].['+Iteration+'] * [Version].[Version Name].['+Version+'] ) on row, ({Measure.[Forecast Engine],Measure.[Forecast Strategy],Measure.[Assigned Algorithm List]}) on column;'
                                var ForecastEngineCellset=cellsetModule.createCellSet(queryModule.select(ForecastEngineQuery))
                                var ForecastEngine = ForecastEngineCellset.row(0).cell(ForecastEngineCellset.measureColumnIndex('Forecast Engine'))
                                var ForecastStrategy = ForecastEngineCellset.row(0).cell(ForecastEngineCellset.measureColumnIndex('Forecast Strategy'))
                                Log.Info('Expect Qeury: '+Process[element].expect)
                                ExpectQuery=Process[element].expect
                                ExpectValue=0
                                if(Process[element].key==16 && ForecastEngine.toString().includes('ML'))
                                ExpectValue=ExtractMeasure(ExpectQuery,'RecordCount')
                                if(Process[element].key==17 && ForecastStrategy.toString().includes('Ensemble'))
                                ExpectValue=ExtractMeasure(ExpectQuery,'RecordCount')
                                Log.Info('Forecast Engine: '+ForecastEngine+' Forecast Strategy: '+ForecastStrategy)
                                Log.Info('Actual Query: '+Process[element].query)
                                ActualQuery=Process[element].query
                                ActualValue=ExtractMeasure(ActualQuery,'RecordCount')
                                if(ActualValue!=ExpectValue)
                                    if(ActualValue+DiscoValue==ExpectValue)
                                        ExpectValue=ActualValue   

                                

                               
                                PopulateMeasures('actual',element,Iteration,ActualValue,Version)
                                PopulateMeasures('expect',element,Iteration,ExpectValue,Version)
                                return

                            }
                            if(NoLCBestfitCellset.rowCount>0)
                                {
                                    ExpectQuery='Select([Region].[Stat Region] * '+NoLCBestfitNamedset+'.relatedmembers([Stat Item]) * [PnL].[Stat PnL] * [Location].[Stat Location] * [Demand Domain].[Stat Demand Domain] * [Account].[Stat Account] * [Channel].[Stat Channel]*[Version].[Version Name].['+Version+']*[Forecast Iteration].[Forecast Iteration].['+Iteration+']) on row,({Measure.[PLC Status L1]}) on column;'
                                    ExpectQueryCellSet=cellsetModule.createCellSet(queryModule.select(ExpectQuery))
                                    ExpectValue=ExpectValue+ExpectQueryCellSet.rowCount
                                    actualQuery='Select ([Version].[Version Name].['+Version+'] * [Forecast Iteration].[Forecast Iteration].['+Iteration+'] * '+NoLCBestfitNamedset+'.relatedmembers([Stat Item]) * [Account].[Stat Account] * [Channel].[Stat Channel] * [Region].[Stat Region] * [PnL].[Stat PnL] * [Location].[Stat Location] * [Demand Domain].[Stat Demand Domain]  ) on row, ({Measure.[Run Time]}) on column;'
                                    ActualQueryCellSet=cellsetModule.createCellSet(queryModule.select(actualQuery))

                                    ActualValue=ActualValue+ActualQueryCellSet.rowCount+CMLCount
                                }
                            if(Process[element].key==8&&NoLCBestfitCellset.rowCount>0)
                                return
                            if(BestfitCellset.rowCount===0&&NoLCBestfitCellset.rowCount==0)
                                {
                                    ActualValue=0
                                    ExpectValue=0
                                }
                            
                            else
                                {
                                    ExpectValue=(!ExpectValue)?-1:ExpectValue-noStatIntersections
                                    DiscoValue=(!DiscoValue)?0:DiscoValue
                                    Log.Info('Test '+element +' Actual: '+ActualValue+' Expect: '+ExpectValue + ' No Stat: '+noStatIntersections)
                                    if(ActualValue!=ExpectValue)
                                        if(ActualValue+DiscoValue==ExpectValue)
                                            ExpectValue=ActualValue   
                                }
                                statIntersectionCount = (ExpectValue=== -1) ? -1 : statIntersectionCount + ExpectValue;  
                            
                            
                            
                            }
                    
                            if(Process[element].key==9||Process[element].key==10||Process[element].key==11)
                                ExpectValue=statIntersectionCount

                            if(Process[element].key==12)
                                {
                                    ExpectValue=ExtractMeasure(Process[element].expect)
                                    if(ActualValue+DisagIntersections+nostatPLcols==ExpectValue)
                                    ExpectValue=ActualValue
                                    else
                                    ExpectValue=ExpectValue-nostatPLcols
                                }
                            if(Process[element].key==13)
                                {
                                    ExpectValue=ExtractMeasure(Process[element].expect)
                                    if(ActualValue+DisagIntersections+nostatTLcols==ExpectValue)
                                        ExpectValue=ActualValue
                                    else
                                    ExpectValue=ExpectValue-nostatTLcols
                                }

                            if(Process[element].key==18)
                            {
                                ActualValue=ExtractMeasure(Process['TL Count Validation'].query)
                                ExpectValue=ExtractMeasure(Process['PL Count Validation'].expect)
                                if(ActualValue+DisagIntersections==ExpectValue)
                                    ExpectValue=ActualValue
                                else
                                    ExpectValue=ExpectValue-DisagIntersections
                            }

                                            
                        }
                    if(Process[element].key==20||Process[element].key==21||Process[element].key==19)
                        {
                            ActualValue=ExtractMeasure(Process[element].query,'RecordCount')
                            ExpectValue=ExtractMeasure(Process[element].expect,'RecordCount')
                            ActualValue=(!ActualValue)?0:ActualValue
                            ExpectValue=(!ExpectValue)?0:ExpectValue
                            
                              
                        }
                    

                PopulateMeasures('actual',element,Iteration,ActualValue,Version)
                PopulateMeasures('expect',element,Iteration,ExpectValue,Version)
               var FlagQ='Scope: (&CWVAndScenarios.['+Version+'] * [Process].[Process].['+element+'] * [Forecast Iteration].[Forecast Iteration].['+Iteration+'] ) ; Measure.[Stat Process Flag]=if(~isnull(Measure.[Stat Process Expected Output])) then 1;end scope;'
                queryModule.select(FlagQ)
    
                //Populate Process Status
               var ProcessQ='Scope: (&CWVAndScenarios.['+Version+'] * [Process].[Process].['+element+'] * [Forecast Iteration].[Forecast Iteration].['+Iteration+'] ) ; Measure.[Stat Process Status]=if(Measure.[Actual vs Expected Stat Process Output Gap %]<0.05 && Measure.[Actual vs Expected Stat Process Output Gap %]>=0) then 1; end scope;'
                queryModule.select(ProcessQ)
            });

            //Populate Process Flag
          
        });
        
    
    
    
    }
return {
Summary:Summary
}
