                queryModule.select(FlagQ)
    
                //Populate Process Status
               var ProcessQ='Scope: (&CWVAndScenarios.['+Version+'] * [Process].[Process].['+element+'] * [Forecast Iteration].[Forecast Iteration].['+Iteration+'] ) ; Measure.[Stat Process Status]=if(Measure.[Actual vs Expected Stat Process Output Gap %]<0.05 && Measure.[Actual vs Expected Stat Process Output Gap %]>=0) then 1; end scope;'
                queryModule.select(ProcessQ)
            });

            //Populate Process Flag
          
        });
        
    
    
    
    }
return {
Summary:Summary
}
});

