define('o9.DemandPlanning.ValidateRealignmentLevels', ['o9/data/query', 'o9/data/cellset', 'o9.web.api'], function () {

    // Validation function accepting o9Params as input
    var validation = function (o9Params) {

        // Parsing input parameters
        var parsedParams = JSON.parse(o9Params);

        // Retrieving required modules
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        
        // Extract input parameters
        var Version = parsedParams.Version;
		var DataObject = parsedParams.DataObject;
        var ItemLevel = parsedParams.Itemlevel;
		var AccountLevel = parsedParams.Accountlevel;
		var ChannelLevel = parsedParams.Channellevel;
		var RegionLevel = parsedParams.Regionlevel;
		var LocationLevel = parsedParams.Locationlevel;
		var PnLLevel = parsedParams.PnLlevel;
		var DemandDomainLevel = parsedParams.DemandDomainlevel;
		var HistoryRealignment = parsedParams.HistoryRealignment;
		var ForecastRealignment = parsedParams.ForecastRealignment;
		var RuleInitializationQuery = " ";
		var leaf_Count=0;
		var planning_Count=0;
        
        // Logging extracted parameters for validation
        Log.Info("Version: " + Version);
        Log.Info("Realignment Type: " + DataObject);
        Log.Info("Item Level: " + ItemLevel);
		Log.Info("Account Level: " + AccountLevel);
		Log.Info("Channel Level: " + ChannelLevel);
		Log.Info("Region Level: " + RegionLevel);
		Log.Info("Location Level: " + LocationLevel);
		Log.Info("PnL Level: " + PnLLevel);
		Log.Info("Demand Domain Level: " + DemandDomainLevel);
		Log.Info("Consider for Forecast Realignment: " + ForecastRealignment);
		Log.Info("Consider for History Realignment: " + HistoryRealignment);
		
		if (HistoryRealignment)
		{
			if(ItemLevel)
			{
				if(ItemLevel === "[Item].[Item]")
				{
					leaf_Count=leaf_Count+1;
				}
			}
			if(AccountLevel)
			{
				if(AccountLevel === "[Account].[Account]")
				{
					leaf_Count=leaf_Count+1;
				}
			}
			if(ChannelLevel)
			{
				if(ChannelLevel === "[Channel].[Channel]")
				{
					leaf_Count=leaf_Count+1;
				}
			}
			if(RegionLevel)
			{
				if(RegionLevel === "[Region].[Region]")
				{
					leaf_Count=leaf_Count+1;
				}
			}
			if(LocationLevel)
			{
				if(LocationLevel === "[Location].[Location]")
				{
					leaf_Count=leaf_Count+1;
				}
			}
			if(PnLLevel)
			{
				if(PnLLevel === "[PnL].[PnL]")
				{
					leaf_Count=leaf_Count+1;
				}
			}
			if(DemandDomainLevel)
			{
				if(DemandDomainLevel === "[Demand Domain].[Demand Domain]")
				{
					leaf_Count=leaf_Count+1;
				}
			}
			if (leaf_Count===0)
			{
				Log.Error("VALIDATION ERROR: To Enable History Realignment atleast one dimension should be selected at the Leaf Level.");
				RuleOutputToUI =
				{
					Status: 'Error',
					Message: 'VALIDATION ERROR: To Enable History Realignment atleast one dimension should be selected at the Leaf Level.'
				};
			}
		}
		
		if (ForecastRealignment)
		{
			if(ItemLevel)
			{
				if(ItemLevel === "[Item].[Planning Item]")
				{
					planning_Count=planning_Count+1;
				}
			}
			if(AccountLevel)
			{
				if(AccountLevel === "[Account].[Planning Account]")
				{
					planning_Count=planning_Count+1;
				}
			}
			if(ChannelLevel)
			{
				if(ChannelLevel === "[Channel].[Planning Channel]")
				{
					planning_Count=planning_Count+1;
				}
			}
			if(RegionLevel)
			{
				if(RegionLevel === "[Region].[Planning Region]")
				{
					planning_Count=planning_Count+1;
				}
			}
			if(LocationLevel)
			{
				if(LocationLevel === "[Location].[Planning Location]")
				{
					planning_Count=planning_Count+1;
				}
			}
			if(PnLLevel)
			{
				if(PnLLevel === "[PnL].[Planning PnL]")
				{
					planning_Count=planning_Count+1;
				}
			}
			if(DemandDomainLevel)
			{
				if(DemandDomainLevel === "[Demand Domain].[Planning Demand Domain]")
				{
					planning_Count=planning_Count+1;
				}
			}
			if (leaf_Count===0 && planning_Count===0)
			{
				Log.Error("VALIDATION ERROR: To Enable Forecast Realignment atleast one dimension should be selected at the Planning Level.");
				RuleOutputToUI =
				{
					Status: 'Error',
					Message: 'VALIDATION ERROR: To Enable Forecast Realignment atleast one dimension should be selected at the Planning Level.'
				};
			}
		}
		
		Log.Info("Validation Complete");
	};
	
	// Returning the validation function
    return {
        validation: validation
    };
});

