						for (var m=0; m< PlanningPnLCount ; m++){
						 
							for (var n=0; n< PlanningDemandDomainCount ; n++){
							 
								for (var o=0; o< LocationCount ; o++){
									
									var ValidAssortmentsQuery ='Select ([Version].[Version Name].['+Version+'] * [Item].[Item].['+Item[i]+'] * [Account].[Planning Account].['+PlanningAccount[j]+'] * [Channel].[Planning Channel].['+PlanningChannel[k]+'] * [Region].[Planning Region].['+PlanningRegion[l]+'] * [PnL].[Planning PnL].['+PlanningPnL[m]+'] *  [Demand Domain].[Planning Demand Domain].['+PlanningDemandDomain[n]+'] * [Location].[Location].['+Location[o]+']  ) on row, ({Measure.[Assortment Final]}) on column where {Measure.[Assortment Final]==1};';
									
									var SelectValidAssortmentsQuery = queryModule.select(ValidAssortmentsQuery);
									var ValidAssortmentsResponse = cellsetModule.createCellSet(SelectValidAssortmentsQuery);
									
									Log.Info("Assortment testing 1 "+ValidAssortmentsResponse.rowCount );
									var ItemQ;
