									if (ValidAssortmentsResponse.rowCount > 0 ){
									
										Log.Info("Assortment testing 2 ");
										
										if(Dimension==="Item"){ ItemQ=''+Attribute+'.['+Member+'].relatedmembers([Item]).Filter(#.[Is New Item] == FALSE)';}
