										if(Dimension==="Account"){ AccountQ=''+Attribute+'.['+Member+'].relatedmembers([Planning Account])';}
										else AccountQ='[Account].[Planning Account].['+PlanningAccount[j]+']';
										
										if(Dimension==="Channel"){ ChannelQ=''+Attribute+'.['+Member+'].relatedmembers([Planning Channel])';}
										else ChannelQ='[Channel].[Planning Channel].['+PlanningChannel[k]+']';
										
										if(Dimension==="Region"){ RegionQ=''+Attribute+'.['+Member+'].relatedmembers([Planning Region])';}
										else RegionQ='[Region].[Planning Region].['+PlanningRegion[l]+']';
										
										if(Dimension==="PnL"){ PnLQ=''+Attribute+'.['+Member+'].relatedmembers([Planning PnL])';}
										else PnLQ='[PnL].[Planning PnL].['+PlanningPnL[m]+']';
										
										if(Dimension==="Demand Domain"){ DemandDomainQ=''+Attribute+'.['+Member+'].relatedmembers([Planning Demand Domain])';}
										else DemandDomainQ='[Demand Domain].[Planning Demand Domain].['+PlanningDemandDomain[n]+']';
										
										if(Dimension==="Location"){ LocationQ=''+Attribute+'.['+Member+'].relatedmembers([Location])';}
										else LocationQ='[Location].[Location].['+Location[o]+']';
										
										Query1 = 'Cartesian scope: ([Version].[Version Name].[' + Version + '] * ' + ItemQ + ' * ' + AccountQ + ' * ' + ChannelQ + ' * ' + RegionQ + ' * ' + PnLQ + ' * ' + DemandDomainQ + ' * ' + LocationQ + '); ' + 'Measure.[Assortment New] = if(coalesce(Measure.[Assortment System], 0) == 0) then 1; end scope;';
       
										Query2='scope: ([Version].[Version Name].['+Version+'] * '+ItemQ+' * '+AccountQ+' * '+ChannelQ+' * '+RegionQ+' * '+PnLQ+' * '+DemandDomainQ+'* '+LocationQ+'); Measure.[Assortment Inactive]=null; end scope;';
										Query3='scope: ([Version].[Version Name].['+Version+'] * '+ItemQ+' * '+AccountQ+' * '+ChannelQ+' * '+RegionQ+' * '+PnLQ+' * '+DemandDomainQ+'* '+LocationQ+'); Measure.[Assortment Final] = if(sum(Measure.[Assortment System], Measure.[Assortment New], Measure.[Assortment Location Split], Measure.[Assortment Forecast Realignment]) >=1 && coalesce(Measure.[Assortment Inactive],0)>=1) then 0 else 1; end scope;';
										Query4='scope: ([Version].[Version Name].['+Version+'] * '+ItemQ+' * '+AccountQ+' * '+ChannelQ+' * '+RegionQ+' * '+PnLQ+' * '+DemandDomainQ+'* '+LocationQ+'); Measure.[Assortment New Created By]=if(sum(Measure.[Assortment New],0) >=1) then"'+CreatedBy+'"; Measure.[Assortment New Created On]=if(sum(Measure.[Assortment New],0) >=1) then todatetime("'+CreatedOn+'");end scope;';
										
										queryModule.update(Query1);
										queryModule.update(Query2);
										queryModule.update(Query3);
										queryModule.update(Query4);
									}
										
									error_Count=error_Count+1;
								}
							}
						}
					}
				}
			}
		}
		
		if (error_Count === 0 ){
			RuleOutputToUI = {
				Status: 'Error',
				Message: 'Assortments not created'
			};
			return;
		}
	
	Log.Info("Assortment Copied");
    };

    // Returning the CopyAssortment function
    return {
        CopyAssortment: CopyAssortment
    };
});

