define('o9.DemandPlanning.PythonInfraValidation',['o9/data/query', 'o9/data/cellset','o9.web.api'], function(){



    var Validate=function(o9Params){
        
        var queryModule=require('o9/data/query')       
        var cellsetModule = require('o9/data/cellset');
        
        var parsedParams = JSON.parse(o9Params);
        var Config = parsedParams.Config
        var MW = parsedParams.MediumWeight


        

       


        let table1 = "<table style='border: 1px solid #000; border-collapse: collapse; width: 48%; margin: 20px; font-size: 12px;'>";
        table1 += "<tbody>";
        table1 += `<tr><td style='padding: 10px 10px; border: 1px solid #ddd; font-weight: bold'>Config Version:</td><td style='padding: 10px 10px; border: 1px solid #ddd;'>${Config}</td></tr>`;


        var query1='Scope:(&CWV);Measure.[Infra Validation Status]=null;end scope;'
        try{
            queryModule.select(query1)
        }
        catch (err) {
            log.Info(`${err.message || 'Unknown error'}`);
        }


        try {
            query='exec plugin instance [DP090PythonInfraValidation] for measures {[Actual Cleansed]} using scope (&CWV) using arguments{([IgnoreMissingOutputs], true), ([MaxSliceTableCells],2000000000),("IncludeNullRows", "False"),([Param.o9_sys_log_level], "INFO"),(ExecutionMode,"MediumWeight")};'
            queryModule.select(query)
            table1+=`<tr><td style='padding: 10px 10px; border: 1px solid #ddd; font-weight: bold'>Medium Weight Execution</td><td style='padding: 10px 10px; border: 1px solid #ddd;'>Success</td></tr>`;
        } 
        catch (err) {
            const errorMessage = `${err.message || 'Unknown error'}`;
            

            // Create the plain text string with the error message and query
            const errorText = `Medium Weight Execution failed. \n\n${errorMessage}\n\nFailed Query Command:\n${query}`;

            // Encode the plain text as a data URL
            const encodedText = encodeURIComponent(errorText);
            table1 += `<tr><td style='padding: 10px 10px; border: 1px solid #ddd; font-weight: bold'>Medium Weight Execution</td>
                <td style='padding: 10px 10px; border: 1px solid #ddd;'>
                    <a href="data:text/plain;charset=utf-8,${encodedText}" download="errorData.txt" style="color: red;">Failed</a>
                </td></tr>`;
        }



        if(!MW){
        try {
            query='exec plugin instance [DP090PythonInfraValidation] for measures {[Actual Cleansed]} using scope (&CWV) using arguments{([IgnoreMissingOutputs], true), ([MaxSliceTableCells],2000000000),("IncludeNullRows", "False"),("SparkProfileConfig", "unsliced_low"),([Param.o9_sys_log_level], "INFO")};'
            queryModule.select(query)
            table1+=`<tr><td style='padding: 10px 10px; border: 1px solid #ddd; font-weight: bold'>Cluster Mode</td><td style='padding: 10px 10px; border: 1px solid #ddd;'>Success</td></tr>`;
        } 
