			if(FromRegion === null  || ToRegion === null)
			{
				error_Count=error_Count+1;
				Log.Error("VALIDATION ERROR: For the Selected Realignment Type From Region and To Region is mandatory.");
				RuleOutputToUI =
				{
					Status: 'Error',
					Message: 'VALIDATION ERROR: For the Selected Realignment Type From Region and To Region is mandatory.'
				};
			}
		}
		if (LocationLevel!==null)
		{
			if(FromLocation === null  || ToLocation === null)
			{
				error_Count=error_Count+1;
				Log.Error("VALIDATION ERROR: For the Selected Realignment Type From Location and To Location is mandatory.");
				RuleOutputToUI =
				{
					Status: 'Error',
					Message: 'VALIDATION ERROR: For the Selected Realignment Type From Location and To Location is mandatory.'
				};
			}
		}
		if (PnLLevel!==null)
		{
			if(FromPnL === null  || ToPnL === null)
			{
				error_Count=error_Count+1;
				Log.Error("VALIDATION ERROR: For the Selected Realignment Type From PnL and To PnL is mandatory.");
				RuleOutputToUI =
				{
					Status: 'Error',
					Message: 'VALIDATION ERROR: For the Selected Realignment Type From PnL and To PnL is mandatory.'
				};
			}
		}
		if (DemandDomainLevel!==null)
		{
			if(FromDemandDomain === null  || ToDemandDomain === null)
			{
				error_Count=error_Count+1;
				Log.Error("VALIDATION ERROR: For the Selected Realignment Type From DemandDomain and To DemandDomain is mandatory.");
				RuleOutputToUI =
				{
					Status: 'Error',
					Message: 'VALIDATION ERROR: For the Selected Realignment Type From DemandDomain and To DemandDomain is mandatory.'
				};
			}
		}
		// Incrementing Rule by 1
		if(error_Count===0)
		{
			if (Rule == "Rule_1000")
			{
				RuleInitializationQuery = 'Cartesian Scope:([Version].[Version Name].Filter( #.Name == "'+Version+'") * [Data Object].[Data Object Type].[Data Realignment].relatedmembers([Data Object])); Measure.[Data Object Rule Count] = 1001; end scope;';
			}
			else 
			{
				RuleInitializationQuery = 'Scope:([Version].[Version Name].Filter( #.Name == "'+Version+'") * [Data Object].[Data Object Type].[Data Realignment].relatedmembers([Data Object])); Measure.[Data Object Rule Count] = Measure.[Data Object Rule Count] +1;end scope;';
			}
			Log.Info("Rule Initialization Query: "+RuleInitializationQuery);
			queryModule.update(RuleInitializationQuery);
		}
        // Logging completion of the validation process
        Log.Info("Validation Complete");
    };

    // Returning the validation function
    return {
        validation: validation
    };
});

