define('o9.DemandPlanning.ReconList', ['o9/data/query', 'o9/data/cellset'], function () {

    
    var ReconList = function (o9Params) {
        // Parsing data from LS to the script
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        var parsedParams = JSON.parse(o9Params);
        var VersionName = parsedParams.VersionName;
        var MW=parsedParams.MediumWeight
		var InputStream = parsedParams.InputStream

        Log.Info("Version Name: " + VersionName);
		Log.Info("InputStream : " + InputStream);
        var Query='Select ([Version].[Version Name].['+VersionName+'] *  [Data Stream].[Data Stream Type].['+InputStream+']) on row,  ({Measure.[Forecast Reconciliation]}) on column;'
        Log.Info("Select Query : "+Query)
        var Cellsetdata = cellsetModule.createCellSet(queryModule.select(Query));
        var ForecastIteration = 'select ([Forecast Iteration].[Forecast Iteration].[FI-Default]);'
        
       
        var data = Cellsetdata.row(0).cell(Cellsetdata.measureColumnIndex('Forecast Reconciliation'));
        Log.Info("Forecast Recon List : "+data) 
        var DataArr=data.split(',')
        DataArr=DataArr.map(i=>"\""+i+"\"")
        Log.Info("Forecast Iteration Type :" + DataArr)
        var GenerateReconciledForecast='exec procedure o9DP0180GenerateReconciledForecast {"VersionName":["'+VersionName+'"],"ForecastIterationType":['+DataArr+'] ,"MediumWeight":'+MW+'};'
        Log.Info("Generate Reconciled Forecast Query :"+GenerateReconciledForecast)
        queryModule.select(GenerateReconciledForecast)
        
        
        
        
        // var nullStatement='Scope: (&CWVAndScenarios.['+VersionName+'] * [Time].[Partial Week] * &AllPlanningItem * &AllPlanningAccount * &AllPlanningChannel * &AllPlanningRegion * &AllPlanningDemandDomain * &AllPlanningPnL * &AllPlanningLocation  ); Measure.[Stat Fcst]=null; end scope;'
        // queryModule.select(nullStatement)
        // Log.Info("Null Stat Fcst : "+nullStatement)
        var actualize='scope: (&AllPlanningItem * &AllPlanningAccount * &AllPlanningChannel * &AllPlanningRegion * &AllPlanningDemandDomain * &AllPlanningPnL * &AllPlanningLocation * &StatHistoryBuckets * &CWVAndScenarios.['+VersionName+'] );Measure.[Stat Fcst]=Measure.[Actual];end scope;'
        queryModule.select(actualize)
        Log.Info("Actualize statement : "+actualize)
        
        var ForecastIterationcount = cellsetModule.createCellSet(queryModule.select(ForecastIteration));
        if (ForecastIterationcount.rowCount > 0 ){
            
        var StatfcstFIDefault = 'Scope: (&CWVAndScenarios.['+VersionName+'] * &StatForecastBuckets * &AllPlanningItem * &AllPlanningAccount * &AllPlanningChannel * &AllPlanningRegion * &AllPlanningDemandDomain * &AllPlanningPnL * &AllPlanningLocation  ) ; Measure.[Stat Fcst]=Measure.[Stat Fcst PL Agg]; end scope;'
        queryModule.select(StatfcstFIDefault)
        Log.Info("Generate Stat Fcst for FI-Default : "+StatfcstFIDefault)
        }
        
        else {
        
        var MWPlugin='Exec plugin instance [DP065HorizonReconciliation] for measures {[Stat Fcst PL Agg]} using scope (&CWVAndScenarios.['+VersionName+']  * &AllForecastIterationTypes.filter(#.DisplayName in {'+DataArr+'})*[Data Stream].[Data Stream Type].['+InputStream+']) using arguments {([IgnoreMissingOutputs], true),([MaxSliceTableCells],2000000000),(ExecutionMode,"MediumWeight")};'
        var CMPlugin='Exec plugin instance [DP065HorizonReconciliation] for measures {[Stat Fcst PL Agg]} using scope (&CWVAndScenarios.['+VersionName+']* &AllForecastIterationTypes.filter(#.DisplayName in {'+DataArr+'})*[Data Stream].[Data Stream Type].['+InputStream+']) using arguments {([IgnoreMissingOutputs], true),([MaxSliceTableCells],2000000000), ("IncludeNullRows", "False"),(DriverMemory, "5G"), (DriverCores, 1), (NumExecutors, 3), (ExecutorMemory, "2G"), (ExecutorCores, 2),("SparkProfileConfig", "unsliced_low")};'
        if(MW){
            Log.Info("MediumWeight Plugin execution : "+MWPlugin)
            queryModule.select(MWPlugin)
        } else {
            Log.Info("Cluster Mode Plugin Execution : "+CMPlugin)
            queryModule.select(CMPlugin)
        }
        }
    };
    return {
        ReconList:ReconList,
    };
    
});

