            // Log the result for debugging
            Log.Info('o9.demandplanning.NewItemUnitSize -> NewItemUnitSizeList : ' + NewItemUnitSizeList);

            // Extract the names from the result and store in NewUnitSize
            var NewUnitSize = NewItemUnitSizeList.map(function (item) {
                return item['Name'];
            });

            parsedParams['NewItemUnitSize'] = NewUnitSize[0];
        } 
        else {
            parsedParams['NewItemUnitSize'] = "Dummy";
        }

        // Return the modified parameters
        return parsedParams;
    };

    // Return the NewItemUnitSize function as part of the module
    return {
        NewItemUnitSize: NewItemUnitSize
    };
});

