define('o9.DemandPlanning.AttributeDS', ['o9/data/query', 'o9/data/cellset'], function () {
    var queryModule = require('o9/data/query');
    var cellsetModule = require('o9/data/cellset');

    var AttributeDS = function (o9Params) {
        var parsedParams = JSON.parse(o9Params);

        if (parsedParams.Dimension === null) {
            Log.Info("Dimension is null. Returning parsedParams as is.");
            return parsedParams;
        }

        var Dimension = parsedParams.Dimension;
        var Query;

        Log.Info("Dimension: " + Dimension);

        if (["Item", "Location"].includes(Dimension)) {
            if (Dimension === "Item") {
                Query = 'SELECT ([_SchemaDimension].[_Dimension].[Item].relatedmembers([_Attribute])) ' +
                        'WHERE {[_SchemaDimension].[_Level].filter(#.Name contains "[DP Item Hierarchy]")};';
            }
            if (Dimension === "Location") {
                Query = 'SELECT ([_SchemaDimension].[_Dimension].[Location].relatedmembers([_Attribute])) ' +
                        'WHERE {[_SchemaDimension].[_Level].filter(#.Name contains "[DP Location Hierarchy]")};';
            }
        } else {
            var hierarchyName = "";
            switch (Dimension) {
                case "Account":
                    hierarchyName = "[Account Hierarchy]";
                    break;
                case "Channel":
                    hierarchyName = "[Channel Hierarchy]";
                    break;
                case "Demand Domain":
                    hierarchyName = "[Demand Domain Hierarchy]";
                    break;
                case "PnL":
                    hierarchyName = "[PnL Hierarchy]";
                    break;
                case "Region":
                    hierarchyName = "[Region Hierarachy]";
                    break;
            }

            Query = 'SELECT (' +
                    '[_SchemaDimension].[_Dimension].[' + Dimension + ']' +
                    '.relatedmembers([_Attribute])' +
                    '.filter(#.Name != "[' + Dimension + '].[' + Dimension + ']")' +
                    ') WHERE {' +
                    '[_SchemaDimension].[_Level].filter(#.Name contains "' + hierarchyName + '")' +
                    '};';
        }

        parsedParams['Query'] = Query;
        return parsedParams;
    };

    return {
        AttributeDS: AttributeDS
    };
});

