var parsedParams = JSON.parse(o9Params);
var queryModule = require('o9/data/query');
var cellsetModule = require('o9/data/cellset');
//Log method logs to the webapi log
Log.Info("init restrict")
var ForecastIterationType = parsedParams.ForecastIterationType;
var VersionName = parsedParams.VersionName;
var MemberName = parsedParams.MemberName
var ForecastIterationCreatedBy = parsedParams.ForecastIterationCreatedBy
var ItemLevel = parsedParams.ItemLevel
var AccountLevel = parsedParams.AccountLevel
var ChannelLevel = parsedParams.ChannelLevel
var RegionLevel = parsedParams.RegionLevel
var LocationLevel = parsedParams.LocationLevel
var PnLLevel = parsedParams.PnLLevel
var DemandDomainLevel = parsedParams.DemandDomainLevel
var DisaggregationType = parsedParams.DisaggregationType
var Description = parsedParams.Description
var DisplayName = parsedParams.DisplayName
var MaxNumberofIteration = 10 


Log.Info("Version Name: " + VersionName);
Log.Info("ForecastIterationType - " + ForecastIterationType);
Log.Info(" MaxNumberofIteration - " + MaxNumberofIteration);


// var IsScopeLocked = 'Select ([Version].[Version Name].['+VersionName +'] * [Forecast Iteration].[Forecast Iteration Type].['+ForecastIterationType +'] ) on row, ({Measure.[Is Iteration Scope Level Created]}) on column;';
// var IsScopeLockedData = cellsetModule.createCellSet(queryModule.select(IsScopeLocked));
var InputStream = ForecastIterationType.split('_');
InputStream = InputStream[1];
var IterationCount = 'select ([Forecast Iteration].[Forecast Iteration Type].filter(#.Name contains "_'+InputStream+'_" )*[Forecast Iteration].[Forecast Iteration]);';
var IterationCountData = cellsetModule.createCellSet(queryModule.select(IterationCount));



Log.Info(" IterationCountData - " + IterationCountData.rowCount);

// if (IsScopeLockedData.rowCount > 0)
// {
// 	RuleOutputToUI =
// 	{
// 	Status: 'Error',
// 	Message: 'Not allowed to create a new iteration within an Iteration Type which already contains iteration(s) with scope configured! Please reset the scope of existing relevant iteration(s) and try again.'    
//   };
// } 

if (IterationCountData.rowCount >= MaxNumberofIteration)
{
	RuleOutputToUI =
	{
	Status: 'Error',
	Message: 'Not allowed to create a new iteration because you have exceeded maximum number allowed (5) per input stream.'    
  };
  return RuleOutputToUI;
}

var createIteration = 'exec procedure o9DP0010InitializeForecastIteration{"VersionName" : ["'+VersionName+'"],"MemberName" : "'+MemberName+'","ForecastIterationType" : "'+ForecastIterationType+'","ForecastIterationCreatedBy" : "'+ForecastIterationCreatedBy+'","Item":"'+ItemLevel+'", "Account":"'+AccountLevel+'" ,"Channel":"'+ChannelLevel+'","Region":"'+RegionLevel+'","Location":"'+LocationLevel+'","PnL":"'+PnLLevel+'", "DemandDomain":"'+DemandDomainLevel+'" ,"Disaggregation":"'+DisaggregationType+'", "Description":"'+Description+'","DisplayName":"'+DisplayName+'" };'
queryModule.select(createIteration)

var FilterAttribute = 'Select ([Version].[Version Name].['+VersionName+'] * [Forecast Iteration].[Forecast Iteration] ) on row, ({Measure.[Is Filter Attribute Set]}) on column;'
var FilterAttributeCount = cellsetModule.createCellSet(queryModule.select(FilterAttribute));
Log.Info("FilterAttributeCount - " + FilterAttributeCount.rowCount);
var flag = ''
if (FilterAttributeCount.rowCount > 0){
    Log.Info("FilterAttributeCount - " + FilterAttributeCount.rowCount);
	flag = 'cartesian scope: ([Version].[Version Name].['+VersionName+'] * [Forecast Iteration].[Forecast Iteration] ) ;Measure.[Is Filter Attribute Set] = 1;end scope;'
    queryModule.select(flag)
	}



};
  
    return {
        validation : validation
    };
});

