define('o9.DemandPlanning.NewItemRGM4', ['o9/data/query', 'o9/data/cellset'], function () {

    // Define the NewItemRGM4 function
    var NewItemRGM4 = function (o9Params) {
        // Parsing data from LS (Local Storage?) to the script
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        var parsedParams = JSON.parse(o9Params);

        // Get the NewItem from the parsed parameters
        var ExistingItem = parsedParams.ExistingItem;
        // Create a query to retrieve data
        var NewItemRGM4Query = 'Select ([Item].[Item].[' + ExistingItem + '] * [Item].[RGM4] );';
        var NewItemRGM4DataCellSet = cellsetModule.createCellSet(queryModule.select(NewItemRGM4Query));
		    // Log the query for debugging
            Log.Info('NewItemRGM4Query : ' + NewItemRGM4Query);
		// Check if NewRGM4 is null or falsy, and replace with "Dummy" if so
        if (NewItemRGM4DataCellSet.rowCount > 0) {

            // Extract the attribute members for "Item" and "RGM4"
            var NewItemRGM4List = NewItemRGM4DataCellSet.attributeMembers("Item", "RGM4");

            // Log the result for debugging
            Log.Info('o9.demandplanning.NewItemRGM4 -> NewItemRGM4List : ' + NewItemRGM4List);

            // Extract the names from the result and store in NewRGM4
            var NewRGM4 = NewItemRGM4List.map(function (item) {
                return item['Name'];
            });

            parsedParams['NewItemRGM4'] = NewRGM4[0];
        } 
        else {
            parsedParams['NewItemRGM4'] = "Dummy";
        }

        // Return the modified parameters
        return parsedParams;
    };

    // Return the NewItemRGM4 function as part of the module
    return {
        NewItemRGM4: NewItemRGM4
    };
});

