define('o9.DemandPlanning.CreateConcatenatedStringSellOut', ['o9/data/query', 'o9/data/cellset', 'o9.web.api'], function () {
    var queryModule = require('o9/data/query');
	var cellsetModule = require('o9/data/cellset');
    
    var CreateConcatenatedString = function (o9Params) {
        Log.Info("Harshit");
        var parsedParams = JSON.parse(o9Params);
        var concatenatedString = '';
        
        // Helper function to extract and append the value
        function appendLevelValue(level) {
            if (level) {
                // Corrected regex to capture the value after the dot in the second set of square brackets
                var match = level.match(/\[[^\]]+\]\.\[([^\]]+)\]/);  // Capture the second part after the dot
                
                if (match && match[1]) {
                    // Append the correct value (after the dot) to the concatenated string.
                    concatenatedString += match[1] + ' ';
                    Log.Info(concatenatedString);
                }
            }
        }
        
        // Check Itemlevel and append
        appendLevelValue(parsedParams.Itemlevel);
        // Check Accountlevel and append
        appendLevelValue(parsedParams.Accountlevel);
        // Check Channellevel and append
        appendLevelValue(parsedParams.Channellevel);
        // Check DemandDomainlevel and append
        appendLevelValue(parsedParams.DemandDomainlevel);
        // Check Regionlevel and append
        appendLevelValue(parsedParams.Regionlevel);
        
        // Add "Realignment_Sell Out" to the end of the string
        concatenatedString += "Realignment_Sell Out";
        // Trim the trailing space and return the concatenated string
        parsedParams['concatenatedString'] = concatenatedString.trim();
        var Version = parsedParams.Version || '';
        var Itemlevel = parsedParams.Itemlevel || 'ALL';
        var Accountlevel = parsedParams.Accountlevel || 'ALL';
        var Channellevel = parsedParams.Channellevel || 'ALL';
        var Regionlevel = parsedParams.Regionlevel || 'ALL';
        var DemandDomainlevel = parsedParams.DemandDomainlevel || 'ALL';
        var HistoryRealignment = parsedParams.HistoryRealignment;
        var ForecastRealignment = parsedParams.ForecastRealignment;
		var ProcessOrder = parsedParams.ProcessOrder;
        var Createmember = 'Createmember([Data Object].[Data Object]={,"'+concatenatedString+'"}, [Data Object].[Data Object Type]={,"Sell Out Realignment"});';
        queryModule.update(Createmember);
        Log.Info(Createmember);
        var cartesianscope = 'Cartesian scope: ([Version].[Version Name].['+Version+'] * [Data Object].[Data Object].['+concatenatedString+']); Measure.[Data Object Item Level]="'+Itemlevel+'"; Measure.[Data Object Account Level]="'+Accountlevel+'"; Measure.[Data Object Channel Level]="'+Channellevel+'"; Measure.[Data Object Region Level]="'+Regionlevel+'"; Measure.[Data Object Demand Domain Level]="'+DemandDomainlevel+'"; Measure.[Realign History]='+HistoryRealignment+'; Measure.[Realign Forecast]='+ForecastRealignment+';Measure.[Data Object Process Order]='+ProcessOrder+'; end scope;';
        queryModule.update(cartesianscope);
        Log.Info(cartesianscope);
        Log.Info(concatenatedString);
        
        return parsedParams;
    };
    
    return {
        CreateConcatenatedString: CreateConcatenatedString
    };
});

