define('o9.DemandPlanning.ElementsforIterationSelection', ['o9/data/query', 'o9/data/cellset'], function () {
    
    
    var DefaultElement={'Item':'All Planning Item',
            'Account':'All Planning Account',
            'Channel':'All Planning Channel',
            'Region':'All Planning Region',
            'Demand Domain':'All Planning Demand Domain',
            'PnL':'All Planning PnL',
            'Location':'All Planning Location'
        }
    
    var ExtractMeasure=function(Query,Measure='RecordCount'){
        var cellsetModule = require('o9/data/cellset');
        var queryModule=require('o9/data/query')
        var QueryCellset=cellsetModule.createCellSet(queryModule.select(Query))
        
        if (QueryCellset.rowCount==0)
            return 0 
        var value =QueryCellset.row(0).cell(QueryCellset.measureColumnIndex(Measure))   
        Log.Info("Value be : "+value)
        return value
        
    }
	var ElementsforIterationSelection = function (o9Params) {
        // Parsing data from LS to the script
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        var parsedParams = JSON.parse(o9Params);

        
        var VersionName = parsedParams.VersionName;
        var IterationType =parsedParams.ForecastIterationType;
        
        Log.Info("STMT Location");
		Log.Info("Check Iteration Type Location: " +IterationType);
		Log.Info("STMT Account1:" +AccountScope);
		ScopeQuery = 'Select ([Version].[Version Name].['+VersionName+']* [Forecast Iteration].[Forecast Iteration Type].['+IterationType+'] ) on row, ({Measure.[Location Scope],Measure.[Item Scope],Measure.[Account Scope],Measure.[Channel Scope],Measure.[Region Scope],Measure.[PnL Scope],Measure.[Demand Domain Scope]}) on column;'

        var PnLScope = ExtractMeasure(ScopeQuery, 'PnL Scope');
        var LocationScope = ExtractMeasure(ScopeQuery, 'Location Scope');
        var ItemScope = ExtractMeasure(ScopeQuery, 'Item Scope');
        var AccountScope = ExtractMeasure(ScopeQuery, 'Account Scope');
        var ChannelScope = ExtractMeasure(ScopeQuery, 'Channel Scope');
        var RegionScope = ExtractMeasure(ScopeQuery, 'Region Scope');
        var DemandDomainScope = ExtractMeasure(ScopeQuery, 'Demand Domain Scope');
       
       

		
		
				
		InputStream = IterationType.split('_');
        InputStream = InputStream[1];
		Log.Info('Input Stream :' +InputStream)
		
		if (InputStream=='Actual') {

        var AssortmentQuery = '(Select ( [Region].[Planning Region].relatedmembers(['+RegionScope+']) * [Item].[Planning Item].relatedmembers(['+ItemScope+']) * [PnL].[Planning PnL].relatedmembers(['+PnLScope+']) * [Location].[Location].relatedmembers(['+LocationScope+']) * [Demand Domain].[Planning Demand Domain].relatedmembers(['+DemandDomainScope+']) * [Account].[Planning Account].relatedmembers(['+AccountScope+']) * [Channel].[Planning Channel].relatedmembers(['+ChannelScope+']) *&CWVAndScenarios.['+VersionName+'] * &AllForecastIterationTypes.['+IterationType+']) on row, ({Measure.[Forecast Iteration Selection],Measure.[Assortment Stat]}) on column include nulls).filter(Measure.[Assortment Stat]>0 && (isnull(Measure.[Forecast Iteration Selection]) || Measure.[Forecast Iteration Selection]=="FI-PL")) ;'
        var AssortmentCellSet = cellsetModule.createCellSet(queryModule.select(AssortmentQuery));
        Log.Info("AssortmentQuery:" + AssortmentCellSet);
        var ItemList=AssortmentCellSet.attributeMembers('Item',ItemScope).map(function (item) {
            return item['Name'];
        });
        ItemList = ItemList.filter(item => item !== DefaultElement['Item']);
        ItemList = ItemList.map(i => '[' + i);
        ItemList = ItemList.map(i => i + ']');
        if (ItemList.length === 0) {
            ItemList = "[]";
        }
        
        var AccountList=AssortmentCellSet.attributeMembers('Account',AccountScope).map(function (item) {
            return item['Name'];
        });
        AccountList = AccountList.filter(item => item !== DefaultElement['Account']);
        AccountList = AccountList.map(i => '[' + i);
        AccountList = AccountList.map(i => i + ']');
        if (AccountList.length === 0) {
            AccountList = "[]";
        }
        var ChannelList=AssortmentCellSet.attributeMembers('Channel',ChannelScope).map(function (item) {
            return item['Name'];
        });
        ChannelList = ChannelList.filter(item => item !== DefaultElement['Channel']);
        ChannelList = ChannelList.map(i => '[' + i);
        ChannelList = ChannelList.map(i => i + ']');
        if (ChannelList.length === 0) {
            ChannelList = "[]";
        }
        var RegionList=AssortmentCellSet.attributeMembers('Region',RegionScope).map(function (item) {
            return item['Name'];
        });
        RegionList = RegionList.filter(item => item !== DefaultElement['Region']);
            RegionList = RegionList.map(i => '[' + i);
            RegionList = RegionList.map(i => i + ']');
            if (RegionList.length === 0) {
                RegionList = "[]";
            }

        var DemandDomainList=AssortmentCellSet.attributeMembers('Demand Domain',DemandDomainScope).map(function (item) {
