	var ExistingRegionString = function (o9Params) {
        // Parsing data from LS to the script
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        var parsedParams = JSON.parse(o9Params);
		

        var VersionName = parsedParams.VersionName;
        var IterationType = parsedParams.ForecastIterationType;
        Log.Info("STMT Region");
        Log.Info("Check Iteration Type Region: " +IterationType);
		
		
						
		var AccountScope = parsedParams.AccountScope;
        Log.Info("STMT Account1:" +AccountScope);
        var ItemScope = parsedParams.ItemScope;
        var LocationScope = parsedParams.LocationScope;
        var ChannelScope = parsedParams.ChannelScope;
        var PnLScope = parsedParams.PnLScope;
        var DemandDomainScope = parsedParams.DemandDomainScope;
        var RegionScope = parsedParams.RegionScope;

        Log.Info("Version Name: " + VersionName);
        var ExistingShiftPatternsRegionQuery = '(Select ( [Region].[Planning Region].relatedmembers(['+RegionScope+']) * [Item].[Planning Item].relatedmembers(['+ItemScope+']) * [PnL].[Planning PnL].relatedmembers(['+PnLScope+']) * [Location].[Location].relatedmembers(['+LocationScope+']) * [Demand Domain].[Planning Demand Domain].relatedmembers(['+DemandDomainScope+']) * [Account].[Planning Account].relatedmembers(['+AccountScope+']) * [Channel].[Planning Channel].relatedmembers(['+ChannelScope+']) *&CWVAndScenarios.['+VersionName+'] * &AllForecastIterationTypes.['+IterationType+']) on row, ({Measure.[Forecast Iteration Selection],Measure.[Assortment Stat]}) on column include nulls).filter(Measure.[Assortment Stat]>0 && (isnull(Measure.[Forecast Iteration Selection]) || Measure.[Forecast Iteration Selection]=="FI-PL")) ;'
         var ExistingShiftPatternsRegionDataCellSet = cellsetModule.createCellSet(queryModule.select(ExistingShiftPatternsRegionQuery));
        Log.Info(ExistingShiftPatternsRegionDataCellSet);
        var ExistingShiftPatternsRegionList = ExistingShiftPatternsRegionDataCellSet.attributeMembers("Region",RegionScope);
        Log.Info('o9.DemandPlanning.ExistingItemString -> ExistingShiftPatternsRegionList : ' + ExistingShiftPatternsRegionList);
        var ExistingShiftPatternsRegion = ExistingShiftPatternsRegionList.map(function (item) {
            return item['Name'];
        });

        ExistingShiftPatternsRegion = ExistingShiftPatternsRegion.map(i => '[' + i);
        ExistingShiftPatternsRegion = ExistingShiftPatternsRegion.map(i => i + ']');
        Log.Info('o9.DemandPlanning.ExistingItemString -> ExistingShiftPatternsRegion : ' + ExistingShiftPatternsRegion);
        if (ExistingShiftPatternsRegion.length === 0){
            ExistingShiftPatternsRegion = "[]";
