define('o9.DemandPlanning.StatActualBatchRun',['o9/data/query', 'o9/data/cellset','o9.web.api'],function(){
    
    
    const AssortmentMeasure = {
        "Actual": "Assortment Stat",
        "Sell Out Actual": "Sell Out Actual"
    };
    var ExtractMeasure=function(Query,Measure='RecordCount'){
        var cellsetModule = require('o9/data/cellset');
        var queryModule=require('o9/data/query')
        var QueryCellset=cellsetModule.createCellSet(queryModule.select(Query))
        
        if (QueryCellset.rowCount==0)
            return 0 
        var value =QueryCellset.row(0).cell(QueryCellset.measureColumnIndex(Measure))   
        Log.Info("Value be : "+value)
        return value
        
    }
    
    
    var  StatActualBatchRun = function(o9Params){
	var parsedParams = JSON.parse(o9Params);

    var queryModule = require('o9/data/query');
	var cellsetModule = require('o9/data/cellset');
	
	var ForecastIterationType = parsedParams.ForecastIterationType;
	var VersionName = parsedParams.VersionName;

    //Log method logs to the webapi log
    var ForecastIteration = 'Select ([Forecast Iteration].[Forecast Iteration] * [Forecast Iteration].[Forecast Iteration Type].['+ForecastIterationType+']);';
    var ForecastIterationq = cellsetModule.createCellSet(queryModule.select(ForecastIteration));
    var ForecastIterationData = ForecastIterationq.attributeMembers('Forecast Iteration', 'Forecast Iteration');
    var ForecastIterationDatalist = ForecastIterationData.map(function (item) {
                return item['Name'];
            });
    Log.Info("ForecastIterationDatalist:" + ForecastIterationDatalist);  

    ForecastIterationDatalist.forEach(function(ForecastIteration) {
    Log.Info("ForecastIterationValue:" +ForecastIteration )

        var IterationQuery='Select([Forecast Iteration].[Forecast Iteration].['+ForecastIteration+'] *[Forecast Iteration].[Forecast Iteration Type]);'
        var Typeset=cellsetModule.createCellSet(queryModule.select(IterationQuery))
        var IterationType=Typeset.row(0).cell(Typeset.memberColumnIndex('Forecast Iteration','Forecast Iteration Type')).Name;
        var InputStream = IterationType.split('_');
        InputStream = InputStream[1];



        if (ForecastIteration == 'FI-Default') {
        
            var MasterIterationScope = 'cartesian scope: (Version.[Version Name].['+VersionName+'] * &AllForecastIterationTypes.['+IterationType+'] * [Item].[Planning Item] * [Account].[Planning Account] * [Channel].[Planning Channel] * [Region].[Planning Region] * [Location].[Location] * [Demand Domain].[Planning Demand Domain] * [PnL].[Planning PnL]);Measure.[Forecast Iteration Selection] = if (~isnull(Measure.['+AssortmentMeasure[InputStream]+'])) then "FI-Default" else null;end scope;';
            queryModule.select(MasterIterationScope)	
        return 
            
        }


        ScopeQuery = 'Select ([Version].[Version Name].['+VersionName+'] * &AllForecastIterationTypes.['+IterationType+']) on row, ({Measure.[Location Scope],Measure.[Item Scope],Measure.[Account Scope],Measure.[Channel Scope],Measure.[Region Scope],Measure.[PnL Scope],Measure.[Demand Domain Scope]}) on column;'

        var PnLScope = ExtractMeasure(ScopeQuery, 'PnL Scope');
        var LocationScope = ExtractMeasure(ScopeQuery, 'Location Scope');
        var ItemScope = ExtractMeasure(ScopeQuery, 'Item Scope');
        var AccountScope = ExtractMeasure(ScopeQuery, 'Account Scope');
        var ChannelScope = ExtractMeasure(ScopeQuery, 'Channel Scope');
        var RegionScope = ExtractMeasure(ScopeQuery, 'Region Scope');
        var DemandDomainScope = ExtractMeasure(ScopeQuery, 'Demand Domain Scope');

        var SelectionScope=`(Select ([Version].[Version Name].[${VersionName}] * [Forecast Iteration].[Forecast Iteration Type].[${IterationType}] * [Account].[${AccountScope}] * [Channel].[${ChannelScope}] * [Demand Domain].[${DemandDomainScope}] * [Item].[${ItemScope}] * [PnL].[${PnLScope}] * [Region].[${RegionScope}] * [Location].[${LocationScope}] ) on row, ({Measure.[Forecast Iteration Selection]}) on column).filter(Measure.[Forecast Iteration Selection] == "${ForecastIteration}");`

        const SelectionCellset= cellsetModule.createCellSet(queryModule.select(SelectionScope));




        var ItemList=SelectionCellset.attributeMembers('Item',ItemScope).map(function (item) {
                        return item['Name'];
                    });
        ItemList=ItemList.map(element => `"${element}"`)
        var AccountList=SelectionCellset.attributeMembers('Account',AccountScope).map(function (item) {
                        return item['Name'];
                    });
        AccountList=AccountList.map(element => `"${element}"`)
        var ChannelList=SelectionCellset.attributeMembers('Channel',ChannelScope).map(function (item) {
                        return item['Name'];
                    });
        ChannelList=ChannelList.map(element => `"${element}"`)
        var RegionList=SelectionCellset.attributeMembers('Region',RegionScope).map(function (item) {
                        return item['Name'];
                    });
        RegionList=RegionList.map(element => `"${element}"`)
        var LocationList=SelectionCellset.attributeMembers('Location',LocationScope).map(function (item) {
                        return item['Name'];
                    });
        LocationList=LocationList.map(element => `"${element}"`)
        var PnLList=SelectionCellset.attributeMembers('PnL',PnLScope).map(function (item) {
                        return item['Name'];
                    });
        PnLList=PnLList.map(element => `"${element}"`)
        var DemandDomainList=SelectionCellset.attributeMembers('Demand Domain',DemandDomainScope).map(function (item) {
