define('o9.DemandPlanning.NewItemPPG', ['o9/data/query', 'o9/data/cellset'], function () {

    // Define the NewItemPPG function
    var NewItemPPG = function (o9Params) {
        // Parsing data from LS (Local Storage?) to the script
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        var parsedParams = JSON.parse(o9Params);

        // Get the NewItem from the parsed parameters
        var ExistingItem = parsedParams.ExistingItem;
        // Create a query to retrieve data
        var NewItemPPGQuery = 'Select ([Item].[Item].[' + ExistingItem + '] * [Item].[PPG] );';
        var NewItemPPGDataCellSet = cellsetModule.createCellSet(queryModule.select(NewItemPPGQuery));
		    // Log the query for debugging
            Log.Info('NewItemPPGQuery : ' + NewItemPPGQuery);
		// Check if NewPPG is null or falsy, and replace with "Dummy" if so
        if (NewItemPPGDataCellSet.rowCount > 0) {

            // Extract the attribute members for "Item" and "PPG"
            var NewItemPPGList = NewItemPPGDataCellSet.attributeMembers("Item", "PPG");

            // Log the result for debugging
            Log.Info('o9.demandplanning.NewItemPPG -> NewItemPPGList : ' + NewItemPPGList);

            // Extract the names from the result and store in NewPPG
            var NewPPG = NewItemPPGList.map(function (item) {
                return item['Name'];
            });

            parsedParams['NewItemPPG'] = NewPPG[0];
        } 
        else {
            parsedParams['NewItemPPG'] = "Dummy";
        }

        // Return the modified parameters
        return parsedParams;
    };

    // Return the NewItemPPG function as part of the module
    return {
        NewItemPPG: NewItemPPG
    };
});

