define('o9.DemandPlanning.CreateStatMemberBatchRun', ['o9/data/query', 'o9/data/cellset'], function () {  
    
    
    function findValueByKey(array, key) {
       const item = array.find(item => item.name === key);
       return item ? item.Member : undefined;
       }
       
   var CreateStatDataBatch = function(o9Params){ 
                   var queryModule = require('o9/data/query');
                   var cellsetModule = require('o9/data/cellset');
                   var parsedParams = JSON.parse(o9Params);
                   var VersionName = parsedParams.VersionName;
                   var ForecastIterationType = parsedParams.ForecastIterationType
                   var MW=parsedParams.MediumWeight
                   Log.Info("ForecastIterationType: "+ForecastIterationType);
				   
				   var ForecastIteration = 'Select ([Forecast Iteration].[Forecast Iteration] * [Forecast Iteration].[Forecast Iteration Type].filter(#.Name startswith(['+ForecastIterationType+'])));';
				   var ForecastIterationq = cellsetModule.createCellSet(queryModule.select(ForecastIteration));
				   var ForecastIterationData = ForecastIterationq.attributeMembers('Forecast Iteration', 'Forecast Iteration');
				   var ForecastIterationDatalist = ForecastIterationData.map(function (item) {
            return item['Name'];
        });
				 Log.Info("ForecastIterationDatalist:" + ForecastIterationDatalist);  
				   
			ForecastIterationDatalist.forEach(function(ForecastIteration) {

                   Log.Info("ForecastIterationValue:" +ForecastIteration )

                   var query='Select ([Forecast Iteration].[Forecast Iteration].['+ForecastIteration+'] * [Forecast Iteration].[Forecast Iteration Type]);'
                   var TypeCellSet=cellsetModule.createCellSet(queryModule.select(query))
                   var TypeDataJSON= TypeCellSet.attributeMembers('Forecast Iteration','Forecast Iteration Type')
                   var TypeData=TypeDataJSON.Name
                  var createmember = 'exec procedure o9DP006CreateStatMeasures{"VersionName" : ["'+VersionName+'"], "ForecastIteration" : "'+ForecastIteration+'","MediumWeight":'+MW+'};'
                  Log.Info("Parameterized Stat Member procedure :"+createmember)
                  var creatememberData = queryModule.select(createmember)
                  Log.Info("Parameterized Create Stat Member procedure :"+creatememberData)

				  
                 
				  
				  if(ForecastIteration == 'FI-PL'){
				  Log.Info("Passed Iteration is FI PL ")
				  var Preprocess = 'exec procedure o9DP013PreprocessPLScope{"VersionName" : ["'+VersionName+'"], "ForecastIterationType" : "'+TypeData+'"};'
				  Log.Info("PreprocessForPL :"+Preprocess)
				  var PreprocessData = queryModule.select(Preprocess)
                  }

				  
                   const ItemHigh = [
                   {name: 'Item', level: 1},
                   {name: 'Reporting Item', Member: 'Reporting Item Members'},
                   {name: 'Planning Item', Member: 'Planning Item Members'},
                   {name: 'L1', Member: 'L1 Members'},
                   {name: 'L2', Member: 'L2 Members'},
                   {name: 'L3', Member: 'L3 Members'},
                   {name: 'L4', Member: 'L4 Members'},
                   {name: 'L5', Member: 'L5 Members'},
                   {name: 'L6', Member: 'L6 Members'},
                   {name: 'All Item', Member: 'All Item Members'},
