define('o9.DemandPlanning.ExistingChannelParameter', ['o9/data/query', 'o9/data/cellset'], function () {

    
	
	var ExistingChannelString = function (o9Params) {
        // Parsing data from LS to the script
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        var parsedParams = JSON.parse(o9Params);

        var VersionName = parsedParams.VersionName;
        var IterationType =parsedParams.ForecastIterationType;
		Log.Info("Check Iteration Type Channel: " +IterationType);
		Log.Info("STMT Channel");
		var AccountScope = parsedParams.AccountScope;
        Log.Info("STMT Account1:" +AccountScope);
        var ItemScope = parsedParams.ItemScope;
        var LocationScope = parsedParams.LocationScope;
        var ChannelScope = parsedParams.ChannelScope;
        var PnLScope = parsedParams.PnLScope;
        var DemandDomainScope = parsedParams.DemandDomainScope;
        var RegionScope = parsedParams.RegionScope;
		
		InputStream = IterationType.split('_');
        InputStream = InputStream[1];
		Log.Info('Input Stream :' +InputStream)

        Log.Info("Version Name: " + VersionName);
		
        if (InputStream=='Actual'){
        var ExistingShiftPatternsChannelQuery ='(Select ( [Region].[Planning Region].relatedmembers(['+RegionScope+']) * [Item].[Planning Item].relatedmembers(['+ItemScope+']) * [PnL].[Planning PnL].relatedmembers(['+PnLScope+']) * [Location].[Location].relatedmembers(['+LocationScope+']) * [Demand Domain].[Planning Demand Domain].relatedmembers(['+DemandDomainScope+']) * [Account].[Planning Account].relatedmembers(['+AccountScope+']) * [Channel].[Planning Channel].relatedmembers(['+ChannelScope+']) *&CWVAndScenarios.['+VersionName+'] * &AllForecastIterationTypes.['+IterationType+']) on row, ({Measure.[Forecast Iteration Selection],Measure.[Assortment Stat]}) on column include nulls).filter(Measure.[Assortment Stat]>0 && (isnull(Measure.[Forecast Iteration Selection]) || Measure.[Forecast Iteration Selection]=="FI-PL")) ;'
        var ExistingShiftPatternsChannelDataCellSet = cellsetModule.createCellSet(queryModule.select(ExistingShiftPatternsChannelQuery));
        Log.Info(ExistingShiftPatternsChannelDataCellSet);
        var ExistingShiftPatternsChannelList = ExistingShiftPatternsChannelDataCellSet.attributeMembers("Channel", ChannelScope);
        Log.Info('o9.DemandPlanning.ExistingItemString -> ExistingShiftPatternsChannelList : ' + ExistingShiftPatternsChannelList);
        }
		
		
		else {
		var ExistingShiftPatternsChannelQuery ='(Select ( [Region].[Planning Region].relatedmembers(['+RegionScope+']) * [Item].[Planning Item].relatedmembers(['+ItemScope+']) * [PnL].[Planning PnL].relatedmembers(['+PnLScope+']) * [Location].[Location].relatedmembers(['+LocationScope+']) * [Demand Domain].[Planning Demand Domain].relatedmembers(['+DemandDomainScope+']) * [Account].[Planning Account].relatedmembers(['+AccountScope+']) * [Channel].[Planning Channel].relatedmembers(['+ChannelScope+']) *&CWVAndScenarios.['+VersionName+'] * &AllForecastIterationTypes.['+IterationType+']) on row, ({Measure.[Forecast Iteration Selection],Measure.['+InputStream+']}) on column include nulls).filter(Measure.['+InputStream+']>0 && (isnull(Measure.[Forecast Iteration Selection]) || Measure.[Forecast Iteration Selection]=="FI-PL")) ;'
        var ExistingShiftPatternsChannelDataCellSet = cellsetModule.createCellSet(queryModule.select(ExistingShiftPatternsChannelQuery));
        Log.Info(ExistingShiftPatternsChannelDataCellSet);
        var ExistingShiftPatternsChannelList = ExistingShiftPatternsChannelDataCellSet.attributeMembers("Channel", ChannelScope);
        Log.Info('o9.DemandPlanning.ExistingItemString -> ExistingShiftPatternsChannelList : ' + ExistingShiftPatternsChannelList);

			
		}
		
		
		
		var ExistingShiftPatternsChannel = ExistingShiftPatternsChannelList.map(function (item) {
            return item['Name'];
        });

        ExistingShiftPatternsChannel = ExistingShiftPatternsChannel.map(i => '[' + i);
        ExistingShiftPatternsChannel = ExistingShiftPatternsChannel.map(i => i + ']');
        Log.Info('o9.DemandPlanning.ExistingItemString -> ExistingShiftPatternsChannel : ' + ExistingShiftPatternsChannel);
        if (ExistingShiftPatternsChannel.length === 0){
            ExistingShiftPatternsChannel = "[]";
        }
        
        parsedParams['ExistingChannelString'] = ExistingShiftPatternsChannel.toString();
        Log.Info("ExistingChannelString: " + ExistingShiftPatternsChannel.toString());

        return parsedParams;
		
	};
    return {
        ExistingChannelString: ExistingChannelString,
    };
    
});

