        Log.Info("Data Object Type: " + DataObject);
        Log.Info("Rule ID: " + Rule);
        Log.Info("Item L2: " + ItemScope);
		Log.Info("Location Country " + LocationScope);
		Log.Info("Channel: " + ChannelScope);
		
         
		// Incrementing Rule by 1

		if (Rule == "Rule_1000")
		{
			RuleInitializationQuery = 'Cartesian Scope:([Version].[Version Name].Filter( #.Name == "'+Version+'") * [Data Object].[Data Object Type].[Forecast Exceptions Objects].relatedmembers([Data Object])); Measure.[Data Object Rule Count] = 1001; end scope;';
