define('o9.DemandPlanning.SetAssortmentInactive', ['o9/data/query', 'o9/data/cellset'],  function() {
	//initiatlize the query modules
	var queryModule = require('o9/data/query');
	var cellsetModule = require('o9/data/cellset');
	var SetAssortmentInactive = function (o9Params){
		var parsedParams = JSON.parse(o9Params);
		
		var Version = parsedParams.Version;
		var ItemLevel = parsedParams.ItemLevel;
		var AccountLevel = parsedParams.AccountLevel;
		var ChannelLevel = parsedParams.ChannelLevel;
		var RegionLevel = parsedParams.RegionLevel;
		var LocationLevel = parsedParams.LocationLevel;
		var PnLLevel = parsedParams.PnLLevel;
		var DemandDomainLevel = parsedParams.DemandDomainLevel;
		
		var ItemScope = parsedParams.ItemScope;
		var AccountScope = parsedParams.AccountScope;
		var ChannelScope = parsedParams.ChannelScope;
		var RegionScope = parsedParams.RegionScope;
		var LocationScope = parsedParams.LocationScope;
		var PnLScope = parsedParams.PnLScope;
		var DemandDomainScope = parsedParams.DemandDomainScope;
		
		//var RuleInitializationQuery = " ";
		var error_Count=0;
		var assort_Count=0;
        
        // Logging extracted parameters for validation
        Log.Info("Version: " + Version);
        Log.Info("Item Level: " + ItemLevel);
        Log.Info("Account Level: " + AccountLevel);
		Log.Info("Channel Level: " + ChannelLevel);
		Log.Info("Region Level: " + RegionLevel);
		Log.Info("PnL Level: " + PnLLevel);
		Log.Info("Demand Domain Level: " + DemandDomainLevel);
		Log.Info("Location Level: " + LocationLevel);
		
		
		Log.Info("Item Scope: " + ItemScope);
		Log.Info("Account Scope: " + AccountScope);
        Log.Info("Channel Scope: " + ChannelScope);
        Log.Info("Region Scope: " + RegionScope);
        Log.Info("PnL Scope: " + PnLScope);
        Log.Info("Demand Domain Scope: " + DemandDomainScope);
        Log.Info("Location Scope: " + LocationScope);

		
		
		var ItemCount = ItemScope.length;
        var AccountCount = AccountScope.length;
        var ChannelCount = ChannelScope.length;
        var RegionCount = RegionScope.length;
        var PnLCount = PnLScope.length;
        var DemandDomainCount = DemandDomainScope.length;
        var LocationCount = LocationScope.length;


		
		// Logging extracted parameters for validation
        Log.Info("Item Count: " + ItemCount);
		Log.Info("Account Count: " + AccountCount);
		Log.Info("Channel Count: " + ChannelCount);
		Log.Info("Region Count: " + RegionCount);
		Log.Info("PnL Count: " + PnLCount);
		Log.Info("Demand Domain Count: " + DemandDomainCount);
		Log.Info("Location Count: " + LocationCount);

		
		for (var i=0; i< ItemCount ; i++) {

			for (var j=0; j< AccountCount ; j++){
			 
				for (var k=0; k< ChannelCount ; k++){
				 
					for (var l=0; l< RegionCount ; l++){
					  
						for (var m=0; m< PnLCount ; m++){
						 
							for (var n=0; n< DemandDomainCount ; n++){
							 
								for (var o=0; o< LocationCount ; o++){
									
									var ValidAssortmentsQuery = 'Select ([Version].[Version Name].[' + Version + '] * [Item].[' + ItemLevel + '].[' + ItemScope[i] + '] * [Account].[' + AccountLevel + '].[' + AccountScope[j] + '] * [Channel].[' + ChannelLevel + '].[' + ChannelScope[k] + '] * [Region].[' + RegionLevel + '].[' + RegionScope[l] + '] * [PnL].[' + PnLLevel + '].[' + PnLScope[m] + '] * [Demand Domain].[' + DemandDomainLevel + '].[' + DemandDomainScope[n] + '] * [Location].[' + LocationLevel + '].[' + LocationScope[o] + ']) on row, ({Measure.[Assortment Final]}) on column where {Measure.[Assortment Final]==1};';

									
									var SelectValidAssortmentsQuery = queryModule.select(ValidAssortmentsQuery);
									var ValidAssortmentsResponse = cellsetModule.createCellSet(SelectValidAssortmentsQuery);
									
									Log.Info("Assortment testing 1 "+ValidAssortmentsResponse.rowCount );
									
									var Query1;
									
									if (ValidAssortmentsResponse.rowCount > 0 ){
									
										Log.Info("Assortment testing 2 ");
										
										Query1= 'scope: ([Version].[Version Name].[' + Version + '] * [Item].[' + ItemLevel + '].[' + ItemScope[i] + '] * [Account].[' + AccountLevel + '].[' + AccountScope[j] + '] * [Channel].[' + ChannelLevel + '].[' + ChannelScope[k] + '] * [Region].[' + RegionLevel + '].[' + RegionScope[l] + '] * [PnL].[' + PnLLevel + '].[' + PnLScope[m] + '] * [Demand Domain].[' + DemandDomainLevel + '].[' + DemandDomainScope[n] + '] * [Location].[' + LocationLevel + '].[' + LocationScope[o] + ']) ; Measure.[Assortment Inactive]=if(sum(Measure.[Assortment System], Measure.[Assortment New]) >=1) then 1; end scope;';
										queryModule.update(Query1);
										
										assort_Count=assort_Count+1;
										
										Log.Info("Assortment testing 3 ");
										
										Query2= 'scope: ([Version].[Version Name].[' + Version + '] * [Item].[' + ItemLevel + '].[' + ItemScope[i] + '] * [Account].[' + AccountLevel + '].[' + AccountScope[j] + '] * [Channel].[' + ChannelLevel + '].[' + ChannelScope[k] + '] * [Region].[' + RegionLevel + '].[' + RegionScope[l] + '] * [PnL].[' + PnLLevel + '].[' + PnLScope[m] + '] * [Demand Domain].[' + DemandDomainLevel + '].[' + DemandDomainScope[n] + '] * [Location].[' + LocationLevel + '].[' + LocationScope[o] + ']) ; Measure.[Assortment Final] = if(sum(Measure.[Assortment System], Measure.[Assortment New]) >=1 && coalesce(Measure.[Assortment Inactive],0)>=1) then 0 else 1;End scope;';
										
										queryModule.update(Query2);
										
										Log.Info("Assortment testing 4 ");
									}
										
									error_Count=error_Count+1;
								}
							}
						}
					}
				}
			}
		}
		
		if (error_Count === 0 ){
			RuleOutputToUI = {
				Status: 'Error',
				Message: 'Assortments not set as inactive'
			};
			return;
		}
	
	Log.Info("Number of assortments set as inactive " + assort_Count);
    };

    // Returning the CopyAssortment function
    return {
        SetAssortmentInactive: SetAssortmentInactive
    };
});

