										Log.Info("Assortment testing 3 ");
										
										Query2= 'scope: ([Version].[Version Name].[' + Version + '] * [Item].[' + ItemLevel + '].[' + ItemScope[i] + '] * [Account].[' + AccountLevel + '].[' + AccountScope[j] + '] * [Channel].[' + ChannelLevel + '].[' + ChannelScope[k] + '] * [Region].[' + RegionLevel + '].[' + RegionScope[l] + '] * [PnL].[' + PnLLevel + '].[' + PnLScope[m] + '] * [Demand Domain].[' + DemandDomainLevel + '].[' + DemandDomainScope[n] + '] * [Location].[' + LocationLevel + '].[' + LocationScope[o] + ']) ; Measure.[Assortment Final] = if(sum(Measure.[Assortment System], Measure.[Assortment New]) >=1 && coalesce(Measure.[Assortment Inactive],0)>=1) then 0 else 1;End scope;';
										
