define('o9.DemandPlanning.ScopeLevelCheck', ['o9/data/query', 'o9/data/cellset'], function () {

    var ExtractMeasure=function(Query,Measure='RecordCount'){
        var cellsetModule = require('o9/data/cellset');
        var queryModule=require('o9/data/query')
        var QueryCellset=cellsetModule.createCellSet(queryModule.select(Query))
        
        if (QueryCellset.rowCount==0)
            return 0 
        var value =QueryCellset.row(0).cell(QueryCellset.measureColumnIndex(Measure))   
        Log.Info("Value be : "+value)
        return value
        
    }
	
	var ScopeLevelCheck = function (o9Params) {
        // Parsing data from LS to the script
        var queryModule = require('o9/data/query');
        var parsedParams = JSON.parse(o9Params);
        var VersionName=parsedParams.VersionName
        var ForecastIterationType = parsedParams.ForecastIterationType
        var InputStream = parsedParams.ExtractedInputStream
        Log.Info('Input Stream test :'+InputStream)

		var MeasureGrain = 'SELECT _SchemaPlan.[_Measure].['+InputStream+'].Ancestor(_SchemaPlan.[Plan-MeasureGroup-Measure],1).Children(_SchemaPlan.[Plan-MeasureGroup-DimensionUsage]);';
        
        var lsResponse = queryModule.select(MeasureGrain);
        var measureListArray = []
        for (var i = 0; i < lsResponse.length; i++){
            var obj = lsResponse[i];
            //Log.Info(obj)
            Log.Info(obj.MemberProperties[3].PropertyValue);
            Log.Info(obj.MemberProperties[4].PropertyValue);
            var concatenated_str = obj.MemberProperties[3].PropertyValue;
            concatenated_str = concatenated_str.replace(/['"]+/g, '')
            measureListArray.push(concatenated_str);
            //Log.Info(measureListArray);
            //Log.Info("Value:" + value);
            }
        Log.Info("Measure Grain " + measureListArray);	
			
		const Masterlist = ["Version", "Item", "Account", "Channel", "Region", "Demand Domain", "PnL", "Location", "Time"];

        // Convert both Masterlist and measureListArray elements to lowercase for case-insensitive comparison
        const notInArray2 = measureListArray.filter(element => 
            !Masterlist.some(masterElement => masterElement.toLowerCase() === element.toLowerCase())
        );

        const notInArray1 = Masterlist.filter(element => 
            !measureListArray.some(measure => measure.toLowerCase() === element.toLowerCase())
        );

        const notCommonElements = [...notInArray1, ...notInArray2];

        Log.Info("Grain Does Not Exist: " + notCommonElements); // Output: elements that are not common

        ScopeQuery = `Select ([Version].[Version Name].[${VersionName}]* [Forecast Iteration].[Forecast Iteration Type].[${ForecastIterationType}] ) on row, ({Measure.[Location Scope],Measure.[Item Scope],Measure.[Account Scope],Measure.[Channel Scope],Measure.[Region Scope],Measure.[PnL Scope],Measure.[Demand Domain Scope]}) on column;`

        var PnLScope = ExtractMeasure(ScopeQuery, 'PnL Scope');
        var LocationScope = ExtractMeasure(ScopeQuery, 'Location Scope');
        var ItemScope = ExtractMeasure(ScopeQuery, 'Item Scope');
        var AccountScope = ExtractMeasure(ScopeQuery, 'Account Scope');
        var ChannelScope = ExtractMeasure(ScopeQuery, 'Channel Scope');
        var RegionScope = ExtractMeasure(ScopeQuery, 'Region Scope');
        var DemandDomainScope = ExtractMeasure(ScopeQuery, 'Demand Domain Scope');

        if (notCommonElements.some(element => element.toLowerCase() === "PnL".toLowerCase())) {
            PnLScope = "Planning PnL";
        }

        if (notCommonElements.some(element => element.toLowerCase() === "Location".toLowerCase())) {
            LocationScope = "Planning Location";
        }

        if (notCommonElements.some(element => element.toLowerCase() === "Item".toLowerCase())) {
            ItemScope = "Planning Item";
        }

        if (notCommonElements.some(element => element.toLowerCase() === "Account".toLowerCase())) {
            AccountScope = "Planning Account";
        }

        if (notCommonElements.some(element => element.toLowerCase() === "Channel".toLowerCase())) {
            ChannelScope = "Planning Channel";
        }

        if (notCommonElements.some(element => element.toLowerCase() === "Region".toLowerCase())) {
            RegionScope = "Planning Region";
        }

        if (notCommonElements.some(element => element.toLowerCase() === "Demand Domain".toLowerCase())) {
            DemandDomainScope = "Planning Demand Domain";
        }

		parsedParams['Item Scope'] = ItemScope;
        parsedParams['Account Scope'] = AccountScope;
        parsedParams['Channel Scope'] = ChannelScope;
        parsedParams['Region Scope'] = RegionScope;
        parsedParams['Demand Domain Scope'] = DemandDomainScope;
        parsedParams['Location Scope'] = LocationScope;
        parsedParams['PnL Scope'] = PnLScope;
        return parsedParams;
    };
    return {
        ScopeLevelCheck: ScopeLevelCheck,
    };
    
});

