define('o9.DemandPlanning.ForecastException', ['o9/data/query', 'o9/data/cellset', 'o9.web.api'], function () {

    // Validation function accepting o9Params as input
    var validation = function (o9Params) {

        // Parsing input parameters
        var parsedParams = JSON.parse(o9Params);

        // Retrieving required modules
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        
        // Extract input parameters
        var Version = parsedParams.Version;
		var DataObject = parsedParams.DataObject;
        var Rule = parsedParams.Rule;
		var ItemScope = parsedParams.ItemScope;
		var LocationScope = parsedParams.LocationScope;
		var ChannelScope = parsedParams.ChannelScope;
        
		var RuleInitializationQuery = " ";
		var error_Count=0;
        
        // Logging extracted parameters for validation
        Log.Info("Version: " + Version);
        Log.Info("Data Object Type: " + DataObject);
        Log.Info("Rule ID: " + Rule);
        Log.Info("Item L2: " + ItemScope);
		Log.Info("Location Country " + LocationScope);
		Log.Info("Channel: " + ChannelScope);
		
         
		// Incrementing Rule by 1

		if (Rule == "Rule_1000")
		{
			RuleInitializationQuery = 'Cartesian Scope:([Version].[Version Name].Filter( #.Name == "'+Version+'") * [Data Object].[Data Object].[L2 x Channel x Location Country Exception]); Measure.[Data Object Rule Count] = 1001; end scope;';
		}
		else 
		{
			RuleInitializationQuery = 'Scope:([Version].[Version Name].Filter( #.Name == "'+Version+'") * [Data Object].[Data Object].[L2 x Channel x Location Country Exception]); Measure.[Data Object Rule Count] = Measure.[Data Object Rule Count] +1;end scope;';
		}
		Log.Info("Rule Initialization Query: "+RuleInitializationQuery);
		queryModule.update(RuleInitializationQuery);

    };

    // Returning the validation function
    return {
        validation: validation
    };
});

