define('o9.DemandPlanning.ExistingAccountParameterNoPL', ['o9/data/query', 'o9/data/cellset'], function () {

    
	var ExistingAccountString = function (o9Params) {
        // Parsing data from LS to the script
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        var parsedParams = JSON.parse(o9Params);

        var VersionName = parsedParams.VersionName;
        var IterationType =parsedParams.ForecastIterationType;
		Log.Info("Check Iteration Type Account: " +IterationType);
		Log.Info("STMT Account");
		
				
		var AccountScope = parsedParams.AccountScope;
        Log.Info("STMT Account1:" +AccountScope);
        var ItemScope = parsedParams.ItemScope;
        var LocationScope = parsedParams.LocationScope;
        var ChannelScope = parsedParams.ChannelScope;
        var PnLScope = parsedParams.PnLScope;
        var DemandDomainScope = parsedParams.DemandDomainScope;
        var RegionScope = parsedParams.RegionScope;


        Log.Info("Version Name: " + VersionName);
        var ExistingShiftPatternsAccountQuery ='(Select ( [Region].[Planning Region].relatedmembers(['+RegionScope+']) * [Item].[Planning Item].relatedmembers(['+ItemScope+']) * [PnL].[Planning PnL].relatedmembers(['+PnLScope+']) * [Location].[Location].relatedmembers(['+LocationScope+']) * [Demand Domain].[Planning Demand Domain].relatedmembers(['+DemandDomainScope+']) * [Account].[Planning Account].relatedmembers(['+AccountScope+']) * [Channel].[Planning Channel].relatedmembers(['+ChannelScope+']) *&CWVAndScenarios.['+VersionName+'] * &AllForecastIterationTypes.['+IterationType+']) on row, ({Measure.[Forecast Iteration Selection],Measure.[Assortment Stat]}) on column include nulls).filter(Measure.[Assortment Stat]>0 && (isnull(Measure.[Forecast Iteration Selection]) || Measure.[Forecast Iteration Selection]=="FI-PL")) ;'
        var ExistingShiftPatternsAccountDataCellSet = cellsetModule.createCellSet(queryModule.select(ExistingShiftPatternsAccountQuery));
        Log.Info(ExistingShiftPatternsAccountDataCellSet);
        var ExistingShiftPatternsAccountList = ExistingShiftPatternsAccountDataCellSet.attributeMembers("Account",AccountScope);
        Log.Info('All Account list : ' + ExistingShiftPatternsAccountList);
        var ExistingShiftPatternsAccount = ExistingShiftPatternsAccountList.map(function (item) {
            return item['Name'];
        });

        ExistingShiftPatternsAccount = ExistingShiftPatternsAccount.map(i => '[' + i);
        ExistingShiftPatternsAccount = ExistingShiftPatternsAccount.map(i => i + ']');
        Log.Info('Existing account member : ' + ExistingShiftPatternsAccount);
        if (ExistingShiftPatternsAccount.length === 0){
            ExistingShiftPatternsAccount = "[]";
        }
       

      
        parsedParams['ExistingAccountString'] = ExistingShiftPatternsAccount.toString();
        Log.Info("ExistingAccountString: " + ExistingShiftPatternsAccount.toString());

        return parsedParams;
    };
    return {
        ExistingAccountString: ExistingAccountString,
    };
    
});

