define('o9.DemandPlanning.ExistingPnLParameter', ['o9/data/query', 'o9/data/cellset'], function () {

    
	
	var ExistingPnLString = function (o9Params) {
        // Parsing data from LS to the script
        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        var parsedParams = JSON.parse(o9Params);

        var VersionName = parsedParams.VersionName;
        var IterationType = parsedParams.ForecastIterationType;
        Log.Info("STMT PnL");
		Log.Info("Check Iteration Type PnL: " +IterationType);
		
		var AccountScope = parsedParams.AccountScope;
        Log.Info("STMT Account1:" +AccountScope);
        var ItemScope = parsedParams.ItemScope;
        var LocationScope = parsedParams.LocationScope;
        var ChannelScope = parsedParams.ChannelScope;
        var PnLScope = parsedParams.PnLScope;
        var DemandDomainScope = parsedParams.DemandDomainScope;
        var RegionScope = parsedParams.RegionScope;


        Log.Info("Version Name: " + VersionName);
		
		InputStream = IterationType.split('_');
        InputStream = InputStream[1];
		Log.Info('Input Stream :' +InputStream)
		
		if (InputStream=='Actual') {

        var ExistingShiftPatternsPnLQuery = '(Select ( [Region].[Planning Region].relatedmembers(['+RegionScope+']) * [Item].[Planning Item].relatedmembers(['+ItemScope+']) * [PnL].[Planning PnL].relatedmembers(['+PnLScope+']) * [Location].[Location].relatedmembers(['+LocationScope+']) * [Demand Domain].[Planning Demand Domain].relatedmembers(['+DemandDomainScope+']) * [Account].[Planning Account].relatedmembers(['+AccountScope+']) * [Channel].[Planning Channel].relatedmembers(['+ChannelScope+']) *&CWVAndScenarios.['+VersionName+'] * &AllForecastIterationTypes.['+IterationType+']) on row, ({Measure.[Forecast Iteration Selection],Measure.[Assortment Stat]}) on column include nulls).filter(Measure.[Assortment Stat]>0 && (isnull(Measure.[Forecast Iteration Selection]) || Measure.[Forecast Iteration Selection]=="FI-PL")) ;'
        var ExistingShiftPatternsPnLDataCellSet = cellsetModule.createCellSet(queryModule.select(ExistingShiftPatternsPnLQuery));
        Log.Info(ExistingShiftPatternsPnLDataCellSet);
        var ExistingShiftPatternsPnLList = ExistingShiftPatternsPnLDataCellSet.attributeMembers("PnL",PnLScope);
        Log.Info('o9.DemandPlanning.ExistingItemString -> ExistingShiftPatternsPnLList : ' + ExistingShiftPatternsPnLList);
		}
		
		
		else {
		var ExistingShiftPatternsPnLQuery = '(Select ( [Region].[Planning Region].relatedmembers(['+RegionScope+']) * [Item].[Planning Item].relatedmembers(['+ItemScope+']) * [PnL].[Planning PnL].relatedmembers(['+PnLScope+']) * [Location].[Location].relatedmembers(['+LocationScope+']) * [Demand Domain].[Planning Demand Domain].relatedmembers(['+DemandDomainScope+']) * [Account].[Planning Account].relatedmembers(['+AccountScope+']) * [Channel].[Planning Channel].relatedmembers(['+ChannelScope+']) *&CWVAndScenarios.['+VersionName+'] * &AllForecastIterationTypes.['+IterationType+']) on row, ({Measure.[Forecast Iteration Selection],Measure.['+InputStream+']}) on column include nulls).filter(Measure.['+InputStream+']>0 && (isnull(Measure.[Forecast Iteration Selection]) || Measure.[Forecast Iteration Selection]=="FI-PL")) ;'
        var ExistingShiftPatternsPnLDataCellSet = cellsetModule.createCellSet(queryModule.select(ExistingShiftPatternsPnLQuery));
        Log.Info(ExistingShiftPatternsPnLDataCellSet);
        var ExistingShiftPatternsPnLList = ExistingShiftPatternsPnLDataCellSet.attributeMembers("PnL",PnLScope);
        Log.Info('o9.DemandPlanning.ExistingItemString -> ExistingShiftPatternsPnLList : ' + ExistingShiftPatternsPnLList);
		
		}
		
        var ExistingShiftPatternsPnL = ExistingShiftPatternsPnLList.map(function (item) {
            return item['Name'];
        });

        ExistingShiftPatternsPnL = ExistingShiftPatternsPnL.map(i => '[' + i);
        ExistingShiftPatternsPnL = ExistingShiftPatternsPnL.map(i => i + ']');
        Log.Info("All PnL: "+ExistingShiftPatternsPnL);
        Log.Info('o9.DemandPlanning.ExistingItemString -> ExistingShiftPatternsPnL : ' + ExistingShiftPatternsPnL);
        if (ExistingShiftPatternsPnL.length === 0){
            ExistingShiftPatternsPnL = "[]";
        }
        parsedParams['ExistingPnLString'] = ExistingShiftPatternsPnL.toString();
        Log.Info("ExistingPnLString: " + ExistingShiftPatternsPnL.toString());

        return parsedParams;
	};
    return {
        ExistingPnLString: ExistingPnLString,
    };
    
});

