define('o9.DemandPlanning.LowerLevels', ['o9/data/query', 'o9/data/cellset'], function () {
    

	var findLowestInHierarchy = function(itemScope, itemGroup, hierarchyItems) {
    Log.Info("In function: findLowestInHierarchy");
    Log.Info("Item Scope: " + itemScope + ", Item Group: " + itemGroup);
    
    // Find items matching itemScope or itemGroup
    const matchingItems = hierarchyItems.filter(item => item.name === itemScope || item.name === itemGroup);
    
    if (matchingItems.length === 0) {
        Log.Info("No matching items found.");
        return "No matching items";
    }
    
    // Find the lowest level among the matched items
    let lowestItem = matchingItems[0].name;
    let lowestLevel = matchingItems[0].level;

    matchingItems.forEach(item => {
        if (item.level < lowestLevel) {
            lowestLevel = item.level;
            lowestItem = item.name;
        }
    });

    Log.Info("Lowest Item: " + lowestItem + ", Lowest Level: " + lowestLevel);
    
    // Find all items that are at or below the lowest level
    const lowerItemList = hierarchyItems.filter(item => item.level <= lowestLevel);
    
    // Format the output list
    const formattedLowerItemList = lowerItemList.map(item => `[${item.name}]`).join(", ");
    
    return formattedLowerItemList;
};

	


