define('o9.CopyRangeMetricsJS', ['o9/data/query', 'o9/data/cellset'], function () {
	var CopyRangeMetricsJS = function (o9Params) 	{
		Log.Info("------------------------------------------------------------Started executing CopyRangeMetricsJS plugin------------------------------------------------------------");

        var parsedParams = JSON.parse(o9Params);
		Log.Info("------------------------------------------------------------This is the value of the parsedParams------------------------------------------------------------");
		Log.Info(JSON.stringify(parsedParams));
		const queryMod = require('o9/data/query');
		
		// initiatlize the query modules
		// var queryModule = require('o9/data/query');
		// var cellsetModule = require('o9/data/cellset');
		
		// const cellsetMod = require('o9/data/cellset');
		// const queryMod = require('o9/data/query');
		
		var Version = parsedParams.Version;
		var LikeItem=String(parsedParams.LikeItem);
        var SellingSeason = String(parsedParams.SellingSeason);
		var RangedChannel = parsedParams.RangedChannel.join('", "');
		var RangedStores = parsedParams.RangedStores.join('", "');		
		var CurrentItem = parsedParams.CurrentItemToFilterDataSource;

		var execprocquery = 'exec procedure AP47CopyRangeMetrics {"Version": "'+Version+'" ,"RangedChannel": ["'+RangedChannel+'"],"LikeItem": "'+LikeItem+'","CurrentItem": "'+CurrentItem+'",  "SellingSeason": "'+SellingSeason+'", "RangedStores": ["'+RangedStores+'"]};' ;
		Log.Info("--------------------Running the exec procedure--------------------");
        Log.Info(execprocquery);
        queryMod.update(execprocquery);
	};
	return 	{
		CopyRangeMetricsJS : CopyRangeMetricsJS
	};
	
});

