define('o9.CheckRealItemDelete', ['o9/data/query', 'o9/data/cellset'], function () {
	var CheckRealItemDelete = function (o9Params) 	{
		Log.Info("------------------------------------------------------------Started executing CheckRealItemDelete plugin------------------------------------------------------------");
		var parsedParams = JSON.parse(o9Params);
		Log.Info("------------------------------------------------------------This is the value of the parsedParams------------------------------------------------------------");
		Log.Info(JSON.stringify(parsedParams));
		
		// initiatlize the query modules
		var queryModule = require('o9/data/query');
		var cellsetModule = require('o9/data/cellset');
		
		const cellsetMod = require('o9/data/cellset');
		const queryMod = require('o9/data/query');
		
		var StyleCl=String(parsedParams.StyleColor);
		var CCArray=StyleCl.split(',');
		var Versionvar = String(parsedParams.Version);
		var PHToDelete = String(parsedParams.RemovefromLBorDeletePH);
		Log.Info("------------------------------------------------------------This is the value of the Version------------------------------------------------------------");
		Log.Info(Versionvar);		
		Log.Info("------------------------------------------------------------This is the value of the PHToDelete------------------------------------------------------------");
		Log.Info(PHToDelete);

		for(i = 0; i <CCArray.length; i++){

			if (PHToDelete === "true") {
				Log.Info("------------------------------------------------------------The placeholder has to be deleted------------------------------------------------------------");
				
				var itemQuery="Select ([Version].[Version Name].["+Versionvar+"] * [Item].[Planning Item].["+CCArray[i]+"]) on row,  ({Measure.[Is Real Item Property]}) on column;";
				Log.Info("------------------------------------------------------------This is the select query to get the Is Real Item Property measure------------------------------------------------------------");
				Log.Info(itemQuery);
				var Itemlist= cellsetMod.createCellSet(queryMod.select(itemQuery));				
				Log.Info("------------------------------------------------------------The value of the Itemlist------------------------------------------------------------");
				Log.Info(Itemlist);
                var Itemlistvar =Number (Itemlist.row(0).cell(Itemlist.measureColumnIndex('Is Real Item Property')));
				Log.Info("------------------------------------------------------------The value of the Itemlistvar------------------------------------------------------------");
				Log.Info(Itemlistvar);
                Log.Info("------------------------------------------------------------This is the value of the StyleCl------------------------------------------------------------");
                Log.Info(StyleCl);


				
	
				if (Itemlistvar === 1) {
					Log.Info("------------------------------------------------------------The placeholder cannot be deleted as the Is Real Item Property is 1------------------------------------------------------------");
					RuleOutputToUI = {
						Status: 'Error',
						Message: 'The placeholder cannot be deleted as it\'s a real item'
					};
					return;
				}


				else if (Itemlistvar === 0){
					Log.Info("------------------------------------------------------------The placeholder can be deleted------------------------------------------------------------");
					
					var delmember = 'deletemember ([Item].[Planning Item] = {,['+CCArray[i]+']}) CASCADE ;';
					queryMod.update(delmember);
	
				}

                else {
                    Log.Info("------------------------------------------------------------The input to delete placeholder is unclear. Terminating the plugin------------------------------------------------------------");

                }
			}
		}

		

	};
	
	return 	{
		CheckRealItemDelete : CheckRealItemDelete
	};
	
});

