	var cellsetModule = require('o9/data/cellset');
	
	Log.Info("StartedBucket ... ");
	var ExistingStartBucketTemp ='SELECT &DailySolverStartBucket.element(0).Name;';	
	var lsResponse = queryModule.select(ExistingStartBucketTemp);
	if(lsResponse !== null){
	var ExistingStartBucket = lsResponse.Result;
	Log.Info("ExistingStartBucket ... " + ExistingStartBucket);}
	else{
	var ExistingStartBucket = null;
	Log.Info("ExistingCurrentBucket ... " + ExistingCurrentBucket);}
	
