    queryModule.update(SetCurrentBucket);
	
	var SetPropertyCurrentBucket = 'updatemember(Time.[Day]={,"'+ CurrentBucket +'"},[Time].[D Solver Current Bucket]={"1",});';
	Log.Info("SetPropertyCurrentBucket ... " + SetPropertyCurrentBucket); 
    queryModule.update(SetPropertyCurrentBucket);
		
	var SetDayIsCurrent = 'updatemember(Time.[Day]={,"'+ CurrentBucket +'"},[Time].[Day$IsCurrent]={TRUE,});';
