define('o9.SupplyPlan170CreateRule.MultiDestinations', ['o9/data/query', 'o9/data/cellset'], function () {
	var MultiDestinations = function(o9Params){
    var parsedParams = JSON.parse(o9Params);
    var queryModule = require('o9/data/query');
    var Version = parsedParams.Version;
	var Rule = parsedParams.o9BodLaneRuleFinal;
	var FromLoc = parsedParams.Activity1BODLaneFromLoc;
    var ToLoc = parsedParams.Activity1BODLaneToLoc;
	var Transmode = parsedParams.Activity2TransMode;
	var Prefix = parsedParams.PrefixFB;
	
	Log.Info('..................Starting with a Smile..................');

	//Populating Measure.[Activity1 From Location]
    var SetFromLoc = 'cartesian scope:( [Version].[Version Name].['+ Version +']  * [DM Rule].[Rule].['+ Rule +'] * [Data Object].[Data Object].[Item BODLane Association]); Measure.[Activity1 From Location] = \"'+ FromLoc +'\" ; end scope;';
	Log.Info('Source Location is '+ FromLoc);
	queryModule.update(SetFromLoc);
	
	//Populating Measure.[Activity1 To Location]
	var SetToLoc = 'cartesian scope:( [Version].[Version Name].['+ Version +']  * [DM Rule].[Rule].['+ Rule +'] * [Data Object].[Data Object].[Item BODLane Association]); Measure.[Activity1 To Location] = \"'+ ToLoc +'\" ; end scope;';
	Log.Info("Destination Locations are "+ ToLoc); 
    queryModule.update(SetToLoc);	

	//Populating Measure.[Activity2 Transmode]
	var SetTransmode = 'cartesian scope:( [Version].[Version Name].['+ Version +']  * [DM Rule].[Rule].['+ Rule +'] * [Data Object].[Data Object].[Item BODLane Association]); Measure.[Activity2 Transmode] = \"'+ Transmode +'\" ; end scope;';
	Log.Info("Transmode used "+ Transmode); 
    queryModule.update(SetTransmode);

	var SplitToLoc = String(ToLoc).split(",");

	if (Prefix === null){
		var UsePF = "False";
		Log.Info('Prefix availability '+ UsePF);
	}
	else {
		UsePF = "True";
		Log.Info('Prefix availability '+ UsePF)
	}

	//Populating Concatinated String for Measure.[Activity1 BODLane]
	var concatelane = [];
	if (UsePF === "True"){
		for (var a = 0; a < SplitToLoc.length; a++){
		concatelane[a] = `${Prefix}_From_${FromLoc}_To_${SplitToLoc[a]}` ;
		var b = a+1;
		Log.Info('Lane '+ b +" is "+ concatelane[a]);
		}
	} else if (UsePF === "False"){
		for (var c = 0; c < SplitToLoc.length; c++){
			concatelane[c] = `Lane_From_${FromLoc}_To_${SplitToLoc[c]}` ;
			var d = c+1;
			Log.Info('Lane '+ d +" is "+ concatelane[c]);
			}
	}
	var Smile = concatelane[0];

	if (SplitToLoc.length > 1){
		var flag = "True";
		Log.Info('Lanes to be created are more than 1 '+ flag);
	}

	if (flag === "True"){
		for (var j = 0; j < SplitToLoc.length-1; j++){
			var IDB = `${Smile},${concatelane[j+1]}`;
			Smile = IDB;
			var r = j+1;
			Log.Info('Check '+ r +" = "+ Smile);
		}
	}
	Log.Info('Activity1 BODLane '+ Smile);

	var CreateLane = `Cartesian Scope: ([Version].[Version Name].[${Version}] * [Data Object].[Data Object].[Item BODLane Association] * [DM Rule].[Rule].[${Rule}]); Measure.[Activity1 BODLane] = \"${Smile}\"; end scope;`;
	Log.Info('Measure.[Activity1 BODLane] = \"' + Smile + "\"");
	queryModule.update(CreateLane);

	//Populating Activity1 Members 
	if (UsePF === "True"){
			for (var i = 0; i < SplitToLoc.length; i++){
			Log.Info('Creating a BODLane from ' + FromLoc + " To " + SplitToLoc[i]);
			
			var CreateActivity1 = `createmember([Activity1].[Activity1]={,\"${Prefix}_From_${FromLoc}_To_${SplitToLoc[i]}\"},[Activity1].[From Location]={,\"${FromLoc}\"},[Activity1].[To Location]={,\"${ToLoc[i]}\"},[Activity1].[Activity Type1]={,"Distribution"});` ;
			queryModule.update(CreateActivity1);

            var SetPrefix = `updatemember (Activity1.[Activity1]={,\"${Prefix}_From_${FromLoc}_To_${SplitToLoc[i]}\"}, Activity1.[Prefix]={\"${Prefix}\",});`;
            queryModule.update(SetPrefix);

            Log.Info('Prefix set as '+ Prefix);		
			var DCCount = i + 1;
		}
	} else if (UsePF === "False"){
		for (var k = 0; k < SplitToLoc.length; k++){
			Log.Info('Creating a BODLane from ' + FromLoc + " To " + SplitToLoc[k]);
			
			CreateActivity1 = `createmember([Activity1].[Activity1]={,\"Lane_From_${FromLoc}_To_${SplitToLoc[k]}\"},[Activity1].[From Location]={,\"${FromLoc}\"},[Activity1].[To Location]={,\"${ToLoc[k]}\"},[Activity1].[Activity Type1]={,"Distribution"});` ;
			queryModule.update(CreateActivity1);

            SetPrefix = `updatemember (Activity1.[Activity1]={,\"Lane_From_${FromLoc}_To_${SplitToLoc[k]}\"}, Activity1.[Prefix]={\"Lane\",});`;
            queryModule.update(SetPrefix);
		
            Log.Info('Prefix set as Lane');
			DCCount = k + 1;		
		}
	}

	Log.Info('Total Lanes created are ' + DCCount);
	
	Log.Info('..................Ending with a Cheesy Smile..................');

};
return {
	MultiDestinations:MultiDestinations
	};
});

